import React, { useState, useEffect } from 'react';
import { poloniexApi } from '@/services/poloniexAPI';
import APIErrorBoundary from '@/components/APIErrorBoundary';
import useAPICall from '@/hooks/useAPICall';

interface AccountBalanceDisplayProps {
  pair?: string;
}

interface AccountBalance {
  currency: string;
  available: string;
  onOrders: string;
  btcValue: string;
}

/**
 * Example component showing proper error handling for API calls
 * This replaces the old pattern of silent fallbacks to mock data
 */
export const AccountBalanceDisplay: React.FC<AccountBalanceDisplayProps> = () => {
  const [accountBalance, setAccountBalance] = useState<AccountBalance[] | null>(null);

  // Use the new useAPICall hook for proper error handling
  const {
    data: balance,
    loading,
    error,
    execute: fetchBalance,
    retry
  } = useAPICall(
    () => poloniexApi.getAccountBalance(),
    {
      onError: (error) => {
        console.error('Account balance fetch failed:', error.message);
      },
      retryCount: 3,
      retryDelay: 1000
    }
  );

  // Initial load
  useEffect(() => {
    fetchBalance();
  }, [fetchBalance]);

  // Update state when data is available
  useEffect(() => {
    if (balance) {
      setAccountBalance(balance);
    }
  }, [balance]);

  // Loading state
  if (loading) {
    return (
      <div className="bg-white p-6 rounded-lg shadow">
        <h3 className="text-lg font-medium text-gray-900 mb-4">Account Balance</h3>
        <div className="animate-pulse">
          <div className="h-4 bg-gray-200 rounded w-3/4 mb-2"></div>
          <div className="h-4 bg-gray-200 rounded w-1/2 mb-2"></div>
          <div className="h-4 bg-gray-200 rounded w-2/3"></div>
        </div>
      </div>
    );
  }

  // Error state - show error boundary instead of mock data
  if (error) {
    return (
      <div className="bg-white p-6 rounded-lg shadow">
        <h3 className="text-lg font-medium text-gray-900 mb-4">Account Balance</h3>
        <APIErrorBoundary 
          error={error} 
          onRetry={retry}
          context="Account Balance"
        />
      </div>
    );
  }

  // Success state
  if (accountBalance) {
    return (
      <div className="bg-white p-6 rounded-lg shadow">
        <h3 className="text-lg font-medium text-gray-900 mb-4">Account Balance</h3>
        <div className="grid grid-cols-2 gap-4">
          <div>
            <p className="text-sm font-medium text-gray-500">Total Amount</p>
            <p className="text-lg font-semibold text-gray-900">${accountBalance.totalAmount}</p>
          </div>
          <div>
            <p className="text-sm font-medium text-gray-500">Available</p>
            <p className="text-lg font-semibold text-gray-900">${accountBalance.availableAmount}</p>
          </div>
          <div>
            <p className="text-sm font-medium text-gray-500">Account Equity</p>
            <p className="text-lg font-semibold text-gray-900">${accountBalance.accountEquity}</p>
          </div>
          <div>
            <p className="text-sm font-medium text-gray-500">Unrealized PnL</p>
            <p className={`text-lg font-semibold ${
              parseFloat(accountBalance.unrealizedPnL) >= 0 ? 'text-green-600' : 'text-red-600'
            }`}>
              ${accountBalance.unrealizedPnL}
            </p>
          </div>
        </div>
      </div>
    );
  }

  // No data state (shouldn't happen with proper error handling)
  return (
    <div className="bg-white p-6 rounded-lg shadow">
      <h3 className="text-lg font-medium text-gray-900 mb-4">Account Balance</h3>
      <p className="text-gray-500">No account data available</p>
    </div>
  );
};

export default AccountBalanceDisplay;

import React, { Component, type ReactNode, type ErrorInfo } from 'react';
import { ErrorFallback } from './ErrorFallback';

interface ErrorBoundaryState {
  hasError: boolean;
  error: Error | null;
  errorInfo: ErrorInfo | null;
  errorCount: number;
  errorId: string;
}

interface ErrorBoundaryProps {
  children: ReactNode;
  fallback?: React.ComponentType<{
    error: Error | null;
    errorInfo: ErrorInfo | null;
    errorCount: number;
    onReset: () => void;
  }>;
}

export class ErrorBoundary extends Component<ErrorBoundaryProps, ErrorBoundaryState> {
  private resetTimeoutId: number | null = null;

  constructor(props: ErrorBoundaryProps) {
    super(props);
    this.state = {
      hasError: false,
      error: null,
      errorInfo: null,
      errorCount: 0,
      errorId: ''
    };
  }

  static getDerivedStateFromError(error: Error): Partial<ErrorBoundaryState> {
    return {
      hasError: true,
      error,
      errorId: `error-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`
    };
  }

  componentDidCatch(error: Error, errorInfo: ErrorInfo) {
    this.setState(prevState => ({
      errorInfo,
      errorCount: prevState.errorCount + 1
    }));

    // Log error to monitoring service
    this.logError(error, errorInfo);

    // Auto-reset after 30 seconds for non-critical errors, but only for first few errors
    if (this.state.errorCount < 3 && !this.isInitializationError(error)) {
      this.resetTimeoutId = window.setTimeout(() => {
        this.handleReset();
      }, 30000);
    }
  }

  componentWillUnmount() {
    if (this.resetTimeoutId) {
      clearTimeout(this.resetTimeoutId);
    }
  }

  private logError = (error: Error, errorInfo: ErrorInfo) => {
    // Log to console for development
    console.error('ErrorBoundary caught an error:', error);
    console.error('Error info:', errorInfo);

    // Check for initialization errors
    if (this.isInitializationError(error)) {
      console.error('Initialization error detected - this may require a page refresh');
    }

    // In production, this would send to a logging service
    if (process.env.NODE_ENV === 'production') {
      // Example: Send to monitoring service
      // logErrorToService(error, errorInfo, this.state.errorCount);
    }
  };

  private isInitializationError = (error: Error): boolean => {
    const message = error.message || '';
    return (
      message.includes('Cannot access') && message.includes('before initialization') ||
      message.includes('temporal dead zone') ||
      message.includes('ReferenceError') ||
      // React Error #185 - Maximum update depth exceeded (infinite loop)
      message.includes('185') ||
      message.includes('Maximum update depth exceeded') ||
      message.includes('infinite') ||
      message.includes('hydration') ||
      message.includes('Text content does not match server-rendered HTML') ||
      message.includes('Hydration failed')
    );
  };

  private handleReset = () => {
    if (this.resetTimeoutId) {
      clearTimeout(this.resetTimeoutId);
      this.resetTimeoutId = null;
    }

    this.setState({
      hasError: false,
      error: null,
      errorInfo: null,
      errorId: ''
    });
  };

  render() {
    if (this.state.hasError) {
      const FallbackComponent = this.props.fallback || ErrorFallback;

      return (
        <FallbackComponent
          error={this.state.error}
          errorCount={this.state.errorCount}
          onReset={this.handleReset}
        />
      );
    }

    return this.props.children;
  }
}

// React is used implicitly for JSX transformation
import { useState } from 'react';
import { X, RefreshCw, Info } from 'lucide-react';
import ExtensionStatus from './ExtensionStatus';
import ExtensionSettings from './ExtensionSettings';

interface ExtensionControlsProps {
  onClose?: () => void;
}

const ExtensionControls: React.FC<ExtensionControlsProps> = ({ onClose }) => {
  // Using settings hook for extension configuration
  const [activePanel, setActivePanel] = useState<'status' | 'settings' | 'info'>('status');
  const [isRefreshing, setIsRefreshing] = useState(false);

  const handleRefresh = () => {
    setIsRefreshing(true);

    // Refresh data from extension
    if (window.chrome && chrome.runtime && chrome.runtime.sendMessage) {
      try {
        // Extension ID will need to be updated with your actual extension ID
        const extensionId = 'jcdmopolmojdhpclfbemdpcdneobmnje';

        chrome.runtime.sendMessage(
          extensionId,
          { type: 'REFRESH_DATA' },
          () => {
            // Removed unused response parameter
            setIsRefreshing(false);
          }
        );
      } catch (error) {
        console.error('Error refreshing extension data:', error);
        setIsRefreshing(false);
      }
    } else {
      // Chrome extension API not available, simulate refresh
      setTimeout(() => {
        setIsRefreshing(false);
      }, 1000);
    }
  };

  return (
    <div className="bg-white rounded-lg shadow-lg overflow-hidden">
      <div className="bg-neutral-800 text-white p-4 flex justify-between items-center">
        <h2 className="text-lg font-bold flex items-center">
          <svg className="h-5 w-5 mr-2" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M20.24 12.24c-1.07-.57-1.23-2.86-1.23-2.86-1.7 1.27-2.33-.28-2.33-.28-3.11 3.3-6.35.23-6.35.23v4.42c0 2.57-2.4 2.57-2.4 2.57H3.5" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
            <path d="M3.75 14.84V7.25c0-1.5 1.5-1.5 1.5-1.5H9.5V19h-4c-.83 0-1.75-.97-1.75-2.16v-2z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
            <path d="M9.5 5.75v13.5M13.5 5.75c-.97 0-3 .4-4 1.48V4.23c1-.82 2.5-1.48 4-1.48 2 0 5 1 5 1.5v2c0 .5-3 .5-3 .5" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
          </svg>
          Extension Controls
        </h2>
        <div className="flex items-center space-x-2">
          <button 
            onClick={handleRefresh}
            disabled={isRefreshing}
            className="p-1.5 rounded-md hover:bg-neutral-700 text-neutral-300 disabled:opacity-50"
          >
            <RefreshCw className={`h-4 w-4 ${isRefreshing ? 'animate-spin' : ''}`} />
          </button>
          {onClose && (
            <button 
              onClick={onClose}
              className="p-1.5 rounded-md hover:bg-neutral-700 text-neutral-300"
            >
              <X className="h-4 w-4" />
            </button>
          )}
        </div>
      </div>

      <div className="border-b">
        <nav className="flex divide-x">
          <button
            onClick={() => setActivePanel('status')}
            className={`flex-1 py-2 px-3 flex justify-center items-center text-sm font-medium ${
              activePanel === 'status' ? 'bg-blue-50 text-blue-600' : 'bg-neutral-50 text-neutral-600 hover:bg-neutral-100'
            }`}
          >
            Status
          </button>
          <button
            onClick={() => setActivePanel('settings')}
            className={`flex-1 py-2 px-3 flex justify-center items-center text-sm font-medium ${
              activePanel === 'settings' ? 'bg-blue-50 text-blue-600' : 'bg-neutral-50 text-neutral-600 hover:bg-neutral-100'
            }`}
          >
            Settings
          </button>
          <button
            onClick={() => setActivePanel('info')}
            className={`flex-1 py-2 px-3 flex justify-center items-center text-sm font-medium ${
              activePanel === 'info' ? 'bg-blue-50 text-blue-600' : 'bg-neutral-50 text-neutral-600 hover:bg-neutral-100'
            }`}
          >
            Info
          </button>
        </nav>
      </div>

      <div className="p-4">
        {activePanel === 'status' && <ExtensionStatus onRefreshRequest={handleRefresh} />}

        {activePanel === 'settings' && <ExtensionSettings />}

        {activePanel === 'info' && (
          <div className="space-y-4">
            <div>
              <h3 className="font-medium text-lg mb-2 flex items-center">
                <Info className="h-5 w-5 mr-2 text-blue-500" />
                About This Extension
              </h3>
              <p className="text-neutral-600">
                This Chrome extension allows you to integrate TradingView's charting capabilities with Poloniex's trading platform.
                Extract chart data, execute trades directly from the chart, and manage your positions all in one place.
              </p>
            </div>

            <div className="bg-neutral-50 p-3 rounded-md">
              <div className="font-medium mb-1">Features:</div>
              <ul className="text-sm text-neutral-600 space-y-1 pl-5 list-disc">
                <li>Real-time data extraction from TradingView charts</li>
                <li>One-click trading directly from TradingView</li>
                <li>Automated trading based on chart indicators</li>
                <li>Account and position management</li>
                <li>Risk management controls</li>
                <li>Secure API credential storage</li>
              </ul>
            </div>

            <div className="bg-blue-50 p-3 rounded-md">
              <div className="font-medium mb-1 text-blue-700">Getting Started:</div>
              <ol className="text-sm text-blue-600 space-y-1 pl-5 list-decimal">
                <li>Install the Chrome extension from the Extension page</li>
                <li>Enter your Poloniex API credentials in Settings</li>
                <li>Visit TradingView to activate chart data extraction</li>
                <li>Start trading directly from TradingView charts</li>
              </ol>
            </div>

            <div className="mt-4 text-neutral-500 text-xs">
              Extension Version: 1.0.0
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ExtensionControls;

import React, { useEffect } from 'react';
import { 
  isChromeExtension, 
  initExtensionSecurity, 
  getExtensionStatus
} from '@/utils/chromeExtension';

/**
 * Component that initializes and monitors extension security
 */
const ExtensionSecurityManager: React.FC = () => {
  useEffect(() => {
    // Initialize extension security on component mount
    if (isChromeExtension()) {
      // Generate a unique security token for this session
      const securityToken = `${Date.now()}-${Math.random().toString(36).substring(2, 15)}`;

      // Initialize extension security with the token
      initExtensionSecurity(securityToken);

      // Store token in session storage for extension verification
      sessionStorage.setItem('extension_security_token', securityToken);

      // Set up periodic status check
      const checkStatus = async () => {
        try {
          await getExtensionStatus();
        } catch (error) {
          console.error('Extension status check failed:', error);
        }
      };

      // Check status immediately
      checkStatus();

      // Set up interval for periodic checks
      const statusInterval = setInterval(checkStatus, 30000);

      // Clean up on unmount
      return () => {
        clearInterval(statusInterval);
      };
    }
  }, []);

  // This component doesn't render anything visible
  return null;
};

export default ExtensionSecurityManager;

// React is used implicitly for JSX transformation
import { useState, useEffect } from 'react';
import { Shield, Lock, TerminalSquare, Zap } from 'lucide-react';
import { useSettings } from '../../hooks/useSettings';

interface ExtensionSettingsProps {
  onClose?: () => void;
}

interface ExtensionResponse {
  installed?: boolean;
  connected?: boolean;
  status?: string;
}

const ExtensionSettings: React.FC<ExtensionSettingsProps> = ({ onClose }) => {
  const { apiKey, apiSecret, updateSettings } = useSettings();
  const [extensionStatus, setExtensionStatus] = useState<string>('Not detected');
  const [formData, setFormData] = useState({
    extensionEnabled: true,
    tradingViewEnabled: true,
    poloniexEnabled: true,
    autoConnect: true,
    notificationsEnabled: true,
    riskLimit: 50,
    apiKey: apiKey,
    apiSecret: apiSecret
  });

  // Check if the extension is installed
  useEffect(() => {
    if (window.chrome && chrome.runtime && chrome.runtime.sendMessage) {
      try {
        // Extension ID will need to be updated with your actual extension ID
        const extensionId = 'jcdmopolmojdhpclfbemdpcdneobmnje';

        chrome.runtime.sendMessage(
          extensionId,
          { type: 'CHECK_INSTALLATION' },
          (response: ExtensionResponse) => {
            if (response && response.installed) {
              setExtensionStatus('Connected');
            } else {
              setExtensionStatus('Not detected');
            }
          }
        );
      } catch {
        setExtensionStatus('Not detected');
      }
    }
  }, []);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleRangeChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: parseInt(value, 10)
    }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    // Save API credentials
    if (formData.apiKey !== apiKey || formData.apiSecret !== apiSecret) {
      updateSettings({
        apiKey: formData.apiKey,
        apiSecret: formData.apiSecret
      });
    }

    // If we have a chrome extension API and it's installed
    if (window.chrome && chrome.runtime && chrome.runtime.sendMessage && extensionStatus === 'Connected') {
      // Extension ID will need to be updated with your actual extension ID
      const extensionId = 'jcdmopolmojdhpclfbemdpcdneobmnje';

      // Update extension settings
      chrome.runtime.sendMessage(
        extensionId,
        { 
          type: 'UPDATE_SETTINGS',
          data: {
            extensionEnabled: formData.extensionEnabled,
            tradingViewEnabled: formData.tradingViewEnabled,
            poloniexEnabled: formData.poloniexEnabled,
            autoConnect: formData.autoConnect,
            notificationsEnabled: formData.notificationsEnabled,
            riskLimit: formData.riskLimit
          }
        },
        (response: ExtensionResponse) => {
          console.log('Extension settings updated:', response);
        }
      );

      // Update extension API credentials
      chrome.runtime.sendMessage(
        extensionId,
        {
          type: 'UPDATE_CREDENTIALS',
          data: {
            apiKey: formData.apiKey,
            apiSecret: formData.apiSecret
          }
        },
        (response: ExtensionResponse) => {
          console.log('Extension API credentials updated:', response);
        }
      );
    }

    // Close the settings panel if a handler was provided
    if (onClose) {
      onClose();
    }
  };

  return (
    <div className="bg-white rounded-lg shadow-lg p-5">
      <div className="flex items-center justify-between mb-5">
        <h2 className="text-xl font-bold flex items-center">
          <Shield className="h-5 w-5 mr-2 text-blue-500" />
          Extension Settings
        </h2>
        <div className={`px-2 py-1 text-xs rounded-full ${
          extensionStatus === 'Connected' 
            ? 'bg-green-100 text-green-800' 
            : 'bg-yellow-100 text-yellow-800'
        }`}>
          {extensionStatus}
        </div>
      </div>

      <form onSubmit={handleSubmit}>
        <div className="space-y-5">
          <div className="border-b pb-4">
            <h3 className="font-medium mb-3 flex items-center">
              <TerminalSquare className="h-4 w-4 mr-2 text-neutral-500" />
              Extension Configuration
            </h3>

            <div className="space-y-3">
              <div className="flex items-center justify-between">
                <label htmlFor="extensionEnabled" className="text-sm text-neutral-700">
                  Enable Extension
                </label>
                <div className="relative inline-block w-10 mr-2 align-middle select-none">
                  <input 
                    type="checkbox" 
                    id="extensionEnabled" 
                    name="extensionEnabled"
                    checked={formData.extensionEnabled}
                    onChange={handleChange}
                    className="sr-only peer"
                  />
                  <div className="w-10 h-5 bg-neutral-200 rounded-full peer peer-checked:bg-blue-600 peer-checked:after:translate-x-5 after:content-[''] after:absolute after:top-0.5 after:left-0.5 after:bg-white after:border after:rounded-full after:h-4 after:w-4 after:transition-all"></div>
                </div>
              </div>

              <div className="flex items-center justify-between">
                <label htmlFor="tradingViewEnabled" className="text-sm text-neutral-700">
                  TradingView Integration
                </label>
                <div className="relative inline-block w-10 mr-2 align-middle select-none">
                  <input 
                    type="checkbox" 
                    id="tradingViewEnabled" 
                    name="tradingViewEnabled"
                    checked={formData.tradingViewEnabled}
                    onChange={handleChange}
                    disabled={!formData.extensionEnabled}
                    className="sr-only peer"
                  />
                  <div className="w-10 h-5 bg-neutral-200 rounded-full peer peer-checked:bg-blue-600 peer-checked:after:translate-x-5 after:content-[''] after:absolute after:top-0.5 after:left-0.5 after:bg-white after:border after:rounded-full after:h-4 after:w-4 after:transition-all"></div>
                </div>
              </div>

              <div className="flex items-center justify-between">
                <label htmlFor="poloniexEnabled" className="text-sm text-neutral-700">
                  Poloniex Integration
                </label>
                <div className="relative inline-block w-10 mr-2 align-middle select-none">
                  <input 
                    type="checkbox" 
                    id="poloniexEnabled" 
                    name="poloniexEnabled"
                    checked={formData.poloniexEnabled}
                    onChange={handleChange}
                    disabled={!formData.extensionEnabled}
                    className="sr-only peer"
                  />
                  <div className="w-10 h-5 bg-neutral-200 rounded-full peer peer-checked:bg-blue-600 peer-checked:after:translate-x-5 after:content-[''] after:absolute after:top-0.5 after:left-0.5 after:bg-white after:border after:rounded-full after:h-4 after:w-4 after:transition-all"></div>
                </div>
              </div>

              <div className="flex items-center justify-between">
                <label htmlFor="autoConnect" className="text-sm text-neutral-700">
                  Auto-connect on startup
                </label>
                <div className="relative inline-block w-10 mr-2 align-middle select-none">
                  <input 
                    type="checkbox" 
                    id="autoConnect" 
                    name="autoConnect"
                    checked={formData.autoConnect}
                    onChange={handleChange}
                    disabled={!formData.extensionEnabled}
                    className="sr-only peer"
                  />
                  <div className="w-10 h-5 bg-neutral-200 rounded-full peer peer-checked:bg-blue-600 peer-checked:after:translate-x-5 after:content-[''] after:absolute after:top-0.5 after:left-0.5 after:bg-white after:border after:rounded-full after:h-4 after:w-4 after:transition-all"></div>
                </div>
              </div>

              <div className="flex items-center justify-between">
                <label htmlFor="notificationsEnabled" className="text-sm text-neutral-700">
                  Enable Notifications
                </label>
                <div className="relative inline-block w-10 mr-2 align-middle select-none">
                  <input 
                    type="checkbox" 
                    id="notificationsEnabled" 
                    name="notificationsEnabled"
                    checked={formData.notificationsEnabled}
                    onChange={handleChange}
                    disabled={!formData.extensionEnabled}
                    className="sr-only peer"
                  />
                  <div className="w-10 h-5 bg-neutral-200 rounded-full peer peer-checked:bg-blue-600 peer-checked:after:translate-x-5 after:content-[''] after:absolute after:top-0.5 after:left-0.5 after:bg-white after:border after:rounded-full after:h-4 after:w-4 after:transition-all"></div>
                </div>
              </div>
            </div>
          </div>

          <div className="border-b pb-4">
            <h3 className="font-medium mb-3 flex items-center">
              <Zap className="h-4 w-4 mr-2 text-neutral-500" />
              Trading Risk Management
            </h3>

            <div>
              <label htmlFor="riskLimit" className="block text-sm text-neutral-700 mb-1">
                Max Position Size (% of Available Balance)
              </label>
              <div className="flex items-center">
                <input
                  type="range"
                  id="riskLimit"
                  name="riskLimit"
                  min="5"
                  max="100"
                  step="5"
                  value={formData.riskLimit}
                  onChange={handleRangeChange}
                  disabled={!formData.extensionEnabled}
                  className="w-full h-2 bg-neutral-200 rounded-lg appearance-none cursor-pointer"
                />
                <span className="ml-2 text-sm font-medium text-neutral-700 min-w-10">
                  {formData.riskLimit}%
                </span>
              </div>
              <p className="mt-1 text-xs text-neutral-500">
                Limits the maximum size of any position you can open through the extension.
              </p>
            </div>
          </div>

          <div>
            <h3 className="font-medium mb-3 flex items-center">
              <Lock className="h-4 w-4 mr-2 text-neutral-500" />
              API Credentials
            </h3>

            <div className="space-y-3">
              <div>
                <label htmlFor="apiKey" className="block text-sm text-neutral-700 mb-1">
                  Poloniex API Key
                </label>
                <input
                  type="password"
                  id="apiKey"
                  name="apiKey"
                  value={formData.apiKey}
                  onChange={handleChange}
                  placeholder="Your API Key"
                  className="w-full px-3 py-2 border border-neutral-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent text-sm"
                />
              </div>

              <div>
                <label htmlFor="apiSecret" className="block text-sm text-neutral-700 mb-1">
                  Poloniex API Secret
                </label>
                <input
