import React, { useState, useEffect, useCallback } from 'react';
import { useWebSocket } from '../../services/websocketService';
import { useTradingContext } from '../../hooks/useTradingContext';
import { 
  Bell, 
  CheckCircle, 
  AlertTriangle, 
  XCircle, 
  Info,
  X,
  Volume2,
  VolumeX,
  Settings
} from 'lucide-react';

interface MarketDataEvent {
  symbol: string;
  price: number;
  changePercent: number;
  volume: number;
  timestamp: number;
}

interface TradeExecutionEvent {
  id: string;
  symbol: string;
  side: 'buy' | 'sell';
  amount: number;
  price: number;
  profit?: number;
  timestamp: number;
}

export interface RealTimeAlert {
  id: string;
  type: 'success' | 'warning' | 'error' | 'info';
  category: 'trade' | 'market' | 'strategy' | 'system' | 'risk';
  title: string;
  message: string;
  timestamp: number;
  priority: 'low' | 'medium' | 'high' | 'critical';
  acknowledged: boolean;
  data?: Record<string, unknown>; // Additional data for the alert
}

interface AlertRules {
  priceChange: {
    enabled: boolean;
    threshold: number; // percentage
  };
  volumeSpike: {
    enabled: boolean;
    threshold: number; // multiplier
  };
  portfolioChange: {
    enabled: boolean;
    threshold: number; // percentage
  };
  strategyStop: {
    enabled: boolean;
  };
  connectionIssues: {
    enabled: boolean;
  };
}

interface RealTimeAlertsProps {
  maxAlerts?: number;
  soundEnabled?: boolean;
  onAlertClick?: (alert: RealTimeAlert) => void;
}

const RealTimeAlerts: React.FC<RealTimeAlertsProps> = ({
  maxAlerts = 50,
  soundEnabled: initialSoundEnabled = true,
  onAlertClick
}) => {
  const { isConnected, on, off } = useWebSocket();
  const { activeStrategies } = useTradingContext();

  const [alerts, setAlerts] = useState<RealTimeAlert[]>([]);
  const [soundEnabled, setSoundEnabled] = useState(initialSoundEnabled);
  const [showSettings, setShowSettings] = useState(false);
  const [alertRules, setAlertRules] = useState<AlertRules>({
    priceChange: { enabled: true, threshold: 5 },
    volumeSpike: { enabled: true, threshold: 3 },
    portfolioChange: { enabled: true, threshold: 2 },
    strategyStop: { enabled: true },
    connectionIssues: { enabled: true }
  });

  // Create alert utility function
  const createAlert = useCallback((
    type: RealTimeAlert['type'],
    category: RealTimeAlert['category'],
    title: string,
    message: string,
    priority: RealTimeAlert['priority'] = 'medium',
    data?: Record<string, unknown>
  ): RealTimeAlert => {
    return {
      id: `alert-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
      type,
      category,
      title,
      message,
      timestamp: Date.now(),
      priority,
      acknowledged: false,
      data
    };
  }, []);

  // Add alert function
  const addAlert = useCallback((alert: RealTimeAlert) => {
    setAlerts(prev => {
      const newAlerts = [alert, ...prev];

      // Limit alerts to maxAlerts
      if (newAlerts.length > maxAlerts) {
        return newAlerts.slice(0, maxAlerts);
      }

      return newAlerts;
    });

    // Play sound for high/critical priority alerts
    if (soundEnabled && (alert.priority === 'high' || alert.priority === 'critical')) {
      playAlertSound(alert.type);
    }
  }, [maxAlerts, soundEnabled]);

  // Play alert sound
  const playAlertSound = (type: RealTimeAlert['type']) => {
    try {
      const AudioContextClass = window.AudioContext || (window as typeof window & { webkitAudioContext?: typeof AudioContext }).webkitAudioContext;
      if (!AudioContextClass) return;

      const audioContext = new AudioContextClass();
      const oscillator = audioContext.createOscillator();
      const gainNode = audioContext.createGain();

      oscillator.connect(gainNode);
      gainNode.connect(audioContext.destination);

      // Different frequencies for different alert types
      const frequencies = {
        success: 800,
        info: 600,
        warning: 400,
        error: 200
      };

      oscillator.frequency.value = frequencies[type];
      oscillator.type = 'sine';

      gainNode.gain.setValueAtTime(0.3, audioContext.currentTime);
      gainNode.gain.exponentialRampToValueAtTime(0.01, audioContext.currentTime + 0.5);

      oscillator.start(audioContext.currentTime);
      oscillator.stop(audioContext.currentTime + 0.5);
    } catch (error) {
      console.warn('Could not play alert sound:', error);
    }
  };

  // Handle market data alerts
  const handleMarketData = useCallback((marketData: MarketDataEvent) => {
    if (!alertRules.priceChange.enabled) return;

    const changePercent = Math.abs(marketData.changePercent || 0);

    if (changePercent >= alertRules.priceChange.threshold) {
      const alert = createAlert(
        changePercent >= 10 ? 'error' : 'warning',
        'market',
        'Significant Price Movement',
        `${marketData.pair} moved ${marketData.changePercent > 0 ? '+' : ''}${marketData.changePercent.toFixed(2)}%`,
        changePercent >= 10 ? 'high' : 'medium',
        marketData
      );
      addAlert(alert);
    }
  }, [alertRules.priceChange, createAlert, addAlert]);

  // Handle trade execution alerts
  const handleTradeExecuted = useCallback((tradeData: TradeExecutionEvent) => {
    const isProfit = (tradeData.profit || 0) > 0;
    const alert = createAlert(
      isProfit ? 'success' : 'error',
      'trade',
      'Trade Executed',
      `${tradeData.side?.toUpperCase() || 'TRADE'} ${tradeData.amount || 0} ${tradeData.pair || ''} ${isProfit ? `+$${tradeData.profit?.toFixed(2) || '0.00'}` : `$${tradeData.profit?.toFixed(2) || '0.00'}`}`,
      isProfit ? 'low' : 'medium',
      tradeData
    );
    addAlert(alert);
  }, [createAlert, addAlert]);

  // Handle WebSocket connection changes
  const handleConnectionChange = useCallback((connectionState: string) => {
    if (!alertRules.connectionIssues.enabled) return;

    if (connectionState === 'disconnected' || connectionState === 'failed') {
      const alert = createAlert(
        'error',
        'system',
        'Connection Lost',
        'WebSocket connection lost. Trading may be affected.',
        'high'
      );
      addAlert(alert);
    } else if (connectionState === 'connected') {
      const alert = createAlert(
        'success',
        'system',
        'Connected',
        'WebSocket connection restored.',
        'low'
      );
      addAlert(alert);
    }
  }, [alertRules.connectionIssues, createAlert, addAlert]);

  // Set up WebSocket listeners
  useEffect(() => {
    if (isConnected) {
      on('marketData', handleMarketData);
      on('tradeExecuted', handleTradeExecuted);
      on('connectionStateChanged', handleConnectionChange);

      return () => {
        off('marketData', handleMarketData);
        off('tradeExecuted', handleTradeExecuted);
        off('connectionStateChanged', handleConnectionChange);
      };
    }
  }, [isConnected, handleMarketData, handleTradeExecuted, handleConnectionChange, on, off]);

  // Monitor strategy changes
  useEffect(() => {
    if (!alertRules.strategyStop.enabled) return;

    const previousActiveCount = alerts.filter(a => a.category === 'strategy').length;
    const currentActiveCount = activeStrategies.length;

    if (previousActiveCount > currentActiveCount) {
      const alert = createAlert(
        'warning',
        'strategy',
        'Strategy Stopped',
        `A trading strategy has stopped. ${currentActiveCount} strategies still active.`,
        'medium'
      );
      addAlert(alert);
    }
  }, [activeStrategies.length, alertRules.strategyStop, createAlert, addAlert, alerts]);

  // Acknowledge alert
  const acknowledgeAlert = (alertId: string) => {
    setAlerts(prev => 
      prev.map(alert => 
        alert.id === alertId ? { ...alert, acknowledged: true } : alert
      )
    );
  };

  // Remove alert
  const removeAlert = (alertId: string) => {
    setAlerts(prev => prev.filter(alert => alert.id !== alertId));
  };

  // Clear all alerts
  const clearAllAlerts = () => {
    setAlerts([]);
  };

  // Acknowledge all alerts
  const acknowledgeAllAlerts = () => {
    setAlerts(prev => prev.map(alert => ({ ...alert, acknowledged: true })));
  };

  // Get alert icon
  const getAlertIcon = (alert: RealTimeAlert) => {
    switch (alert.type) {
      case 'success':
        return <CheckCircle className="h-5 w-5 text-green-500" />;
      case 'warning':
        return <AlertTriangle className="h-5 w-5 text-yellow-500" />;
      case 'error':
        return <XCircle className="h-5 w-5 text-red-500" />;
      case 'info':
      default:
        return <Info className="h-5 w-5 text-blue-500" />;
    }
  };

  // Get alert background color
  const getAlertBgColor = (alert: RealTimeAlert) => {
    const base = alert.acknowledged ? 'opacity-50 ' : '';
    switch (alert.type) {
      case 'success':
        return base + 'bg-green-50 border-green-200';
      case 'warning':
        return base + 'bg-yellow-50 border-yellow-200';
      case 'error':
        return base + 'bg-red-50 border-red-200';
      case 'info':
      default:
        return base + 'bg-blue-50 border-blue-200';
    }
  };

  // Get priority badge color
  const getPriorityColor = (priority: RealTimeAlert['priority']) => {
    switch (priority) {
      case 'critical':
        return 'bg-red-100 text-red-800';
      case 'high':
        return 'bg-orange-100 text-orange-800';
      case 'medium':
        return 'bg-yellow-100 text-yellow-800';
      case 'low':
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const unacknowledgedCount = alerts.filter(a => !a.acknowledged).length;

  return (
    <div className="space-y-4">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div className="flex items-center space-x-2">
          <Bell className="h-5 w-5 text-neutral-600" />
          <h3 className="text-lg font-medium text-neutral-800">Real-time Alerts</h3>
          {unacknowledgedCount > 0 && (
            <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-red-100 text-red-800">
              {unacknowledgedCount} new
            </span>
          )}
        </div>

        <div className="flex items-center space-x-2">
          {/* Sound Toggle */}
          <button
            onClick={() => setSoundEnabled(!soundEnabled)}
            className={`p-2 rounded-md transition-colors ${
              soundEnabled 
                ? 'bg-blue-100 text-blue-600 hover:bg-blue-200' 
                : 'bg-gray-100 text-gray-400 hover:bg-gray-200'
            }`}
            title={soundEnabled ? 'Disable sound' : 'Enable sound'}
          >
            {soundEnabled ? <Volume2 className="h-4 w-4" /> : <VolumeX className="h-4 w-4" />}
          </button>

          {/* Settings */}
          <button
            onClick={() => setShowSettings(!showSettings)}
            className="p-2 rounded-md bg-gray-100 text-gray-600 hover:bg-gray-200 transition-colors"
            title="Alert settings"
          >
            <Settings className="h-4 w-4" />
          </button>

          {/* Acknowledge All */}
          {unacknowledgedCount > 0 && (
            <button
              onClick={acknowledgeAllAlerts}
              className="px-3 py-1 text-sm bg-blue-100 text-blue-600 rounded-md hover:bg-blue-200 transition-colors"
            >
              Acknowledge All
            </button>
          )}

          {/* Clear All */}
          {alerts.length > 0 && (
            <button
              onClick={clearAllAlerts}
              className="px-3 py-1 text-sm bg-red-100 text-red-600 rounded-md hover:bg-red-200 transition-colors"
            >
              Clear All
            </button>
          )}
        </div>
      </div>

      {/* Settings Panel */}
      {showSettings && (
        <div className="p-4 bg-neutral-50 rounded-md border">
          <h4 className="text-md font-medium mb-3">Alert Settings</h4>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  checked={alertRules.priceChange.enabled}
                  onChange={(e) => setAlertRules(prev => ({
                    ...prev,
                    priceChange: { ...prev.priceChange, enabled: e.target.checked }
                  }))}
                  className="rounded"
                />
                <span className="text-sm">Price change alerts</span>
              </label>
              {alertRules.priceChange.enabled && (
                <input
                  type="number"
                  min="1"
                  max="50"
                  value={alertRules.priceChange.threshold}
                  onChange={(e) => setAlertRules(prev => ({
                    ...prev,
                    priceChange: { ...prev.priceChange, threshold: parseFloat(e.target.value) }
                  }))}
                  className="mt-1 block w-full px-3 py-1 border border-gray-300 rounded-md text-sm"
                  placeholder="Threshold %"
                />
              )}
            </div>

            <div>
              <label className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  checked={alertRules.portfolioChange.enabled}
                  onChange={(e) => setAlertRules(prev => ({
                    ...prev,
                    portfolioChange: { ...prev.portfolioChange, enabled: e.target.checked }
                  }))}
                  className="rounded"
                />
                <span className="text-sm">Portfolio change alerts</span>
              </label>
            </div>

            <div>
              <label className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  checked={alertRules.strategyStop.enabled}
                  onChange={(e) => setAlertRules(prev => ({
                    ...prev,
                    strategyStop: { ...prev.strategyStop, enabled: e.target.checked }
                  }))}
                  className="rounded"
                />
                <span className="text-sm">Strategy alerts</span>
              </label>
            </div>

            <div>
              <label className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  checked={alertRules.connectionIssues.enabled}
                  onChange={(e) => setAlertRules(prev => ({
                    ...prev,
                    connectionIssues: { ...prev.connectionIssues, enabled: e.target.checked }
                  }))}
                  className="rounded"
                />
                <span className="text-sm">Connection alerts</span>
              </label>
            </div>
          </div>
        </div>
      )}

      {/* Alerts List */}
      <div className="space-y-2 max-h-96 overflow-y-auto">
        {alerts.length > 0 ? (
          alerts.map(alert => (
            <div
              key={alert.id}
              className={`p-3 rounded-md border transition-all duration-200 cursor-pointer ${getAlertBgColor(alert)}`}
              onClick={() => {
                if (onAlertClick) {
                  onAlertClick(alert);
                }
                if (!alert.acknowledged) {
                  acknowledgeAlert(alert.id);
                }
              }}
            >
              <div className="flex items-start justify-between">
                <div className="flex items-start space-x-3 flex-1">
                  {getAlertIcon(alert)}
                  <div className="flex-1 min-w-0">
                    <div className="flex items-center space-x-2">
                      <h4 className="text-sm font-medium text-neutral-800 truncate">
                        {alert.title}
                      </h4>
                      <span className={`inline-flex items-center px-2 py-0.5 rounded-full text-xs font-medium ${getPriorityColor(alert.priority)}`}>
                        {alert.priority}
                      </span>
                    </div>
                    <p className="text-sm text-neutral-600 mt-1">
                      {alert.message}
                    </p>
                    <p className="text-xs text-neutral-400 mt-1">
                      {new Date(alert.timestamp).toLocaleTimeString()}
                    </p>
                  </div>
                </div>
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    removeAlert(alert.id);
                  }}
                  className="ml-2 p-1 text-neutral-400 hover:text-neutral-600 transition-colors"
                >
                  <X className="h-4 w-4" />
                </button>
              </div>
            </div>
          ))
        ) : (
          <div className="text-center py-8">
            <Bell className="h-12 w-12 text-neutral-300 mx-auto mb-4" />
            <p className="text-neutral-500">No alerts yet</p>
            <p className="text-sm text-neutral-400 mt-1">
              Real-time alerts will appear here
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default RealTimeAlerts;

import { Activity, TrendingDown, TrendingUp, Wifi, WifiOff } from 'lucide-react';
import React, { useEffect, useState } from 'react';
import { useTradingContext } from '../../hooks/useTradingContext';
import { useWebSocket } from '../../services/websocketService';

interface TickerData {
  symbol: string;
  price: number;
  change24h: number;
  changePercent24h: number;
  volume24h: number;
  high24h: number;
  low24h: number;
  lastUpdateTime: Date;
}

const RealTimeMarketTicker: React.FC = () => {
  const { isConnected, isMockMode } = useWebSocket();
  const { isMockMode: contextMockMode } = useTradingContext();
  const [tickerData, setTickerData] = useState<TickerData[]>([]);
  const [selectedPairs] = useState(['BTC-USDT', 'ETH-USDT', 'ADA-USDT', 'DOT-USDT']);

  // Generate mock ticker data for demonstration
  useEffect(() => {
    const generateMockTickerData = (): TickerData[] => {
      const mockPairs = selectedPairs.map(pair => {
        const basePrices: Record<string, number> = {
          'BTC-USDT': 43000,
          'ETH-USDT': 2500,
          'ADA-USDT': 0.45,
          'DOT-USDT': 6.5,
          'SOL-USDT': 100,
          'MATIC-USDT': 1.0,
          'AVAX-USDT': 35,
          'LINK-USDT': 15
        };
        return { symbol: pair, basePrice: basePrices[pair] || 100 };
      });

      return mockPairs.map(pair => {
        const variance = (Math.random() - 0.5) * 0.1; // 5% variance
        const price = pair.basePrice * (1 + variance);
        const change24h = pair.basePrice * (Math.random() - 0.5) * 0.2; // 10% daily change
        const changePercent24h = (change24h / pair.basePrice) * 100;

        return {
          symbol: pair.symbol,
          price,
          change24h,
          changePercent24h,
          volume24h: Math.random() * 1000000,
          high24h: price * (1 + Math.random() * 0.05),
          low24h: price * (1 - Math.random() * 0.05),
          lastUpdateTime: new Date()
        };
      });
    };

    // Set initial mock data
    setTickerData(generateMockTickerData());

    // Update ticker data every 2 seconds to simulate real-time updates
    const interval = setInterval(() => {
      setTickerData(prevData =>
        prevData.map(ticker => {
          const priceChange = (Math.random() - 0.5) * ticker.price * 0.001; // 0.1% change
          const newPrice = Math.max(0.01, ticker.price + priceChange);
          const change24h = ticker.change24h + priceChange;
          const changePercent24h = (change24h / (newPrice - change24h)) * 100;

          return {
            ...ticker,
            price: newPrice,
            change24h,
            changePercent24h,
            lastUpdateTime: new Date()
          };
        })
      );
    }, 2000);

    return () => clearInterval(interval);
  }, []);

  const formatPrice = (price: number, symbol: string) => {
    if (symbol.includes('BTC')) return price.toFixed(0);
    if (symbol.includes('ETH')) return price.toFixed(0);
    return price.toFixed(4);
  };

  const formatVolume = (volume: number) => {
    if (volume >= 1000000)
    {
      return `${(volume / 1000000).toFixed(1)}M`;
    } else if (volume >= 1000)
    {
      return `${(volume / 1000).toFixed(1)}K`;
    }
    return volume.toFixed(0);
  };

  const getConnectionStatusColor = () => {
    if (isConnected && !isMockMode && !contextMockMode)
    {
      return 'bg-green-500';
    } else if (isMockMode || contextMockMode)
    {
      return 'bg-yellow-500';
    } else
    {
      return 'bg-red-500';
    }
  };

  const getConnectionStatusText = () => {
    if (isConnected && !isMockMode && !contextMockMode)
    {
      return 'Live Data';
    } else if (isMockMode || contextMockMode)
    {
      return 'Mock Data';
    } else
    {
      return 'Offline';
    }
  };

  return (
    <div className="bg-white rounded-lg shadow p-4">
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-lg font-semibold flex items-center">
          <Activity className="w-5 h-5 mr-2 text-blue-600" />
          Market Ticker
        </h3>
        <div className="flex items-center space-x-2">
          <div className={`w-2 h-2 rounded-full ${getConnectionStatusColor()} animate-pulse`}></div>
          <span className="text-xs text-gray-600">{getConnectionStatusText()}</span>
          {isConnected ?
            <Wifi className="w-4 h-4 text-green-600" /> :
            <WifiOff className="w-4 h-4 text-red-600" />
          }
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        {tickerData.map((ticker) => (
          <div
            key={ticker.symbol}
            className="border border-gray-200 rounded-md p-3 hover:border-blue-300 transition-colors"
          >
            <div className="flex items-center justify-between mb-2">
              <span className="font-medium text-gray-900">{ticker.symbol}</span>
              {ticker.changePercent24h >= 0 ? (
                <TrendingUp className="w-4 h-4 text-green-600" />
              ) : (
                <TrendingDown className="w-4 h-4 text-red-600" />
              )}
            </div>

            <div className="space-y-1">
              <div className="text-xl font-bold text-gray-900">
                ${formatPrice(ticker.price, ticker.symbol)}
              </div>

              <div className={`text-sm font-medium ${ticker.changePercent24h >= 0 ? 'text-green-600' : 'text-red-600'
                }`}>
                {ticker.changePercent24h >= 0 ? '+' : ''}{ticker.changePercent24h.toFixed(2)}%
                <span className="ml-1 text-gray-500">
                  ({ticker.change24h >= 0 ? '+' : ''}${ticker.change24h.toFixed(2)})
                </span>
              </div>

              <div className="text-xs text-gray-500 space-y-0.5">
                <div>Vol: {formatVolume(ticker.volume24h)}</div>
                <div>H: ${formatPrice(ticker.high24h, ticker.symbol)} L: ${formatPrice(ticker.low24h, ticker.symbol)}</div>
              </div>
            </div>

            <div className="mt-2 text-xs text-gray-400">
              {ticker.lastUpdateTime.toLocaleTimeString()}
            </div>
          </div>
        ))}
      </div>

      {/* Real-time status indicator */}
      <div className="mt-4 flex items-center justify-center text-xs text-gray-500">
        <div className="flex items-center space-x-1">
          <div className="w-1 h-1 bg-blue-500 rounded-full animate-pulse"></div>
          <span>Updates every 2 seconds</span>
        </div>
      </div>
    </div>
  );
};

export default RealTimeMarketTicker;

import React, { useState, useEffect } from 'react';
import { 
  monitorMLModelPerformance, 
  monitorDQNModelPerformance, 
  recalibrateMLModel,
  recalibrateDQNModel,
  scheduleModelRecalibration,
  RecalibrationConfig,
  ModelPerformanceMetrics,
  RecalibrationResult
} from '@/ml/modelRecalibration';
import { usePoloniexData } from '@/hooks/usePoloniexData';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

interface ModelData {
  id: string;
  name: string;
  type: string;
  performance: number;
}

interface MarketDataPoint {
  timestamp: number;
  price: number;
  volume: number;
}

const ModelRecalibrationPanel: React.FC = () => {
  const { marketData: poloniexMarketData, fetchMarketData } = usePoloniexData();

  const [mlModels, setMlModels] = useState<ModelData[]>([]);
  const [dqnModels, setDqnModels] = useState<ModelData[]>([]);
  const [selectedModel, setSelectedModel] = useState<ModelData | null>(null);
  const [marketData, setMarketData] = useState<MarketDataPoint[]>([]);
  const [performanceMetrics, setPerformanceMetrics] = useState<ModelPerformanceMetrics | null>(null);
  const [recalibrationResult, setRecalibrationResult] = useState<RecalibrationResult | null>(null);
  const [performanceHistory, setPerformanceHistory] = useState<ModelPerformanceMetrics[]>([]);
  const [isMonitoring, setIsMonitoring] = useState(false);
  const [isRecalibrating, setIsRecalibrating] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [recalibrationConfig, setRecalibrationConfig] = useState<RecalibrationConfig>({
    monitoringFrequency: 'daily',
    driftThreshold: 0.15,
    autoRecalibrate: true,
    performanceMetrics: ['accuracy', 'f1Score', 'sharpeRatio', 'winRate'],
    validationSplit: 0.3,
    maxHistoricalModels: 5,
    recalibrationStrategy: 'incremental'
  });
  const [recalibrationStrategy, setRecalibrationStrategy] = useState<'full' | 'incremental' | 'transfer'>('incremental');
  const [autoRecalibrationEnabled, setAutoRecalibrationEnabled] = useState(false);
  const [cleanupFunction, setCleanupFunction] = useState<(() => void) | null>(null);

  // Load models from storage
  useEffect(() => {
    const loadModels = async () => {
      try {
        // In a real implementation, this would load from a database or file storage
        // For now, we'll just check if there are any models in localStorage
        const mlModelsJson = localStorage.getItem('mlModels');
        const dqnModelsJson = localStorage.getItem('dqnModels');

        if (mlModelsJson) {
          setMlModels(JSON.parse(mlModelsJson));
        }

        if (dqnModelsJson) {
          setDqnModels(JSON.parse(dqnModelsJson));
        }
      } catch (err) {
        console.error('Error loading models:', err);
        setError('Failed to load models');
      }
    };

    loadModels();
  }, []);

  // Fetch market data
  useEffect(() => {
    const fetchData = async () => {
      try {
        // Get market data for monitoring and recalibration
        await fetchMarketData('BTC_USDT');
        setMarketData(poloniexMarketData);
      } catch (err) {
        console.error('Error fetching market data:', err);
        setError('Failed to fetch market data');
      }
    };

    fetchData();

    // Set up interval to refresh data
    const intervalId = setInterval(fetchData, 4 * 60 * 60 * 1000); // Refresh every 4 hours

    return () => clearInterval(intervalId);
  }, [fetchMarketData, poloniexMarketData]);

  // Monitor model performance
  const handleMonitorPerformance = async () => {
    if (!selectedModel) {
      setError('No model selected');
      return;
    }

    if (marketData.length === 0) {
      setError('No market data available');
      return;
    }

    setIsMonitoring(true);
    setError(null);

    try {
      let metrics;

      if (selectedModel.config.modelType) {
        // ML model
        metrics = await monitorMLModelPerformance(selectedModel, marketData);
      } else {
        // DQN model
        metrics = await monitorDQNModelPerformance(selectedModel, marketData);
      }

      setPerformanceMetrics(metrics);

      // Add to performance history
      setPerformanceHistory(prev => [...prev, metrics]);

      setIsMonitoring(false);
    } catch (err) {
      console.error('Error monitoring performance:', err);
      setError('Failed to monitor performance');
      setIsMonitoring(false);
    }
  };

  // Recalibrate model
  const handleRecalibrate = async () => {
    if (!selectedModel) {
      setError('No model selected');
      return;
    }

    if (marketData.length === 0) {
      setError('No market data available');
      return;
    }

    setIsRecalibrating(true);
    setError(null);

    try {
      let result;

      if (selectedModel.config.modelType) {
        // ML model
        result = await recalibrateMLModel(selectedModel, marketData, recalibrationStrategy);
      } else {
        // DQN model
        result = await recalibrateDQNModel(selectedModel, marketData, recalibrationStrategy);
      }

      setRecalibrationResult(result);

      // Add to recalibration history
      setRecalibrationHistory(prev => [...prev, result]);

      // Update model list with new model
      if (selectedModel.config.modelType) {
        // ML model
        setMlModels(prev => [...prev, { id: result.newModelId, name: `${selectedModel.name} (Recalibrated)` }]);
      } else {
        // DQN model
        setDqnModels(prev => [...prev, { id: result.newModelId, name: `${selectedModel.name} (Recalibrated)` }]);
      }

      setIsRecalibrating(false);
    } catch (err) {
      console.error('Error recalibrating model:', err);
      setError('Failed to recalibrate model');
      setIsRecalibrating(false);
    }
  };

  // Toggle auto-recalibration
  const handleToggleAutoRecalibration = () => {
    if (autoRecalibrationEnabled) {
      // Disable auto-recalibration
      if (cleanupFunction) {
        cleanupFunction();
        setCleanupFunction(null);
      }
      setAutoRecalibrationEnabled(false);
    } else {
      // Enable auto-recalibration
      if (!selectedModel) {
        setError('No model selected');
        return;
      }

      const getNewData = async () => {
        try {
          await fetchMarketData('BTC_USDT');
          return poloniexMarketData;
        } catch (err) {
          console.error('Error fetching new data for auto-recalibration:', err);
          return [];
        }
      };

      // Schedule recalibration
      const scheduleRecalibration = async () => {
        try {
          const newData = await getNewData();
          if (newData.length === 0) return;

          const result = await scheduleModelRecalibration(
            selectedModel,
            recalibrationConfig,
            newData
          );

          if (result) {
            // Update recalibration history
            setRecalibrationHistory(prev => [...prev, result]);

            // Update model list with new model
            if (selectedModel.config.modelType) {
              // ML model
              setMlModels(prev => [...prev, { id: result.newModelId, name: `${selectedModel.name} (Recalibrated)` }]);
            } else {
              // DQN model
              setDqnModels(prev => [...prev, { id: result.newModelId, name: `${selectedModel.name} (Recalibrated)` }]);
            }
          }
        } catch (err) {
          console.error('Error in scheduled recalibration:', err);
        }
      };

      // Set up interval based on monitoring frequency
      let intervalMs = 24 * 60 * 60 * 1000; // Default: daily

      if (recalibrationConfig.monitoringFrequency === 'hourly') {
        intervalMs = 60 * 60 * 1000;
      } else if (recalibrationConfig.monitoringFrequency === 'weekly') {
        intervalMs = 7 * 24 * 60 * 60 * 1000;
      }

      // Initial run
      scheduleRecalibration();

      // Set up interval
      const intervalId = setInterval(scheduleRecalibration, intervalMs);

      // Cleanup function
      const cleanup = () => clearInterval(intervalId);

      setCleanupFunction(() => cleanup);
      setAutoRecalibrationEnabled(true);
    }
  };

  // Format performance metrics for display
  const formatMetrics = (metrics: ModelPerformanceMetrics) => {
    const result = [];

    for (const [key, value] of Object.entries(metrics)) {
      if (key !== 'timestamp' && key !== 'modelId' && key !== 'modelType' && typeof value === 'number') {
        result.push({
          name: key.charAt(0).toUpperCase() + key.slice(1),
          value: value.toFixed(4)
        });
      }
    }

    return result;
  };

  // Prepare performance history data for chart
  const prepareChartData = () => {
    return performanceHistory.map((metrics, index) => ({
      index,
      timestamp: new Date(metrics.timestamp).toLocaleDateString(),
      driftScore: metrics.driftScore,
      accuracy: metrics.accuracy,
      f1Score: metrics.f1Score,
      sharpeRatio: metrics.sharpeRatio,
      winRate: metrics.winRate
    }));
  };

  return (
    <div className="bg-white dark:bg-neutral-800 rounded-lg shadow p-6">
      <h2 className="text-xl font-semibold mb-4 text-neutral-800 dark:text-white">Model Recalibration</h2>

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}

      <div className="mb-6">
        <h3 className="text-lg font-medium mb-2 text-neutral-700 dark:text-neutral-300">Select Model</h3>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <label className="block text-sm font-medium text-neutral-700 dark:text-neutral-300 mb-1">
              ML Models
            </label>
            <select
              value={selectedModel?.id || ''}
              onChange={(e) => {
                const model = mlModels.find(m => m.id === e.target.value);
                setSelectedModel(model || null);
              }}
              className="w-full px-3 py-2 border border-neutral-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 dark:bg-neutral-700 dark:border-neutral-600 dark:text-white"
            >
              <option value="">Select ML Model</option>
              {mlModels.map(model => (
                <option key={model.id} value={model.id}>{model.name}</option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-neutral-700 dark:text-neutral-300 mb-1">
              DQN Models
            </label>
            <select
              value={selectedModel?.id || ''}
              onChange={(e) => {
                const model = dqnModels.find(m => m.id === e.target.value);
                setSelectedModel(model || null);
              }}
              className="w-full px-3 py-2 border border-neutral-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 dark:bg-neutral-700 dark:border-neutral-600 dark:text-white"
            >
              <option value="">Select DQN Model</option>
              {dqnModels.map(model => (
                <option key={model.id} value={model.id}>{model.name}</option>
              ))}
            </select>
          </div>
        </div>
      </div>

      <div className="mb-6">
        <h3 className="text-lg font-medium mb-2 text-neutral-700 dark:text-neutral-300">Recalibration Settings</h3>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <label className="block text-sm font-medium text-neutral-700 dark:text-neutral-300 mb-1">
              Recalibration Strategy
            </label>
            <select
              value={recalibrationStrategy}
              onChange={(e) => setRecalibrationStrategy(e.target.value as 'full' | 'incremental' | 'transfer')}
              className="w-full px-3 py-2 border border-neutral-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 dark:bg-neutral-700 dark:border-neutral-600 dark:text-white"
            >
              <option value="incremental">Incremental (Fine-tune existing model)</option>
              <option value="full">Full (Retrain from scratch)</option>
              <option value="transfer">Transfer Learning (Optimize for new data)</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-neutral-700 dark:text-neutral-300 mb-1">
              Drift Threshold
            </label>
            <input
              type="number"
              min="0.01"
              max="0.5"
              step="0.01"
              value={recalibrationConfig.driftThreshold}
              onChange={(e) => setRecalibrationConfig({ ...recalibrationConfig, driftThreshold: parseFloat(e.target.value) })}
              className="w-full px-3 py-2 border border-neutral-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 dark:bg-neutral-700 dark:border-neutral-600 dark:text-white"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-neutral-700 dark:text-neutral-300 mb-1">
              Monitoring Frequency
            </label>
            <select
              value={recalibrationConfig.monitoringFrequency}
              onChange={(e) => setRecalibrationConfig({ ...recalibrationConfig, monitoringFrequency: e.target.value as 'hourly' | 'daily' | 'weekly' })}
              className="w-full px-3 py-2 border border-neutral-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 dark:bg-neutral-700 dark:border-neutral-600 dark:text-white"
            >
              <option value="hourly">Hourly</option>
              <option value="daily">Daily</option>
              <option value="weekly">Weekly</option>
            </select>
          </div>

          <div className="flex items-center">
            <input
              type="checkbox"
              id="autoRecalibrate"
              checked={recalibrationConfig.autoRecalibrate}
              onChange={(e) => setRecalibrationConfig({ ...recalibrationConfig, autoRecalibrate: e.target.checked })}
              className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-neutral-300 rounded"
            />
            <label htmlFor="autoRecalibrate" className="ml-2 block text-sm text-neutral-700 dark:text-neutral-300">
              Auto-recalibrate when drift exceeds threshold
            </label>
          </div>
        </div>
      </div>

      <div className="flex flex-wrap gap-4 mb-6">
        <button
          onClick={handleMonitorPerformance}
          disabled={isMonitoring || !selectedModel}
          className={`px-4 py-2 rounded-md text-white ${
            isMonitoring || !selectedModel ? 'bg-neutral-400' : 'bg-blue-600 hover:bg-blue-700'
          }`}
        >
          {isMonitoring ? 'Monitoring...' : 'Monitor Performance'}
        </button>

        <button
          onClick={handleRecalibrate}
          disabled={isRecalibrating || !selectedModel}
          className={`px-4 py-2 rounded-md text-white ${
            isRecalibrating || !selectedModel ? 'bg-neutral-400' : 'bg-green-600 hover:bg-green-700'
          }`}
        >
          {isRecalibrating ? 'Recalibrating...' : 'Recalibrate Model'}
        </button>

        <button
          onClick={handleToggleAutoRecalibration}
          disabled={!selectedModel}
          className={`px-4 py-2 rounded-md text-white ${
            !selectedModel ? 'bg-neutral-400' : autoRecalibrationEnabled ? 'bg-red-600 hover:bg-red-700' : 'bg-purple-600 hover:bg-purple-700'
          }`}
        >
          {autoRecalibrationEnabled ? 'Disable Auto-Recalibration' : 'Enable Auto-Recalibration'}
        </button>
      </div>

      {performanceMetrics && (
        <div className="mb-6">
          <h3 className="text-lg font-medium mb-2 text-neutral-700 dark:text-neutral-300">Performance Metrics</h3>

          <div className="bg-neutral-100 dark:bg-neutral-700 p-4 rounded-md">
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
              {formatMetrics(performanceMetrics).map((metric, index) => (
                <div key={index}>
                  <p className="text-sm font-medium text-neutral-500 dark:text-neutral-400">{metric.name}</p>
                  <p className="text-neutral-800 dark:text-white">{metric.value}</p>
                </div>
              ))}
            </div>

            <div className="mt-4">
              <p className="text-sm font-medium text-neutral-500 dark:text-neutral-400">Drift Status</p>
              <div className="flex items-center mt-1">
                <div className="w-full bg-neutral-200 rounded-full h-2.5 dark:bg-neutral-700">
                  <div 
                    className={`h-2.5 rounded-full ${
                      performanceMetrics.driftScore! > recalibrationConfig.driftThreshold
                        ? 'bg-red-600'
                        : 'bg-green-600'
                    }`} 
                    style={{ width: `${performanceMetrics.driftScore! * 100}%` }}
                  ></div>
                </div>
                <span className="ml-2 text-sm text-neutral-700 dark:text-neutral-300">
                  {(performanceMetrics.driftScore! * 100).toFixed(1)}%
                </span>
              </div>
              {performanceMetrics.driftScore! > recalibrationConfig.driftThreshold && (
                <p className="mt-1 text-sm text-red-600">
                  Significant drift detected. Recalibration recommended.
                </p>
              )}
            </div>
          </div>
        </div>
      )}

      {recalibrationResult && (
        <div className="mb-6">
          <h3 className="text-lg font-medium mb-2 text-neutral-700 dark:text-neutral-300">Recalibration Results</h3>

          <div className="bg-neutral-100 dark:bg-neutral-700 p-4 rounded-md">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <p className="text-sm font-medium text-neutral-500 dark:text-neutral-400">Original Model</p>
                <p className="text-neutral-800 dark:text-white">{recalibrationResult.originalModelId}</p>
              </div>

              <div>
                <p className="text-sm font-medium text-neutral-500 dark:text-neutral-400">New Model</p>
                <p className="text-neutral-800 dark:text-white">{recalibrationResult.newModelId}</p>
              </div>

              <div>
                <p className="text-sm font-medium text-neutral-500 dark:text-neutral-400">Recalibration Strategy</p>
                <p className="text-neutral-800 dark:text-white">{recalibrationResult.recalibrationStrategy}</p>
              </div>

              <div>
                <p className="text-sm font-medium text-neutral-500 dark:text-neutral-400">Performance Improvement</p>
                <p className={`${recalibrationResult.performanceImprovement > 0 ? 'text-green-600' : 'text-red-600'}`}>
                  {(recalibrationResult.performanceImprovement * 100).toFixed(1)}%
                </p>
              </div>

              <div className="col-span-2">
                <p className="text-sm font-medium text-neutral-500 dark:text-neutral-400">Reason</p>
                <p className="text-neutral-800 dark:text-white">{recalibrationResult.reason}</p>
              </div>
            </div>
          </div>
        </div>
      )}

      {performanceHistory.length > 1 && (
        <div className="mb-6">
          <h3 className="text-lg font-medium mb-2 text-neutral-700 dark:text-neutral-300">Performance History</h3>

          <div className="bg-neutral-100 dark:bg-neutral-700 p-4 rounded-md" style={{ height: '300px' }}>
            <ResponsiveContainer width="100%" height="100%">
              <LineChart data={prepareChartData()}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="timestamp" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Line type="monotone" dataKey="driftScore" stroke="#ff0000" name="Drift Score" />
                <Line type="monotone" dataKey="accuracy" stroke="#00ff00" name="Accuracy" />
                <Line type="monotone" dataKey="f1Score" stroke="#0000ff" name="F1 Score" />
                <Line type="monotone" dataKey="sharpeRatio" stroke="#ff00ff" name="Sharpe Ratio" />
                <Line type="monotone" dataKey="winRate" stroke="#00ffff" name="Win Rate" />
              </LineChart>
            </ResponsiveContainer>
          </div>
        </div>
      )}
    </div>
  );
};

