        </div>
        <PriceChart data={marketData} pair={strategy.parameters.pair} />
      </div>
    </div>
  );
};

export default StrategyDetails;

import { useState, useEffect } from 'react';
import { useSettings } from '../../hooks/useSettings';
import { tradingEngine } from '@/trading/tradingEngine';
import { executeStrategy } from '@/utils/strategyExecutors';
import { poloniexApi } from '@/services/poloniexAPI';
import { logger } from '@/utils/logger';
import { Card, CardHeader, CardBody, CardFooter, Button, Switch, Label, Alert } from '@/components/ui';

const AutomationPanel: React.FC = () => {
  const { 
    autoTradingEnabled, 
    defaultPair,
    // Removed unused timeframe variable
    leverage,
    riskPerTrade,
    stopLossPercent,
    takeProfitPercent,
    updateSettings
  } = useSettings();

  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [strategies, setStrategies] = useState<any[]>([]);
  const [activeStrategy, setActiveStrategy] = useState<string | null>(null);
  const [lastSignal, setLastSignal] = useState<{
    time: string;
    pair: string;
    signal: string | null;
    reason: string;
  } | null>(null);

  // Load saved strategies
  useEffect(() => {
    const savedStrategies = localStorage.getItem('trading_strategies');
    if (savedStrategies) {
      try {
        setStrategies(JSON.parse(savedStrategies));
      } catch (err) {
        console.error('Failed to parse saved strategies:', err);
      }
    }

    // Load active strategy
    const active = localStorage.getItem('active_strategy');
    if (active) {
      setActiveStrategy(active);
    }
  }, []);

  // Start/stop automation
  const toggleAutomation = async () => {
    try {
      if (autoTradingEnabled) {
        await tradingEngine.stopTrading();
        updateSettings({ autoTradingEnabled: false });
      } else {
        if (!activeStrategy) {
          setError('Please select a strategy to activate automation');
          return;
        }

        await tradingEngine.startTradingLoop();
        updateSettings({ autoTradingEnabled: true });
      }
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to toggle automation');
    }
  };

  // Activate a strategy
  const activateStrategy = (strategyId: string) => {
    setActiveStrategy(strategyId);
    localStorage.setItem('active_strategy', strategyId);
  };

  // Run strategy once
  const runStrategyOnce = async (strategyId: string) => {
    setIsLoading(true);
    setError(null);

    try {
      const strategy = strategies.find(s => s.id === strategyId);
      if (!strategy) {
        throw new Error('Strategy not found');
      }

      // Get market data
      const pair = strategy.parameters.pair || defaultPair;
      const data = await poloniexApi.getMarketData(pair);

      // Execute strategy
      const result = executeStrategy(strategy, data);

      // Record signal
      setLastSignal({
        time: new Date().toLocaleTimeString(),
        pair,
        signal: result.signal,
        reason: result.reason
      });

      // Execute trade if signal is present and confidence is high enough
      if (result.signal && result.confidence > 0.7) {
        const side = result.signal === 'BUY' ? 'buy' : 'sell';

        // Check if we're in paper or live mode
        if (tradingEngine.modeManager.isLiveMode()) {
          // For live trading, use the API - fixed parameter count
          await poloniexApi.placeOrder(
            pair,
            side,
            'market',
            0.001 // Minimum order size
          );
        } else {
          // For paper trading, use the paper engine
          const paperEngine = tradingEngine.modeManager.getPaperEngine();
          await paperEngine.placeOrder({
            symbol: pair,
            side: side.toLowerCase() as 'buy' | 'sell',
            type: 'market',
            size: 0.001,
            leverage
          });
        }

        logger.info(`Executed ${side} order based on strategy ${strategy.name}`);
      }
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to run strategy');
      console.error(err);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <Card className="w-full">
      <CardHeader>
        <h3 className="text-lg font-medium">Trading Automation</h3>
      </CardHeader>
      <CardBody>
        {error && (
          <Alert variant="error" className="mb-4">
            {error}
          </Alert>
        )}

        <div className="space-y-4">
          <div className="flex items-center justify-between">
            <div>
              <Label htmlFor="automation">Automated Trading</Label>
              <p className="text-xs text-neutral-500">
                Automatically execute trades based on your active strategy
              </p>
            </div>
            <Switch
              id="automation"
              checked={autoTradingEnabled}
              onCheckedChange={toggleAutomation}
              disabled={isLoading || !activeStrategy}
            />
          </div>

          {lastSignal && (
            <div className="p-4 border rounded-md">
              <h4 className="font-medium mb-2">Last Signal:</h4>
              <p><strong>Time:</strong> {lastSignal.time}</p>
              <p><strong>Pair:</strong> {lastSignal.pair}</p>
              <p><strong>Signal:</strong> {lastSignal.signal || 'No signal'}</p>
              <p><strong>Reason:</strong> {lastSignal.reason}</p>
            </div>
          )}

          <div className="mt-6">
            <h4 className="font-medium mb-2">Available Strategies</h4>
            {strategies.length === 0 ? (
              <p className="text-neutral-500">No strategies available. Create one in the Strategy Builder.</p>
            ) : (
              <div className="space-y-2">
                {strategies.map(strategy => (
                  <div 
                    key={strategy.id} 
                    className={`p-3 border rounded-md flex justify-between items-center ${
                      activeStrategy === strategy.id ? 'border-blue-500 bg-blue-50' : ''
                    }`}
                  >
                    <div>
                      <p className="font-medium">{strategy.name}</p>
                      <p className="text-sm text-neutral-500">{strategy.type} - {strategy.parameters.pair}</p>
                    </div>
                    <div className="flex space-x-2">
                      <Button 
                        size="sm" 
                        variant={activeStrategy === strategy.id ? "default" : "outline"}
                        onClick={() => activateStrategy(strategy.id)}
                      >
                        {activeStrategy === strategy.id ? 'Active' : 'Activate'}
                      </Button>
                      <Button 
                        size="sm" 
                        variant="outline" 
                        onClick={() => runStrategyOnce(strategy.id)}
                        disabled={isLoading}
                      >
                        Run Once
                      </Button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </CardBody>
      <CardFooter>
        <p className="text-sm text-neutral-500">
          Trading parameters: {leverage}x leverage, {riskPerTrade}% risk per trade, 
          {stopLossPercent}% stop loss, {takeProfitPercent}% take profit
        </p>
      </CardFooter>
    </Card>
  );
};

export default AutomationPanel;

import React, { useState, useEffect } from 'react';
import { default as dqnTrading } from '@/ml/dqnTrading';
import { usePoloniexData } from '@/hooks/usePoloniexData';
import { useSettings } from '../../hooks/useSettings';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

interface DQNConfig {
  stateDimension: number;
  actionDimension: number;
  learningRate: number;
  gamma: number;
  epsilonStart: number;
  epsilonEnd: number;
  epsilonDecay: number;
  memorySize: number;
  batchSize: number;
  updateTargetFreq: number;
  hiddenLayers: number[];
  activationFunction: string;
  optimizer: string;
}

interface DQNModelInfo {
  id: string;
  name: string;
  description: string;
  config: DQNConfig;
  performance: {
    averageReward: number;
    cumulativeReward: number;
    sharpeRatio: number;
    maxDrawdown: number;
    winRate: number;
    episodeRewards: number[];
    trainingEpisodes: number;
  };
  createdAt: number;
  updatedAt: number;
  lastTrainedAt: number;
  status: 'training' | 'ready' | 'error';
  episodesCompleted: number;
  totalTrainingSteps: number;
}

interface DQNAction {
  timestamp: number;
  symbol: string;
  action: 'buy' | 'sell' | 'hold';
  confidence: number;
  position?: number;
  price?: number;
}

const DQNTradingPanel: React.FC = () => {
  const { marketData: poloniexMarketData, fetchMarketData } = usePoloniexData();
  const { defaultPair, timeframe } = useSettings();

  const [modelConfig, setModelConfig] = useState<Partial<DQNConfig>>({
    stateDimension: 8, // Default state dimension
    actionDimension: 3, // Default action dimension (buy, sell, hold)
    learningRate: 0.0001,
    gamma: 0.99,
    epsilonStart: 1.0,
    epsilonEnd: 0.01,
    epsilonDecay: 0.995,
    memorySize: 10000,
    batchSize: 32,
    updateTargetFreq: 100,
    hiddenLayers: [128, 64],
    activationFunction: 'relu',
    optimizer: 'adam',
  });

  const [modelInfo, setModelInfo] = useState<DQNModelInfo | null>(null);
  const [actions, setActions] = useState<DQNAction[]>([]);
  const [isTraining, setIsTraining] = useState(false);
  const [isGettingActions, setIsGettingActions] = useState(false);
  const [isContinuingTraining, setIsContinuingTraining] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [marketData, setMarketData] = useState<any[]>([]);
  const [modelName, setModelName] = useState('My DQN Model');
  const [episodes, setEpisodes] = useState(100);
  const [additionalEpisodes, setAdditionalEpisodes] = useState(50);
  const [performanceData, setPerformanceData] = useState<any[]>([]);

  // Fetch market data
  useEffect(() => {
    const fetchData = async () => {
      try {
        // Get market data for training
        await fetchMarketData(defaultPair);
        setMarketData(poloniexMarketData);
      } catch (err) {
        setError('Failed to fetch market data');
        console.error(err);
      }
    };

    fetchData();
  }, [defaultPair, timeframe, fetchMarketData, poloniexMarketData]);

  // Train model
  const handleTrainModel = async () => {
    if (marketData.length === 0) {
      setError('No market data available for training');
      return;
    }

    setIsTraining(true);
    setError(null);

    try {
      const info = await dqnTrading.trainDQNModel(marketData, modelConfig as DQNConfig, modelName, episodes);
      setModelInfo(info);

      // Prepare performance data for chart
      const chartData = info.performance.episodeRewards.map((reward, index) => ({
        episode: index + 1,
        reward,
      }));
      setPerformanceData(chartData);

      setIsTraining(false);
    } catch (err) {
      setError('Failed to train model');
      console.error(err);
      setIsTraining(false);
    }
  };

  // Get trading actions
  const handleGetActions = async () => {
    if (!modelInfo) {
      setError('No trained model available');
      return;
    }

    if (marketData.length === 0) {
      setError('No market data available for prediction');
      return;
    }

    setIsGettingActions(true);
    setError(null);

    try {
      const actionsList = await dqnTrading.getDQNActions(modelInfo, marketData);
      setActions(actionsList);
      setIsGettingActions(false);
    } catch (err) {
      setError('Failed to get trading actions');
      console.error(err);
      setIsGettingActions(false);
    }
  };

  // Continue training
  const handleContinueTraining = async () => {
    if (!modelInfo) {
      setError('No trained model available');
      return;
    }

    if (marketData.length === 0) {
      setError('No market data available for training');
      return;
    }

    setIsContinuingTraining(true);
    setError(null);

    try {
      const updatedInfo = await dqnTrading.continueDQNTraining(modelInfo, marketData, additionalEpisodes);
      setModelInfo(updatedInfo);

      // Update performance data for chart
      const chartData = updatedInfo.performance.episodeRewards.map((reward, index) => ({
        episode: index + 1,
        reward,
      }));
      setPerformanceData(chartData);

      setIsContinuingTraining(false);
    } catch (err) {
      setError('Failed to continue training');
      console.error(err);
      setIsContinuingTraining(false);
    }
  };

  // Execute trades based on DQN actions
  const handleExecuteTrades = () => {
    if (actions.length === 0) {
      setError('No actions available');
      return;
    }

    // Get the latest action
    const latestAction = actions[0];

    if (latestAction.action === 'buy') {
      // Execute buy strategy
      // TODO: Implement executeStrategy functionality
      console.log('DQN Strategy - BUY signal:', {
        type: 'DQN_STRATEGY',
        action: 'BUY',
        symbol: defaultPair,
        amount: 0.01, // Small fixed amount
        confidence: latestAction.confidence,
        modelId: modelInfo?.id
      });
    } else if (latestAction.action === 'sell') {
      // Execute sell strategy
      // TODO: Implement executeStrategy functionality
      console.log('DQN Strategy - SELL signal:', {
        type: 'DQN_STRATEGY',
        action: 'SELL',
        symbol: defaultPair,
        amount: 0.01, // Small fixed amount
        confidence: latestAction.confidence,
        modelId: modelInfo?.id
      });
    }
  };

  return (
    <div className="bg-white dark:bg-neutral-800 rounded-lg shadow p-6">
      <h2 className="text-xl font-semibold mb-4 text-neutral-800 dark:text-white">DQN Reinforcement Learning Trading</h2>

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}

      <div className="mb-6">
        <h3 className="text-lg font-medium mb-2 text-neutral-700 dark:text-neutral-300">Model Configuration</h3>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <label className="block text-sm font-medium text-neutral-700 dark:text-neutral-300 mb-1">
              Model Name
            </label>
            <input
              type="text"
              value={modelName}
              onChange={(e) => setModelName(e.target.value)}
              className="w-full px-3 py-2 border border-neutral-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 dark:bg-neutral-700 dark:border-neutral-600 dark:text-white"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-neutral-700 dark:text-neutral-300 mb-1">
              Training Episodes
            </label>
            <input
              type="number"
              min="10"
              max="1000"
              value={episodes}
              onChange={(e) => setEpisodes(parseInt(e.target.value))}
              className="w-full px-3 py-2 border border-neutral-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 dark:bg-neutral-700 dark:border-neutral-600 dark:text-white"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-neutral-700 dark:text-neutral-300 mb-1">
              Learning Rate
            </label>
            <input
              type="number"
              min="0.00001"
              max="0.1"
              step="0.00001"
              value={modelConfig.learningRate}
              onChange={(e) => setModelConfig({ ...modelConfig, learningRate: parseFloat(e.target.value) })}
              className="w-full px-3 py-2 border border-neutral-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 dark:bg-neutral-700 dark:border-neutral-600 dark:text-white"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-neutral-700 dark:text-neutral-300 mb-1">
              Discount Factor (Gamma)
            </label>
            <input
              type="number"
              min="0.5"
              max="0.999"
              step="0.001"
              value={modelConfig.gamma}
              onChange={(e) => setModelConfig({ ...modelConfig, gamma: parseFloat(e.target.value) })}
              className="w-full px-3 py-2 border border-neutral-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 dark:bg-neutral-700 dark:border-neutral-600 dark:text-white"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-neutral-700 dark:text-neutral-300 mb-1">
              Initial Exploration Rate
            </label>
            <input
              type="number"
              min="0.1"
              max="1.0"
              step="0.1"
              value={modelConfig.epsilonStart}
              onChange={(e) => setModelConfig({ ...modelConfig, epsilonStart: parseFloat(e.target.value) })}
              className="w-full px-3 py-2 border border-neutral-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 dark:bg-neutral-700 dark:border-neutral-600 dark:text-white"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-neutral-700 dark:text-neutral-300 mb-1">
              Final Exploration Rate
            </label>
            <input
              type="number"
              min="0.001"
              max="0.5"
              step="0.001"
              value={modelConfig.epsilonEnd}
              onChange={(e) => setModelConfig({ ...modelConfig, epsilonEnd: parseFloat(e.target.value) })}
              className="w-full px-3 py-2 border border-neutral-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 dark:bg-neutral-700 dark:border-neutral-600 dark:text-white"
            />
          </div>
        </div>
      </div>

      <div className="flex flex-wrap gap-4 mb-6">
        <button
          onClick={handleTrainModel}
          disabled={isTraining || marketData.length === 0}
          className={`px-4 py-2 rounded-md text-white ${
            isTraining ? 'bg-neutral-400' : 'bg-blue-600 hover:bg-blue-700'
          }`}
        >
          {isTraining ? 'Training...' : 'Train DQN Model'}
        </button>

        <button
          onClick={handleGetActions}
          disabled={isGettingActions || !modelInfo}
          className={`px-4 py-2 rounded-md text-white ${
            isGettingActions || !modelInfo ? 'bg-neutral-400' : 'bg-purple-600 hover:bg-purple-700'
          }`}
        >
          {isGettingActions ? 'Getting Actions...' : 'Get Trading Actions'}
        </button>

        <button
          onClick={handleExecuteTrades}
          disabled={actions.length === 0}
          className={`px-4 py-2 rounded-md text-white ${
            actions.length === 0 ? 'bg-neutral-400' : 'bg-red-600 hover:bg-red-700'
          }`}
        >
          Execute Trades
        </button>
