import { useState, useEffect } from 'react';
import { useSettings } from '../../hooks/useSettings';
import { tradingEngine } from '@/trading/tradingEngine';
import { Card, CardHeader, CardBody, CardFooter, Button, Switch, Label, Alert } from '@/components/ui';

const LiveTradingPanel: React.FC = () => {
  const { 
    isLiveTrading, 
    apiKey, 
    apiSecret, 
    // Removed unused variables
    autoTradingEnabled,
    updateSettings
  } = useSettings();

  const [isInitialized, setIsInitialized] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [status, setStatus] = useState('Idle');
  const [balance, setBalance] = useState<number | null>(null);

  // Initialize trading engine
  useEffect(() => {
    const init = async () => {
      try {
        await tradingEngine.initialize();
        setIsInitialized(true);

        // Get initial balance if in paper mode
        if (!isLiveTrading) {
          const paperEngine = tradingEngine.modeManager.getPaperEngine();
          setBalance(paperEngine.getBalance());
        }
      } catch (err) {
        setError('Failed to initialize trading engine');
        console.error(err);
      }
    };

    init();

    return () => {
      tradingEngine.stopTrading();
    };
  }, [isLiveTrading]);

  // Toggle trading mode
  const toggleTradingMode = async () => {
    setIsLoading(true);
    setError(null);

    try {
      const newMode = isLiveTrading ? 'paper' : 'live';
      const result = await tradingEngine.switchMode(newMode);

      if (result.success) {
        updateSettings({ isLiveTrading: !isLiveTrading });
        setStatus(result.status);

        if (result.balance) {
          setBalance(result.balance);
        }
      } else {
        setError('Failed to switch trading mode');
      }
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Unknown error occurred');
    } finally {
      setIsLoading(false);
    }
  };

  // Toggle auto trading
  const toggleAutoTrading = async () => {
    try {
      if (autoTradingEnabled) {
        await tradingEngine.stopTrading();
      } else {
        await tradingEngine.startTradingLoop();
      }

      updateSettings({ autoTradingEnabled: !autoTradingEnabled });
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to toggle auto trading');
    }
  };

  // Check if trading is possible
  const canTrade = isInitialized && (isLiveTrading ? Boolean(apiKey && apiSecret) : true);

  return (
    <Card className="w-full">
      <CardHeader>
        <h3 className="text-lg font-medium">Trading Controls</h3>
      </CardHeader>
      <CardBody>
        {error && (
          <Alert variant="error" className="mb-4">
            {error}
          </Alert>
        )}

        <div className="space-y-4">
          <div className="flex items-center justify-between">
            <div>
              <Label htmlFor="tradingMode">Live Trading</Label>
              <p className="text-xs text-neutral-500">
                {isLiveTrading 
                  ? 'Trading with real funds on Poloniex' 
                  : 'Paper trading with simulated funds'}
              </p>
            </div>
            <Switch
              id="tradingMode"
              checked={isLiveTrading}
              onCheckedChange={toggleTradingMode}
              disabled={isLoading || !isInitialized || (isLiveTrading && !apiKey)}
            />
          </div>

          {!isLiveTrading && balance !== null && (
            <div>
              <Label>Paper Trading Balance</Label>
              <p className="text-lg font-medium">${balance.toFixed(2)}</p>
            </div>
          )}

          <div className="flex items-center justify-between">
            <div>
              <Label htmlFor="autoTrading">Automated Trading</Label>
              <p className="text-xs text-neutral-500">
                Automatically execute trades based on market analysis
              </p>
            </div>
            <Switch
              id="autoTrading"
              checked={autoTradingEnabled}
              onCheckedChange={toggleAutoTrading}
              disabled={!canTrade || isLoading}
            />
          </div>

          <div>
            <Label>Current Status</Label>
            <p className="text-sm">{status}</p>
            <p className="text-xs text-neutral-500 mt-1">
              {tradingEngine.getCurrentActivity()}
            </p>
          </div>
        </div>
      </CardBody>
      <CardFooter className="flex justify-between">
        <Button 
          variant="outline" 
          onClick={() => tradingEngine.stopTrading()}
          disabled={!autoTradingEnabled || isLoading}
        >
          Stop Trading
        </Button>

        <Button 
          onClick={() => tradingEngine.startTradingLoop()}
          disabled={autoTradingEnabled || !canTrade || isLoading}
        >
          Start Trading
        </Button>
      </CardFooter>
    </Card>
  );
};

export default LiveTradingPanel;

import React, { useState, useEffect } from 'react';
import { default as mlTrading } from '@/ml/mlTrading';
import { usePoloniexData } from '@/hooks/usePoloniexData';
import { useSettings } from '../../hooks/useSettings';

interface MLModelConfig {
  modelType: 'randomforest' | 'gradientboosting' | 'svm' | 'neuralnetwork';
  featureSet: 'basic' | 'technical' | 'advanced' | 'custom';
  predictionTarget: 'price_direction' | 'price_change' | 'volatility';
  timeHorizon: number;
  hyperParameters?: {
    learningRate?: number;
    maxDepth?: number;
    numEstimators?: number;
    epochs?: number;
    batchSize?: number;
  };
}

interface MLModelInfo {
  id: string;
  name: string;
  description: string;
  config: MLModelConfig;
  performance: {
    accuracy: number;
    precision: number;
    recall: number;
    f1Score: number;
    trainingSamples: number;
    validationSamples: number;
  };
  createdAt: number;
  updatedAt: number;
  lastTrainedAt: number;
  status: 'training' | 'ready' | 'error';
  filePath?: string;
}

const MLTradingPanel: React.FC = () => {
  const { marketData: poloniexMarketData, fetchMarketData } = usePoloniexData();
  const { defaultPair, timeframe } = useSettings();

  const [modelConfig, setModelConfig] = useState<MLModelConfig>({
    modelType: 'neuralnetwork',
    featureSet: 'technical',
    predictionTarget: 'price_direction',
    timeHorizon: 12, // 12 candles ahead
    hyperParameters: {
      learningRate: 0.001,
      epochs: 100,
      batchSize: 32
    }
  });

  const [modelInfo, setModelInfo] = useState<MLModelInfo | null>(null);
  const [predictions, setPredictions] = useState<any[]>([]);
  const [isTraining, setIsTraining] = useState(false);
  const [isPredicting, setIsPredicting] = useState(false);
  const [isOptimizing, setIsOptimizing] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [marketData, setMarketData] = useState<any[]>([]);
  const [modelName, setModelName] = useState('My ML Model');

  // Fetch market data
  useEffect(() => {
    const fetchData = async () => {
      try {
        // Get market data for training
        await fetchMarketData(defaultPair);
        setMarketData(poloniexMarketData);
      } catch (err) {
        setError('Failed to fetch market data');
        console.error(err);
      }
    };

    fetchData();
  }, [defaultPair, timeframe, fetchMarketData, poloniexMarketData]);

  // Train model
  const handleTrainModel = async () => {
    if (marketData.length === 0) {
      setError('No market data available for training');
      return;
    }

    setIsTraining(true);
    setError(null);

    try {
      const info = await mlTrading.trainMLModel(marketData, modelConfig, modelName);
      setModelInfo(info);
      setIsTraining(false);
    } catch (err) {
      setError('Failed to train model');
      console.error(err);
      setIsTraining(false);
    }
  };

  // Make predictions
  const handlePredict = async () => {
    if (!modelInfo) {
      setError('No trained model available');
      return;
    }

    if (marketData.length === 0) {
      setError('No market data available for prediction');
      return;
    }

    setIsPredicting(true);
    setError(null);

    try {
      const preds = await mlTrading.predictWithMLModel(modelInfo, marketData);
      setPredictions(preds);
      setIsPredicting(false);
    } catch (err) {
      setError('Failed to make predictions');
      console.error(err);
      setIsPredicting(false);
    }
  };

  // Optimize model
  const handleOptimizeModel = async () => {
    if (marketData.length === 0) {
      setError('No market data available for optimization');
      return;
    }

    setIsOptimizing(true);
    setError(null);

    try {
      const info = await mlTrading.optimizeMLModel(marketData, modelConfig, `${modelName} (Optimized)`);
      setModelInfo(info);
      setIsOptimizing(false);
    } catch (err) {
      setError('Failed to optimize model');
      console.error(err);
      setIsOptimizing(false);
    }
  };

  // Execute trades based on predictions
  const handleExecuteTrades = () => {
    if (predictions.length === 0) {
      setError('No predictions available');
      return;
    }

    // Get the latest prediction
    const latestPrediction = predictions[0];

    if (latestPrediction.prediction === 1 && latestPrediction.confidence > 0.6) {
      // Execute buy strategy
      // TODO: Implement executeStrategy functionality
      console.log('ML Strategy - BUY signal:', {
        type: 'ML_STRATEGY',
        action: 'BUY',
        symbol: defaultPair,
        amount: 0.01, // Small fixed amount
        confidence: latestPrediction.confidence,
        modelId: modelInfo?.id
      });
    } else if (latestPrediction.prediction === 0 && latestPrediction.confidence > 0.6) {
      // Execute sell strategy
      // TODO: Implement executeStrategy functionality
      console.log('ML Strategy - SELL signal:', {
        type: 'ML_STRATEGY',
        action: 'SELL',
        symbol: defaultPair,
        amount: 0.01, // Small fixed amount
        confidence: latestPrediction.confidence,
        modelId: modelInfo?.id
      });
    }
  };

  return (
    <div className="bg-white dark:bg-neutral-800 rounded-lg shadow p-6">
      <h2 className="text-xl font-semibold mb-4 text-neutral-800 dark:text-white">ML Trading</h2>

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}

      <div className="mb-6">
        <h3 className="text-lg font-medium mb-2 text-neutral-700 dark:text-neutral-300">Model Configuration</h3>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <label className="block text-sm font-medium text-neutral-700 dark:text-neutral-300 mb-1">
              Model Name
            </label>
            <input
              type="text"
              value={modelName}
              onChange={(e) => setModelName(e.target.value)}
              className="w-full px-3 py-2 border border-neutral-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 dark:bg-neutral-700 dark:border-neutral-600 dark:text-white"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-neutral-700 dark:text-neutral-300 mb-1">
              Model Type
            </label>
            <select
              value={modelConfig.modelType}
              onChange={(e) => setModelConfig({ ...modelConfig, modelType: e.target.value as any })}
              className="w-full px-3 py-2 border border-neutral-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 dark:bg-neutral-700 dark:border-neutral-600 dark:text-white"
            >
              <option value="randomforest">Random Forest</option>
              <option value="gradientboosting">Gradient Boosting</option>
              <option value="svm">Support Vector Machine</option>
              <option value="neuralnetwork">Neural Network</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-neutral-700 dark:text-neutral-300 mb-1">
              Feature Set
            </label>
            <select
              value={modelConfig.featureSet}
              onChange={(e) => setModelConfig({ ...modelConfig, featureSet: e.target.value as any })}
              className="w-full px-3 py-2 border border-neutral-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 dark:bg-neutral-700 dark:border-neutral-600 dark:text-white"
            >
              <option value="basic">Basic</option>
              <option value="technical">Technical</option>
              <option value="advanced">Advanced</option>
              <option value="custom">Custom</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-neutral-700 dark:text-neutral-300 mb-1">
              Prediction Target
            </label>
            <select
              value={modelConfig.predictionTarget}
              onChange={(e) => setModelConfig({ ...modelConfig, predictionTarget: e.target.value as any })}
              className="w-full px-3 py-2 border border-neutral-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 dark:bg-neutral-700 dark:border-neutral-600 dark:text-white"
            >
              <option value="price_direction">Price Direction</option>
              <option value="price_change">Price Change</option>
              <option value="volatility">Volatility</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-neutral-700 dark:text-neutral-300 mb-1">
              Time Horizon (candles)
            </label>
            <input
              type="number"
              min="1"
              max="100"
              value={modelConfig.timeHorizon}
              onChange={(e) => setModelConfig({ ...modelConfig, timeHorizon: parseInt(e.target.value) })}
              className="w-full px-3 py-2 border border-neutral-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 dark:bg-neutral-700 dark:border-neutral-600 dark:text-white"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-neutral-700 dark:text-neutral-300 mb-1">
              Learning Rate
            </label>
            <input
              type="number"
              min="0.0001"
              max="0.1"
              step="0.0001"
              value={modelConfig.hyperParameters?.learningRate || 0.001}
              onChange={(e) => setModelConfig({ 
                ...modelConfig, 
                hyperParameters: { 
                  ...modelConfig.hyperParameters, 
                  learningRate: parseFloat(e.target.value) 
                } 
              })}
              className="w-full px-3 py-2 border border-neutral-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 dark:bg-neutral-700 dark:border-neutral-600 dark:text-white"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-neutral-700 dark:text-neutral-300 mb-1">
              Epochs
            </label>
            <input
              type="number"
              min="10"
              max="1000"
              step="10"
              value={modelConfig.hyperParameters?.epochs || 100}
              onChange={(e) => setModelConfig({ 
                ...modelConfig, 
                hyperParameters: { 
                  ...modelConfig.hyperParameters, 
                  epochs: parseInt(e.target.value) 
                } 
              })}
              className="w-full px-3 py-2 border border-neutral-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 dark:bg-neutral-700 dark:border-neutral-600 dark:text-white"
            />
          </div>
        </div>
      </div>

      <div className="flex flex-wrap gap-4 mb-6">
        <button
          onClick={handleTrainModel}
          disabled={isTraining || marketData.length === 0}
          className={`px-4 py-2 rounded-md text-white ${
            isTraining ? 'bg-neutral-400' : 'bg-blue-600 hover:bg-blue-700'
          }`}
        >
          {isTraining ? 'Training...' : 'Train Model'}
        </button>

        <button
          onClick={handleOptimizeModel}
          disabled={isOptimizing || marketData.length === 0}
          className={`px-4 py-2 rounded-md text-white ${
            isOptimizing ? 'bg-neutral-400' : 'bg-green-600 hover:bg-green-700'
          }`}
        >
          {isOptimizing ? 'Optimizing...' : 'Optimize Model'}
        </button>

        <button
          onClick={handlePredict}
          disabled={isPredicting || !modelInfo}
          className={`px-4 py-2 rounded-md text-white ${
            isPredicting || !modelInfo ? 'bg-neutral-400' : 'bg-purple-600 hover:bg-purple-700'
          }`}
        >
          {isPredicting ? 'Predicting...' : 'Make Predictions'}
        </button>

        <button
          onClick={handleExecuteTrades}
          disabled={predictions.length === 0}
          className={`px-4 py-2 rounded-md text-white ${
            predictions.length === 0 ? 'bg-neutral-400' : 'bg-red-600 hover:bg-red-700'
          }`}
        >
          Execute Trades
        </button>
      </div>

      {modelInfo && (
        <div className="mb-6">
          <h3 className="text-lg font-medium mb-2 text-neutral-700 dark:text-neutral-300">Model Information</h3>

          <div className="bg-neutral-100 dark:bg-neutral-700 p-4 rounded-md">
            <div className="grid grid-cols-2 gap-4">
              <div>
                <p className="text-sm font-medium text-neutral-500 dark:text-neutral-400">Name</p>
                <p className="text-neutral-800 dark:text-white">{modelInfo.name}</p>
              </div>

              <div>
                <p className="text-sm font-medium text-neutral-500 dark:text-neutral-400">Type</p>
                <p className="text-neutral-800 dark:text-white">{modelInfo.config.modelType}</p>
              </div>

              <div>
                <p className="text-sm font-medium text-neutral-500 dark:text-neutral-400">Accuracy</p>
                <p className="text-neutral-800 dark:text-white">{(modelInfo.performance.accuracy * 100).toFixed(2)}%</p>
              </div>

              <div>
                <p className="text-sm font-medium text-neutral-500 dark:text-neutral-400">F1 Score</p>
                <p className="text-neutral-800 dark:text-white">{(modelInfo.performance.f1Score * 100).toFixed(2)}%</p>
              </div>

              <div>
                <p className="text-sm font-medium text-neutral-500 dark:text-neutral-400">Training Samples</p>
                <p className="text-neutral-800 dark:text-white">{modelInfo.performance.trainingSamples}</p>
              </div>

              <div>
                <p className="text-sm font-medium text-neutral-500 dark:text-neutral-400">Last Trained</p>
                <p className="text-neutral-800 dark:text-white">
                  {new Date(modelInfo.lastTrainedAt).toLocaleString()}
                </p>
              </div>
            </div>
          </div>
        </div>
      )}

      {predictions.length > 0 && (
        <div>
          <h3 className="text-lg font-medium mb-2 text-neutral-700 dark:text-neutral-300">Latest Predictions</h3>

          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-neutral-200 dark:divide-neutral-700">
              <thead className="bg-neutral-50 dark:bg-neutral-800">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-neutral-500 dark:text-neutral-300 uppercase tracking-wider">
                    Time
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-neutral-500 dark:text-neutral-300 uppercase tracking-wider">
                    Symbol
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-neutral-500 dark:text-neutral-300 uppercase tracking-wider">
                    Prediction
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-neutral-500 dark:text-neutral-300 uppercase tracking-wider">
                    Confidence
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-neutral-200 dark:bg-neutral-900 dark:divide-neutral-700">
                {predictions.slice(0, 10).map((pred, index) => (
                  <tr key={index}>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-neutral-500 dark:text-neutral-400">
                      {new Date(pred.timestamp).toLocaleString()}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-neutral-500 dark:text-neutral-400">
                      {pred.symbol}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <span
                        className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                          pred.prediction === 1
                            ? 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200'
                            : 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200'
                        }`}
                      >
                        {pred.prediction === 1 ? 'UP' : 'DOWN'}
                      </span>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-neutral-500 dark:text-neutral-400">
                      {(pred.confidence * 100).toFixed(2)}%
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}
    </div>
  );
};

export { MLTradingPanel };

import { useState, useEffect } from 'react';
import { useSettings } from '../../hooks/useSettings';
import { Strategy, MarketData } from '@/types';
import { executeStrategy } from '@/utils/strategyExecutors';
import { poloniexApi } from '@/services/poloniexAPI';
import { Card } from '@/components/ui/Card';
import { Button } from '@/components/ui/Button';
import { Select, SelectOption } from '@/components/ui/Select';
import { Input } from '@/components/ui/Input';
import { Label } from '@/components/ui/Label';
import { getExtensionData, setExtensionData, isChromeExtension } from '@/utils/chromeExtension';

// Strategy types
export type StrategyType = 'MovingAverageCrossover' | 'RSI' | 'MACD' | 'BollingerBands' | 'Custom';

// Strategy parameters interfaces
interface BaseParameters {
  pair: string;
  timeframe: string;
}

interface MovingAverageCrossoverParameters extends BaseParameters {
  fastPeriod: number;
  slowPeriod: number;
}

interface RSIParameters extends BaseParameters {
  period: number;
  overbought: number;
  oversold: number;
}

interface MACDParameters extends BaseParameters {
  fastPeriod: number;
  slowPeriod: number;
  signalPeriod: number;
}

interface BollingerBandsParameters extends BaseParameters {
  period: number;
  stdDev: number;
}

interface CustomParameters extends BaseParameters {
  // Add any custom parameters here
  lookbackPeriod: number;
  breakoutThreshold: number;
}

// Union type for all parameter types
export type StrategyParameters = 
  | MovingAverageCrossoverParameters 
  | RSIParameters 
  | MACDParameters 
  | BollingerBandsParameters
  | CustomParameters;

// Test result interface
interface TestResult {
  signal: 'BUY' | 'SELL' | null;
  reason: string;
  confidence: number;
}

const StrategyBuilder: React.FC = () => {
  const { defaultPair, timeframe } = useSettings();

  const [strategies, setStrategies] = useState<Strategy[]>([]);
  const [selectedStrategy, setSelectedStrategy] = useState<Strategy | null>(null);
  const [newStrategy, setNewStrategy] = useState<Partial<Strategy>>({
    id: '',
    name: '',
    type: 'MovingAverageCrossover',
    parameters: {
      pair: defaultPair,
      timeframe: timeframe,
      fastPeriod: 9,
      slowPeriod: 21
    } as StrategyParameters
  });
  const [marketData, setMarketData] = useState<MarketData[]>([]);
  const [testResult, setTestResult] = useState<TestResult | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // Load saved strategies from storage
  useEffect(() => {
    const loadStrategies = async () => {
      try {
        if (isChromeExtension()) {
          // Load from Chrome storage if in extension
          getExtensionData('trading_strategies')
            .then((data: any) => {
              if (data) {
                setStrategies(data);
              }
            })
            .catch(err => console.error('Failed to load strategies from extension:', err));
        } else {
          // Load from localStorage if in browser
          const savedStrategies = localStorage.getItem('trading_strategies');
          if (savedStrategies) {
            setStrategies(JSON.parse(savedStrategies));
          }
        }
      } catch (err) {
        console.error('Failed to load saved strategies:', err);
      }
    };

    loadStrategies();
  }, []);

  // Save strategies to storage when they change
  useEffect(() => {
    if (strategies.length > 0) {
      try {
        if (isChromeExtension()) {
          // Save to Chrome storage if in extension
          setExtensionData('trading_strategies', strategies)
            .catch(err => console.error('Failed to save strategies to extension:', err));
        } else {
          // Save to localStorage if in browser
          localStorage.setItem('trading_strategies', JSON.stringify(strategies));
        }
      } catch (err) {
        console.error('Failed to save strategies:', err);
      }
    }
  }, [strategies]);

  // Load market data for testing
  const loadMarketData = async () => {
    setIsLoading(true);
    setError(null);

    try {
      const pair = (newStrategy.parameters as BaseParameters)?.pair || defaultPair;
      const data = await poloniexApi.getMarketData(pair);
      setMarketData(data);
    } catch (err) {
      setError('Failed to load market data');
      console.error(err);
    } finally {
      setIsLoading(false);
    }
  };

  // Test strategy with current market data
  const testStrategy = () => {
    if (!newStrategy.type || !marketData.length) {
      setError('Strategy or market data not available');
      return;
    }

    try {
      const result = executeStrategy(newStrategy as Strategy, marketData);
      setTestResult(result);
    } catch (err) {
      setError('Failed to execute strategy');
      console.error(err);
    }
  };

  // Save new strategy
  const saveStrategy = () => {
    if (!newStrategy.id || !newStrategy.name || !newStrategy.type) {
      setError('Strategy ID, name, and type are required');
      return;
    }

    // Check if ID already exists
    if (strategies.some(s => s.id === newStrategy.id)) {
      setError('Strategy ID already exists');
      return;
    }

    const strategy = {
      ...newStrategy,
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString()
    } as Strategy;

    setStrategies([...strategies, strategy]);
    resetNewStrategy();
    setError(null);
  };

  // Delete strategy
  const deleteStrategy = (id: string) => {
    setStrategies(strategies.filter(s => s.id !== id));
  };

  // Select strategy for editing
  const selectStrategy = (strategy: Strategy) => {
    setSelectedStrategy(strategy);
    setNewStrategy(strategy);
  };

  // Reset new strategy form
  const resetNewStrategy = () => {
    setNewStrategy({
      id: '',
      name: '',
      type: 'MovingAverageCrossover',
      parameters: {
        pair: defaultPair,
        timeframe: timeframe,
        fastPeriod: 9,
        slowPeriod: 21
      } as StrategyParameters
    });
    setSelectedStrategy(null);
  };

  // Update strategy parameters based on type
  const updateStrategyType = (type: StrategyType) => {
    let parameters: StrategyParameters;

    switch (type) {
      case 'MovingAverageCrossover':
        parameters = { 
          pair: defaultPair, 
          timeframe, 
          fastPeriod: 9, 
          slowPeriod: 21 
        } as MovingAverageCrossoverParameters;
        break;
      case 'RSI':
        parameters = { 
          pair: defaultPair, 
          timeframe, 
          period: 14, 
          overbought: 70, 
          oversold: 30 
        } as RSIParameters;
        break;
      case 'MACD':
        parameters = { 
          pair: defaultPair, 
          timeframe, 
          fastPeriod: 12, 
          slowPeriod: 26, 
          signalPeriod: 9 
        } as MACDParameters;
        break;
      case 'BollingerBands':
        parameters = { 
          pair: defaultPair, 
          timeframe, 
          period: 20, 
          stdDev: 2 
        } as BollingerBandsParameters;
        break;
      default:
        parameters = { 
          pair: defaultPair, 
          timeframe,
          lookbackPeriod: 20,
          breakoutThreshold: 2
        } as CustomParameters;
    }

    setNewStrategy({
      ...newStrategy,
      type,
      parameters
    });
  };

  // Render parameter inputs based on strategy type
  const renderParameterInputs = () => {
    const type = newStrategy.type as StrategyType;
    const parameters = newStrategy.parameters || {};

    switch (type) {
      case 'MovingAverageCrossover': {
        const params = parameters as MovingAverageCrossoverParameters;
        return (
          <>
            <div className="mb-4">
              <Label htmlFor="fastPeriod">Fast Period</Label>
              <Input
                id="fastPeriod"
                type="number"
                value={params.fastPeriod || 9}
                onChange={(e) => setNewStrategy({
                  ...newStrategy,
                  parameters: { 
                    ...parameters, 
                    fastPeriod: parseInt(e.target.value) 
                  } as MovingAverageCrossoverParameters
                })}
                min="2"
                max="50"
              />
            </div>
            <div className="mb-4">
              <Label htmlFor="slowPeriod">Slow Period</Label>
              <Input
                id="slowPeriod"
                type="number"
                value={params.slowPeriod || 21}
                onChange={(e) => setNewStrategy({
                  ...newStrategy,
                  parameters: { 
                    ...parameters, 
                    slowPeriod: parseInt(e.target.value) 
                  } as MovingAverageCrossoverParameters
                })}
                min="5"
                max="200"
              />
            </div>
          </>
        );
      }

      case 'RSI': {
        const params = parameters as RSIParameters;
        return (
          <>
            <div className="mb-4">
              <Label htmlFor="period">RSI Period</Label>
              <Input
                id="period"
                type="number"
                value={params.period || 14}
                onChange={(e) => setNewStrategy({
                  ...newStrategy,
                  parameters: { 
                    ...parameters, 
                    period: parseInt(e.target.value) 
                  } as RSIParameters
                })}
                min="2"
                max="50"
              />
            </div>
            <div className="mb-4">
              <Label htmlFor="overbought">Overbought Level</Label>
              <Input
                id="overbought"
                type="number"
                value={params.overbought || 70}
                onChange={(e) => setNewStrategy({
                  ...newStrategy,
                  parameters: { 
                    ...parameters, 
                    overbought: parseInt(e.target.value) 
                  } as RSIParameters
                })}
                min="50"
                max="90"
              />
            </div>
            <div className="mb-4">
              <Label htmlFor="oversold">Oversold Level</Label>
              <Input
                id="oversold"
                type="number"
                value={params.oversold || 30}
                onChange={(e) => setNewStrategy({
                  ...newStrategy,
                  parameters: { 
                    ...parameters, 
                    oversold: parseInt(e.target.value) 
                  } as RSIParameters
                })}
                min="10"
                max="50"
              />
            </div>
          </>
        );
      }

      case 'MACD': {
        const params = parameters as MACDParameters;
        return (
          <>
            <div className="mb-4">
              <Label htmlFor="fastPeriod">Fast Period</Label>
              <Input
                id="fastPeriod"
                type="number"
                value={params.fastPeriod || 12}
                onChange={(e) => setNewStrategy({
                  ...newStrategy,
                  parameters: { 
                    ...parameters, 
                    fastPeriod: parseInt(e.target.value) 
                  } as MACDParameters
                })}
                min="2"
                max="50"
              />
            </div>
            <div className="mb-4">
              <Label htmlFor="slowPeriod">Slow Period</Label>
              <Input
                id="slowPeriod"
                type="number"
                value={params.slowPeriod || 26}
                onChange={(e) => setNewStrategy({
                  ...newStrategy,
                  parameters: { 
                    ...parameters, 
                    slowPeriod: parseInt(e.target.value) 
                  } as MACDParameters
                })}
                min="5"
                max="100"
              />
            </div>
            <div className="mb-4">
              <Label htmlFor="signalPeriod">Signal Period</Label>
              <Input
                id="signalPeriod"
                type="number"
                value={params.signalPeriod || 9}
                onChange={(e) => setNewStrategy({
                  ...newStrategy,
                  parameters: { 
                    ...parameters, 
                    signalPeriod: parseInt(e.target.value) 
                  } as MACDParameters
                })}
                min="2"
                max="50"
              />
            </div>
          </>
        );
      }

      case 'BollingerBands': {
        const params = parameters as BollingerBandsParameters;
        return (
          <>
            <div className="mb-4">
              <Label htmlFor="period">Period</Label>
              <Input
                id="period"
                type="number"
                value={params.period || 20}
                onChange={(e) => setNewStrategy({
                  ...newStrategy,
                  parameters: { 
                    ...parameters, 
                    period: parseInt(e.target.value) 
                  } as BollingerBandsParameters
                })}
                min="5"
                max="100"
              />
            </div>
            <div className="mb-4">
              <Label htmlFor="stdDev">Standard Deviations</Label>
              <Input
                id="stdDev"
                type="number"
                value={params.stdDev || 2}
                onChange={(e) => setNewStrategy({
                  ...newStrategy,
                  parameters: { 
                    ...parameters, 
                    stdDev: parseFloat(e.target.value) 
                  } as BollingerBandsParameters
                })}
                min="0.5"
                max="4"
                step="0.1"
              />
            </div>
          </>
        );
      }

      case 'Custom': {
        const params = parameters as CustomParameters;
        return (
          <>
            <div className="mb-4">
              <Label htmlFor="lookbackPeriod">Lookback Period</Label>
              <Input
                id="lookbackPeriod"
                type="number"
                value={params.lookbackPeriod || 20}
                onChange={(e) => setNewStrategy({
                  ...newStrategy,
                  parameters: { 
                    ...parameters, 
                    lookbackPeriod: parseInt(e.target.value) 
                  } as CustomParameters
                })}
                min="5"
                max="100"
              />
            </div>
            <div className="mb-4">
              <Label htmlFor="breakoutThreshold">Breakout Threshold</Label>
              <Input
                id="breakoutThreshold"
                type="number"
                value={params.breakoutThreshold || 2}
                onChange={(e) => setNewStrategy({
                  ...newStrategy,
                  parameters: { 
                    ...parameters, 
                    breakoutThreshold: parseFloat(e.target.value) 
                  } as CustomParameters
                })}
                min="0.5"
                max="5"
                step="0.1"
              />
            </div>
          </>
        );
      }

      default:
        return null;
    }
  };

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
      <Card className="w-full">
        <div className="p-4 border-b">
          <h3 className="text-lg font-medium">Strategy Builder</h3>
        </div>
        <div className="p-4">
          {error && (
            <div className="p-4 mb-4 text-sm text-red-700 bg-red-100 rounded-lg">
              {error}
            </div>
          )}

          <div className="space-y-4">
            <div>
              <Label htmlFor="strategyId">Strategy ID</Label>
              <Input
                id="strategyId"
                value={newStrategy.id || ''}
                onChange={(e) => setNewStrategy({ ...newStrategy, id: e.target.value })}
                placeholder="unique-strategy-id"
                disabled={!!selectedStrategy}
              />
            </div>

            <div>
              <Label htmlFor="strategyName">Strategy Name</Label>
              <Input
                id="strategyName"
                value={newStrategy.name || ''}
                onChange={(e) => setNewStrategy({ ...newStrategy, name: e.target.value })}
                placeholder="My Trading Strategy"
              />
            </div>

            <div>
              <Label htmlFor="strategyType">Strategy Type</Label>
              <Select
                id="strategyType"
                value={newStrategy.type || 'MovingAverageCrossover'}
                onChange={(e) => updateStrategyType(e.target.value as StrategyType)}
              >
                <SelectOption value="MovingAverageCrossover">Moving Average Crossover</SelectOption>
                <SelectOption value="RSI">Relative Strength Index (RSI)</SelectOption>
                <SelectOption value="MACD">MACD</SelectOption>
                <SelectOption value="BollingerBands">Bollinger Bands</SelectOption>
                <SelectOption value="Custom">Custom</SelectOption>
              </Select>
            </div>

            <div>
              <Label htmlFor="pair">Trading Pair</Label>
              <Input
                id="pair"
                value={(newStrategy.parameters as BaseParameters)?.pair || defaultPair}
                onChange={(e) => setNewStrategy({
                  ...newStrategy,
                  parameters: { 
                    ...newStrategy.parameters, 
                    pair: e.target.value 
                  } as StrategyParameters
                })}
                placeholder="BTC-USDT"
              />
            </div>

            <div>
              <Label htmlFor="timeframe">Timeframe</Label>
              <Select
                id="timeframe"
                value={(newStrategy.parameters as BaseParameters)?.timeframe || timeframe}
                onChange={(e) => setNewStrategy({
                  ...newStrategy,
                  parameters: { 
                    ...newStrategy.parameters, 
                    timeframe: e.target.value 
                  } as StrategyParameters
                })}
              >
                <SelectOption value="1m">1 Minute</SelectOption>
                <SelectOption value="5m">5 Minutes</SelectOption>
                <SelectOption value="15m">15 Minutes</SelectOption>
                <SelectOption value="1h">1 Hour</SelectOption>
                <SelectOption value="4h">4 Hours</SelectOption>
                <SelectOption value="1d">1 Day</SelectOption>
              </Select>
            </div>

            {renderParameterInputs()}

            <div className="flex space-x-2 pt-4">
              <Button
                type="button"
                onClick={resetNewStrategy}
                variant="outline"
              >
                Reset
              </Button>
              <Button
                type="button"
                onClick={saveStrategy}
                disabled={!newStrategy.id || !newStrategy.name}
              >
                {selectedStrategy ? 'Update Strategy' : 'Save Strategy'}
              </Button>
            </div>
          </div>
        </div>
      </Card>

      <Card className="w-full">
        <div className="p-4 border-b">
          <h3 className="text-lg font-medium">Test & Manage Strategies</h3>
        </div>
        <div className="p-4">
          <div className="mb-6">
            <h4 className="font-medium mb-2">Test Current Strategy</h4>
            <div className="flex space-x-2 mb-4">
              <Button
                type="button"
                onClick={loadMarketData}
                disabled={isLoading}
                variant="outline"
              >
                {isLoading ? 'Loading...' : 'Load Market Data'}
              </Button>
              <Button
                type="button"
                onClick={testStrategy}
                disabled={!marketData.length || isLoading}
              >
                Test Strategy
              </Button>
            </div>

            {testResult && (
              <div className={`p-4 rounded-lg ${
                testResult.signal === 'BUY' 
                  ? 'bg-green-100 text-green-800' 
                  : testResult.signal === 'SELL' 
                    ? 'bg-red-100 text-red-800' 
                    : 'bg-neutral-100 text-neutral-800'
              }`}>
                <div className="font-medium">
                  Signal: {testResult.signal || 'NEUTRAL'}
                </div>
                <div className="text-sm mt-1">
                  {testResult.reason}
                </div>
                <div className="text-sm mt-2">
                  Confidence: {testResult.confidence}%
                </div>
              </div>
            )}
          </div>

          <div>
            <h4 className="font-medium mb-2">Saved Strategies</h4>
            <div className="space-y-2">
              {strategies.map(strategy => (
                <div 
                  key={strategy.id}
                  className={`p-3 border rounded-md cursor-pointer ${
                    selectedStrategy?.id === strategy.id ? 'border-blue-500 bg-blue-50' : 'border-neutral-200'
                  }`}
                  onClick={() => selectStrategy(strategy)}
                >
                  <div className="flex justify-between items-center">
                    <div>
                      <div className="font-medium">{strategy.name}</div>
                      <div className="text-xs text-neutral-500">{strategy.type}</div>
                    </div>
                    <button
                      className="text-red-500 hover:text-red-700"
                      onClick={(e) => {
                        e.stopPropagation();
                        deleteStrategy(strategy.id);
                      }}
                    >
                      Delete
                    </button>
                  </div>
                </div>
              ))}

              {strategies.length === 0 && (
                <div className="text-center py-4 text-neutral-500">
                  No saved strategies yet
                </div>
              )}
            </div>
          </div>
        </div>
      </Card>
    </div>
  );
};

export default StrategyBuilder;

import React, { useState, useEffect, useCallback } from 'react';
import { Strategy, MarketData } from '@/types';
import { 
  backtestStrategy, 
  optimizeStrategy, 
  monteCarloSimulation, 
  walkForwardAnalysis,
  BacktestResult,
  StrategyTestOptions,
  StrategyOptimizationResult
} from '@/utils/strategyTester';
import { Line } from 'react-chartjs-2';
import { 
  Chart as ChartJS, 
  CategoryScale, 
  LinearScale, 
  PointElement, 
  LineElement, 
  Title, 
  Tooltip, 
  Legend,
  Filler
} from 'chart.js';

// Register Chart.js components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler
);

interface StrategyTesterProps {
  strategy: Strategy;
  marketData: MarketData[];
  initialOptions?: Partial<StrategyTestOptions>;
  parameterRanges?: Record<string, [number, number, number]>;
  onResultsChange?: (results: BacktestResult) => void;
}

const StrategyTester: React.FC<StrategyTesterProps> = ({
  strategy,
  marketData,
  initialOptions = {},
  parameterRanges = {},
  onResultsChange
}) => {
  // State for test options
  const [testOptions, setTestOptions] = useState<StrategyTestOptions>({
    initialBalance: 10000,
    feePercent: 0.1,
    slippagePercent: 0.05,
    positionSizePercent: 10,
    useStopLoss: false,
    stopLossPercent: 5,
    useTakeProfit: false,
    takeProfitPercent: 10,
    useTrailingStop: false,
    trailingStopPercent: 2,
    confidenceThreshold: 0.5,
    allowSimultaneousPositions: false,
    maxOpenPositions: 1,
    reinvestProfits: true,
    ...initialOptions
  });

  // State for test results
  const [backtestResult, setBacktestResult] = useState<BacktestResult | null>(null);
  const [optimizationResult, setOptimizationResult] = useState<StrategyOptimizationResult | null>(null);
  const [monteCarloResult, setMonteCarloResult] = useState<any | null>(null);
  const [walkForwardResult, setWalkForwardResult] = useState<any | null>(null);

  // State for active tab
  const [activeTab, setActiveTab] = useState<'backtest' | 'optimize' | 'monteCarlo' | 'walkForward'>('backtest');

  // State for loading indicators
  const [isLoading, setIsLoading] = useState<Record<string, boolean>>({
    backtest: false,
    optimize: false,
    monteCarlo: false,
    walkForward: false
  });

  // Run backtest when options change
  useEffect(() => {
    runBacktest();
  }, [runBacktest]);

  // Notify parent component when results change
  useEffect(() => {
    if (backtestResult && onResultsChange) {
      onResultsChange(backtestResult);
    }
  }, [backtestResult, onResultsChange]);

  // Run backtest
  const runBacktest = useCallback(async () => {
    if (!marketData.length) return;

    setIsLoading(prev => ({ ...prev, backtest: true }));

    try {
      // Use setTimeout to allow UI to update before running the backtest
      setTimeout(() => {
        const result = backtestStrategy(strategy, marketData, testOptions);
        setBacktestResult(result);
        setIsLoading(prev => ({ ...prev, backtest: false }));
      }, 50);
    } catch (error) {
      console.error('Backtest error:', error);
      setIsLoading(prev => ({ ...prev, backtest: false }));
    }
  }, [strategy, marketData, testOptions]);

  // Run optimization
  const runOptimization = async (metric: string = 'netProfit') => {
    if (!marketData.length || Object.keys(parameterRanges).length === 0) return;

    setIsLoading(prev => ({ ...prev, optimize: true }));

    try {
      // Use setTimeout to allow UI to update before running the optimization
      setTimeout(() => {
        const result = optimizeStrategy(
          strategy.type,
          marketData,
          parameterRanges,
          metric as any,
          strategy.parameters,
          testOptions
        );
        setOptimizationResult(result);
        setIsLoading(prev => ({ ...prev, optimize: false }));
      }, 50);
    } catch (error) {
      console.error('Optimization error:', error);
      setIsLoading(prev => ({ ...prev, optimize: false }));
    }
  };

  // Run Monte Carlo simulation
  const runMonteCarloSimulation = async (numSimulations: number = 1000) => {
    if (!backtestResult) return;

    setIsLoading(prev => ({ ...prev, monteCarlo: true }));

    try {
      // Use setTimeout to allow UI to update before running the simulation
      setTimeout(() => {
        const result = monteCarloSimulation(backtestResult, numSimulations);
        setMonteCarloResult(result);
        setIsLoading(prev => ({ ...prev, monteCarlo: false }));
      }, 50);
    } catch (error) {
      console.error('Monte Carlo simulation error:', error);
      setIsLoading(prev => ({ ...prev, monteCarlo: false }));
    }
  };

  // Run walk-forward analysis
  const runWalkForwardAnalysis = async (
    inSamplePercent: number = 70,
    numFolds: number = 5
  ) => {
    if (!marketData.length) return;

    setIsLoading(prev => ({ ...prev, walkForward: true }));

    try {
      // Use setTimeout to allow UI to update before running the analysis
      setTimeout(() => {
        const result = walkForwardAnalysis(
          strategy,
          marketData,
          {
            inSamplePercent,
            numFolds,
            optimizationMetric: 'netProfit',
            parameterRanges,
            testOptions
          }
        );
        setWalkForwardResult(result);
        setIsLoading(prev => ({ ...prev, walkForward: false }));
      }, 50);
    } catch (error) {
      console.error('Walk-forward analysis error:', error);
      setIsLoading(prev => ({ ...prev, walkForward: false }));
    }
  };

  // Prepare equity curve chart data
  const prepareEquityCurveData = () => {
    if (!backtestResult) return null;

    const labels = backtestResult.equityCurve.map(point => 
      new Date(point.date).toLocaleDateString()
    );

    const equityData = backtestResult.equityCurve.map(point => point.equity);
    const drawdownData = backtestResult.equityCurve.map(point => point.drawdownPercent);

    return {
      labels,
      datasets: [
        {
          label: 'Equity',
          data: equityData,
          borderColor: 'rgb(75, 192, 192)',
          backgroundColor: 'rgba(75, 192, 192, 0.1)',
          tension: 0.1,
          fill: false
        },
        {
          label: 'Drawdown %',
          data: drawdownData,
          borderColor: 'rgb(255, 99, 132)',
          backgroundColor: 'rgba(255, 99, 132, 0.1)',
          tension: 0.1,
          fill: false,
          yAxisID: 'y1'
        }
      ]
    };
  };

  // Prepare optimization heatmap data
  // const prepareHeatmapData = () => {
  //   if (!optimizationResult) return null;
  //   
  //   // Implementation depends on the charting library used
  //   // This is a placeholder for the heatmap data preparation
  //   return optimizationResult.parameterHeatmap;
  // };

  // Prepare Monte Carlo simulation chart data
  const prepareMonteCarloData = () => {
    if (!monteCarloResult) return null;

    const equityDistribution = monteCarloResult.distributions.finalBalance;
    const sortedEquity = [...equityDistribution].sort((a, b) => a - b);

    // Create histogram data
    const min = Math.floor(sortedEquity[0]);
    const max = Math.ceil(sortedEquity[sortedEquity.length - 1]);
    const binSize = Math.ceil((max - min) / 20); // 20 bins

    const bins: number[] = [];
    const counts: number[] = [];

    for (let i = min; i <= max; i += binSize) {
      bins.push(i);
      counts.push(0);
    }

    for (const value of equityDistribution) {
      const binIndex = Math.floor((value - min) / binSize);
      if (binIndex >= 0 && binIndex < counts.length) {
        counts[binIndex]++;
      }
    }

    return {
      labels: bins.map(bin => bin.toFixed(0)),
      datasets: [
        {
          label: 'Final Equity Distribution',
          data: counts,
          backgroundColor: 'rgba(75, 192, 192, 0.6)',
          borderColor: 'rgb(75, 192, 192)',
          borderWidth: 1
        }
      ]
    };
  };

  // Render backtest results
  const renderBacktestResults = () => {
    if (!backtestResult) return null;

    const {
      initialBalance,
      finalBalance,
      totalTrades,
      winningTrades,
      losingTrades,
      winRate,
      profitFactor,
      maxDrawdown,
      maxDrawdownPercent,
      sharpeRatio,
      metrics
    } = backtestResult;

    const equityCurveData = prepareEquityCurveData();

    return (
      <div className="space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
          <div className="bg-white p-4 rounded-lg shadow">
            <h3 className="text-sm font-medium text-neutral-500">Profit/Loss</h3>
            <p className={`text-2xl font-bold ${finalBalance > initialBalance ? 'text-green-600' : 'text-red-600'}`}>
              ${(finalBalance - initialBalance).toFixed(2)}
            </p>
            <p className="text-sm text-neutral-500">
              {((finalBalance - initialBalance) / initialBalance * 100).toFixed(2)}%
            </p>
          </div>

          <div className="bg-white p-4 rounded-lg shadow">
            <h3 className="text-sm font-medium text-neutral-500">Win Rate</h3>
            <p className="text-2xl font-bold">{winRate.toFixed(2)}%</p>
            <p className="text-sm text-neutral-500">
              {winningTrades} / {totalTrades} trades
            </p>
          </div>

          <div className="bg-white p-4 rounded-lg shadow">
            <h3 className="text-sm font-medium text-neutral-500">Profit Factor</h3>
            <p className="text-2xl font-bold">{profitFactor.toFixed(2)}</p>
            <p className="text-sm text-neutral-500">
              Wins / Losses ratio
            </p>
          </div>

          <div className="bg-white p-4 rounded-lg shadow">
            <h3 className="text-sm font-medium text-neutral-500">Max Drawdown</h3>
            <p className="text-2xl font-bold text-red-600">{maxDrawdownPercent.toFixed(2)}%</p>
            <p className="text-sm text-neutral-500">
              ${maxDrawdown.toFixed(2)}
            </p>
          </div>
        </div>

        <div className="bg-white p-4 rounded-lg shadow">
          <h3 className="text-lg font-medium mb-4">Equity Curve</h3>
          {equityCurveData && (
            <div className="h-64">
              <Line 
                data={equityCurveData} 
                options={{
                  responsive: true,
                  maintainAspectRatio: false,
                  scales: {
                    x: {
                      ticks: {
                        maxTicksLimit: 10
                      }
                    },
                    y: {
                      beginAtZero: false
                    },
                    y1: {
                      position: 'right',
                      beginAtZero: true,
                      max: 100,
                      reverse: true,
                      grid: {
                        drawOnChartArea: false
                      }
                    }
                  }
                }}
              />
            </div>
          )}
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div className="bg-white p-4 rounded-lg shadow">
            <h3 className="text-lg font-medium mb-4">Performance Metrics</h3>
            <table className="w-full">
              <tbody>
                <tr>
                  <td className="py-1 text-neutral-600">Sharpe Ratio</td>
                  <td className="py-1 text-right font-medium">{sharpeRatio.toFixed(2)}</td>
                </tr>
                <tr>
                  <td className="py-1 text-neutral-600">Annualized Return</td>
                  <td className="py-1 text-right font-medium">{(metrics.annualizedReturn * 100).toFixed(2)}%</td>
                </tr>
                <tr>
                  <td className="py-1 text-neutral-600">Average Trade</td>
                  <td className="py-1 text-right font-medium">${metrics.averageTrade.toFixed(2)}</td>
                </tr>
                <tr>
                  <td className="py-1 text-neutral-600">Average Win</td>
                  <td className="py-1 text-right font-medium">${metrics.averageProfit.toFixed(2)}</td>
                </tr>
                <tr>
                  <td className="py-1 text-neutral-600">Average Loss</td>
                  <td className="py-1 text-right font-medium">${metrics.averageLoss.toFixed(2)}</td>
                </tr>
              </tbody>
            </table>
          </div>

          <div className="bg-white p-4 rounded-lg shadow">
            <h3 className="text-lg font-medium mb-4">Strategy Parameters</h3>
            <table className="w-full">
              <tbody>
                {Object.entries(strategy.parameters).map(([key, value]) => (
                  <tr key={key}>
                    <td className="py-1 text-neutral-600">{key}</td>
                    <td className="py-1 text-right font-medium">{value}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>

        <div className="bg-white p-4 rounded-lg shadow">
          <h3 className="text-lg font-medium mb-4">Recent Trades</h3>
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-neutral-200">
              <thead className="bg-neutral-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-neutral-500 uppercase tracking-wider">Date</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-neutral-500 uppercase tracking-wider">Type</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-neutral-500 uppercase tracking-wider">Entry Price</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-neutral-500 uppercase tracking-wider">Exit Price</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-neutral-500 uppercase tracking-wider">Profit</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-neutral-500 uppercase tracking-wider">Reason</th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-neutral-200">
                {backtestResult.trades.slice(-10).reverse().map((trade, index) => (
                  <tr key={index}>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-neutral-500">
                      {new Date(trade.entryDate).toLocaleDateString()}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm">
                      <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                        trade.type === 'BUY' ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
                      }`}>
                        {trade.type}
                      </span>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-neutral-500">
                      ${trade.entryPrice.toFixed(2)}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-neutral-500">
                      ${trade.exitPrice ? trade.exitPrice.toFixed(2) : '-'}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm">
                      <span className={trade.profit > 0 ? 'text-green-600' : 'text-red-600'}>
