export default StrategyTester;

// Enhanced environment management for proper mock mode detection
interface EnvironmentConfig {
  apiKey: string | null;
  apiSecret: string | null;
  forceMockMode: boolean;
  isProduction: boolean;
  wsUrl: string;
  apiUrl: string;
  liveTradingEnabled: boolean;
}

export class EnvironmentManager {
  private static instance: EnvironmentManager;
  private config: EnvironmentConfig;

  private constructor() {
    this.config = this.loadConfiguration();
    this.validateConfiguration();
  }

  static getInstance(): EnvironmentManager {
    if (!EnvironmentManager.instance) {
      EnvironmentManager.instance = new EnvironmentManager();
    }
    return EnvironmentManager.instance;
  }

  private loadConfiguration(): EnvironmentConfig {
    // Check for environment variables
    const apiKey = import.meta.env.VITE_POLONIEX_API_KEY || null;
    const apiSecret = import.meta.env.VITE_POLONIEX_API_SECRET || null;
    const forceMockMode = import.meta.env.VITE_FORCE_MOCK_MODE === 'true';

    // Check for localStorage override (for testing)
    const storedConfig = this.getStoredConfig();

    return {
      apiKey: storedConfig?.apiKey || apiKey,
      apiSecret: storedConfig?.apiSecret || apiSecret,
      forceMockMode: storedConfig?.forceMockMode ?? forceMockMode,
      isProduction: import.meta.env.PROD,
      wsUrl: import.meta.env.VITE_WS_URL || 'wss://ws.poloniex.com/ws/public',
      apiUrl: import.meta.env.VITE_API_URL || 'https://api.poloniex.com',
      liveTradingEnabled: false // Will be set after validation
    };
  }

  private getStoredConfig(): Partial<EnvironmentConfig> | null {
    try {
      const stored = localStorage.getItem('poloniex_config');
      return stored ? JSON.parse(stored) : null;
    } catch {
      return null;
    }
  }

  private validateConfiguration(): void {
    const { apiKey, apiSecret, forceMockMode } = this.config;

    // Determine if live trading should be enabled
    const hasValidCredentials = !!(apiKey && apiSecret && apiKey.length > 10 && apiSecret.length > 10);
    const isExplicitlyMocked = forceMockMode === true;

    this.config.liveTradingEnabled = hasValidCredentials && !isExplicitlyMocked;

    // Log configuration status (only in development)
    if (import.meta.env.DEV) {
      console.info('Environment Configuration:', {
        hasApiKey: !!apiKey,
        hasApiSecret: !!apiSecret,
        forceMockMode: isExplicitlyMocked,
        liveTradingEnabled: this.config.liveTradingEnabled,
        mode: this.config.liveTradingEnabled ? 'LIVE' : 'MOCK'
      });
    }

    // Warn about configuration issues
    if (!hasValidCredentials && !isExplicitlyMocked) {
      console.warn(
        'API credentials missing or invalid. Running in MOCK mode.',
        '\nTo enable live trading:',
        '\n1. Set VITE_POLONIEX_API_KEY and VITE_POLONIEX_API_SECRET in .env',
        '\n2. Ensure VITE_FORCE_MOCK_MODE is not set to "true"'
      );
    }

    if (hasValidCredentials && isExplicitlyMocked) {
      console.warn(
        'Valid API credentials found but VITE_FORCE_MOCK_MODE is enabled.',
        '\nTo enable live trading, set VITE_FORCE_MOCK_MODE=false'
      );
    }
  }

  getConfig(): EnvironmentConfig {
    return { ...this.config };
  }

  isLiveTradingEnabled(): boolean {
    return this.config.liveTradingEnabled;
  }

  isMockMode(): boolean {
    return !this.config.liveTradingEnabled;
  }

  // Allow runtime configuration updates (for testing)
  updateConfig(updates: Partial<EnvironmentConfig>): void {
    this.config = { ...this.config, ...updates };
    this.validateConfiguration();

    // Persist to localStorage
    localStorage.setItem('poloniex_config', JSON.stringify({
      apiKey: this.config.apiKey,
      apiSecret: this.config.apiSecret,
      forceMockMode: this.config.forceMockMode
    }));

    // Trigger configuration change event
    window.dispatchEvent(new CustomEvent('config-updated', { 
      detail: this.config 
    }));
  }

  // Clear stored configuration
  clearStoredConfig(): void {
    localStorage.removeItem('poloniex_config');
    this.config = this.loadConfiguration();
    this.validateConfiguration();
  }
}

import React, { createContext, useState, useEffect, ReactNode } from 'react';
import { isAuthenticated, clearAuthData, storeAuthDataLegacy, getUserData } from '@/utils/auth';
import { authService } from '@/services/authService';

interface AuthContextType {
  isLoggedIn: boolean;
  login: (usernameOrToken: string, passwordOrExpiresIn?: string | number) => Promise<boolean>;
  logout: () => Promise<void>;
  user: UserProfile | null;
  isAuthenticated: boolean; // Added for FuturesContext
  loading: boolean;
  refreshToken: () => Promise<boolean>;
}

interface UserProfile {
  id: string;
  username: string;
  email: string;
  role: string;
}

export const AuthContext = createContext<AuthContextType>({
  isLoggedIn: false,
  login: async () => false,
  logout: async () => {},
  user: null,
  isAuthenticated: false, // Added for FuturesContext
  loading: false,
  refreshToken: async () => false
});

interface AuthProviderProps {
  children: ReactNode;
}

export const AuthProvider: React.FC<AuthProviderProps> = ({ children }) => {
  const [isLoggedIn, setIsLoggedIn] = useState<boolean>(false);
  const [user, setUser] = useState<UserProfile | null>(null);
  const [loading, setLoading] = useState<boolean>(true);

  // Check authentication status on mount
  useEffect(() => {
    const checkAuth = async () => {
      setLoading(true);

      try {
        const authenticated = isAuthenticated();
        setIsLoggedIn(authenticated);

        if (authenticated) {
          // Get user data from localStorage first
          const userData = getUserData();
          if (userData) {
            setUser(userData);
          }

          // Verify token with server
          const isValid = await authService.verifyToken();
          if (!isValid) {
            console.warn('Token verification failed, logging out');
            await logout();
          }
        }
      } catch (error) {
        console.error('Auth check error:', error);
        await logout();
      } finally {
        setLoading(false);
      }
    };

    checkAuth();
  }, []);

  const login = async (usernameOrToken: string, passwordOrExpiresIn?: string | number): Promise<boolean> => {
    try {
      // Check if this is JWT login (new method) or legacy login
      if (typeof passwordOrExpiresIn === 'string') {
        // New JWT login method
        const response = await authService.login({
          username: usernameOrToken,
          password: passwordOrExpiresIn
        });

        if (response.success && response.data) {
          setIsLoggedIn(true);
          setUser(response.data.user);
          return true;
        } else {
          console.error('Login failed:', response.error);
          return false;
        }
      } else {
        // Legacy login method for backward compatibility
        const token = usernameOrToken;
        const expiresIn = passwordOrExpiresIn as number || 3600;

        storeAuthDataLegacy(token, expiresIn);
        setIsLoggedIn(true);

        // Set mock user data for legacy login
        setUser({
          id: '1',
          username: 'trader',
          email: 'trader@example.com',
          role: 'user'
        });

        return true;
      }
    } catch (error) {
      console.error('Login error:', error);
      return false;
    }
  };

  const logout = async (): Promise<void> => {
    try {
      await authService.logout();
    } catch (error) {
      console.error('Logout error:', error);
    } finally {
      setIsLoggedIn(false);
      setUser(null);
    }
  };

  const refreshToken = async (): Promise<boolean> => {
    try {
      const newToken = await authService.refreshToken();
      if (newToken) {
        // Token refreshed successfully
        return true;
      } else {
        // Refresh failed, log out
        await logout();
        return false;
      }
    } catch (error) {
      console.error('Token refresh error:', error);
      await logout();
      return false;
    }
  };

  return (
    <AuthContext.Provider value={{ 
      isLoggedIn, 
      login, 
      logout, 
      user,
      isAuthenticated: isLoggedIn, // Added for FuturesContext
      loading,
      refreshToken
    }}>
      {children}
    </AuthContext.Provider>
  );
};

import React, { createContext, useState, useEffect, ReactNode, useCallback } from 'react';
import PoloniexFuturesAPI, { 
  PositionMode, 
  MarginMode, 
  FuturesPosition, 
  FuturesAccountBalance 
} from '@/services/poloniexFuturesAPI';
import { useAuth } from '@/hooks/useAuth';
import { useSettings } from '@/hooks/useSettings';

interface FuturesContextType {
  api: PoloniexFuturesAPI;
  positions: FuturesPosition[];
  accountBalance: FuturesAccountBalance | null;
  positionMode: PositionMode;
  isLoading: boolean;
  error: string | null;
  refreshPositions: () => Promise<void>;
  refreshAccountBalance: () => Promise<void>;
  setPositionMode: (mode: PositionMode) => Promise<void>;
  setLeverage: (symbol: string, leverage: string, marginMode: MarginMode) => Promise<void>;
}

const defaultFuturesContext: FuturesContextType = {
  api: new PoloniexFuturesAPI(true), // Default to mock mode
  positions: [],
  accountBalance: null,
  positionMode: PositionMode.ONE_WAY,
  isLoading: false,
  error: null,
  refreshPositions: async () => {},
  refreshAccountBalance: async () => {},
  setPositionMode: async () => {},
  setLeverage: async () => {}
};

const FuturesContext = createContext<FuturesContextType>(defaultFuturesContext);

export { FuturesContext };

interface FuturesProviderProps {
  children: ReactNode;
}

export const FuturesProvider: React.FC<FuturesProviderProps> = ({ children }) => {
  const { isAuthenticated } = useAuth();
  const { mockMode } = useSettings();

  const [api] = useState<PoloniexFuturesAPI>(() => new PoloniexFuturesAPI(mockMode));
  const [positions, setPositions] = useState<FuturesPosition[]>([]);
  const [accountBalance, setAccountBalance] = useState<FuturesAccountBalance | null>(null);
  const [positionMode, setPositionModeState] = useState<PositionMode>(PositionMode.ONE_WAY);
  const [isLoading, setIsLoading] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);

  // Refresh positions data
  const refreshPositions = useCallback(async () => {
    if (!isAuthenticated && !mockMode) return;

    setIsLoading(true);
    setError(null);

    try {
      const positions = await api.getCurrentPositions();
      setPositions(positions);
    } catch (err) {
      console.error('Failed to fetch positions:', err);
      setError(err instanceof Error ? err.message : 'Failed to fetch positions');
    } finally {
      setIsLoading(false);
    }
  }, [isAuthenticated, mockMode, api]);

  // Refresh account balance
  const refreshAccountBalance = useCallback(async () => {
    if (!isAuthenticated && !mockMode) return;

    setIsLoading(true);
    setError(null);

    try {
      const balance = await api.getAccountBalance();
      setAccountBalance(balance);
    } catch (err) {
      console.error('Failed to fetch account balance:', err);
      setError(err instanceof Error ? err.message : 'Failed to fetch account balance');
    } finally {
      setIsLoading(false);
    }
  }, [isAuthenticated, mockMode, api]);

  // Set position mode (Hedge or One-way)
  const setPositionMode = async (mode: PositionMode) => {
    if (!isAuthenticated && !mockMode) return;

    setIsLoading(true);
    setError(null);

    try {
      await api.switchPositionMode(mode);
      setPositionModeState(mode);
    } catch (err) {
      console.error('Failed to set position mode:', err);
      setError(err instanceof Error ? err.message : 'Failed to set position mode');
    } finally {
      setIsLoading(false);
    }
  };

  // Set leverage for a symbol
  const setLeverage = async (symbol: string, leverage: string, marginMode: MarginMode) => {
    if (!isAuthenticated && !mockMode) return;

    setIsLoading(true);
    setError(null);

    try {
      await api.setLeverage({ symbol, lever: leverage, mgnMode: marginMode });
      // Refresh positions to get updated leverage
      await refreshPositions();
    } catch (err) {
      console.error('Failed to set leverage:', err);
      setError(err instanceof Error ? err.message : 'Failed to set leverage');
    } finally {
      setIsLoading(false);
    }
  };

  // Fetch initial data when authenticated or in mock mode
  useEffect(() => {
    if (isAuthenticated || mockMode) {
      const fetchInitialData = async () => {
        setIsLoading(true);
        setError(null);

        try {
          // Get position mode
          const modeResponse = await api.getPositionMode();
          setPositionModeState(modeResponse.posMode);

          // Get positions and account balance
          await Promise.all([
            refreshPositions(),
            refreshAccountBalance()
          ]);
        } catch (err) {
          console.error('Failed to fetch initial futures data:', err);
          setError(err instanceof Error ? err.message : 'Failed to fetch initial futures data');
        } finally {
          setIsLoading(false);
        }
      };

      fetchInitialData();
    }
  }, [api, refreshAccountBalance, refreshPositions, isAuthenticated, mockMode]);

  // Refresh data periodically
  useEffect(() => {
    if (!isAuthenticated && !mockMode) return;

    const refreshInterval = setInterval(() => {
      refreshPositions();
      refreshAccountBalance();
    }, 30000); // Refresh every 30 seconds

    return () => clearInterval(refreshInterval);
  }, [refreshAccountBalance, refreshPositions, isAuthenticated, mockMode]);

  const value = {
    api,
    positions,
    accountBalance,
    positionMode,
    isLoading,
    error,
    refreshPositions,
    refreshAccountBalance,
    setPositionMode,
    setLeverage
  };

  return (
    <FuturesContext.Provider value={value}>
      {children}
    </FuturesContext.Provider>
  );
};

import React, { createContext, useState, useEffect, ReactNode } from 'react';
import { getStorageItem, setStorageItem, STORAGE_KEYS, isStorageAvailable } from '@/utils/storage';

interface SettingsContextType {
  apiKey: string;
  apiSecret: string;
  isLiveTrading: boolean;
  darkMode: boolean;
  defaultPair: string;
  emailNotifications: boolean;
  tradeNotifications: boolean;
  priceAlerts: boolean;
  chatNotifications: boolean;
  showExtension: boolean;
  chartType: 'candle' | 'line' | 'area';
  timeframe: '1m' | '5m' | '15m' | '1h' | '4h' | '1d';
  leverage: number;
  riskPerTrade: number;
  stopLossPercent: number;
  takeProfitPercent: number;
  trailingStopPercent: number;
  autoTradingEnabled: boolean;
  updateSettings: (settings: Partial<SettingsState>) => void;
  resetSettings: () => void;
  hasStoredCredentials: boolean;
  exportSettings: () => string;
  importSettings: (settingsJson: string) => boolean;
  mockMode?: boolean; // Added for FuturesContext
}

interface SettingsState {
  apiKey: string;
  apiSecret: string;
  isLiveTrading: boolean;
  darkMode: boolean;
  defaultPair: string;
  emailNotifications: boolean;
  tradeNotifications: boolean;
  priceAlerts: boolean;
  chatNotifications: boolean;
  showExtension: boolean;
  chartType: 'candle' | 'line' | 'area';
  timeframe: '1m' | '5m' | '15m' | '1h' | '4h' | '1d';
  leverage: number;
  riskPerTrade: number;
  stopLossPercent: number;
  takeProfitPercent: number;
  trailingStopPercent: number;
  autoTradingEnabled: boolean;
}

const defaultSettings: SettingsState = {
  apiKey: '',
  apiSecret: '',
  isLiveTrading: false,
  darkMode: false,
  defaultPair: 'BTC-USDT',
  emailNotifications: true,
  tradeNotifications: true,
  priceAlerts: false,
  chatNotifications: true,
  showExtension: true,
  chartType: 'candle',
  timeframe: '5m',
  leverage: 1,
  riskPerTrade: 2,
  stopLossPercent: 2,
  takeProfitPercent: 4,
  trailingStopPercent: 1,
  autoTradingEnabled: false
};

// Define storage keys
const EXTENDED_STORAGE_KEYS = {
  ...STORAGE_KEYS,
  CHART_TYPE: 'poloniex_chart_type',
  TIMEFRAME: 'poloniex_timeframe',
  LEVERAGE: 'poloniex_leverage',
  RISK_PER_TRADE: 'poloniex_risk_per_trade',
  STOP_LOSS_PERCENT: 'poloniex_stop_loss_percent',
  TAKE_PROFIT_PERCENT: 'poloniex_take_profit_percent',
  TRAILING_STOP_PERCENT: 'poloniex_trailing_stop_percent',
  AUTO_TRADING_ENABLED: 'poloniex_auto_trading_enabled'
};

const SettingsContext = createContext<SettingsContextType | undefined>(undefined);

export { SettingsContext };

interface SettingsProviderProps {
  children: ReactNode;
}

export const SettingsProvider: React.FC<SettingsProviderProps> = ({ children }) => {
  // Check if we can access localStorage
  const canUseStorage = isStorageAvailable();

  // Get initial settings from localStorage or environment variables
  const getInitialSettings = (): SettingsState => {
    if (!canUseStorage) {
      return {
        ...defaultSettings,
        apiKey: import.meta.env.VITE_POLONIEX_API_KEY || '',
        apiSecret: import.meta.env.VITE_POLONIEX_API_SECRET || ''
      };
    }

    return {
      apiKey: getStorageItem(EXTENDED_STORAGE_KEYS.API_KEY, import.meta.env.VITE_POLONIEX_API_KEY || ''),
      apiSecret: getStorageItem(EXTENDED_STORAGE_KEYS.API_SECRET, import.meta.env.VITE_POLONIEX_API_SECRET || ''),
      isLiveTrading: getStorageItem(EXTENDED_STORAGE_KEYS.IS_LIVE_TRADING, false),
      darkMode: getStorageItem(EXTENDED_STORAGE_KEYS.DARK_MODE, false),
      defaultPair: getStorageItem(EXTENDED_STORAGE_KEYS.DEFAULT_PAIR, 'BTC-USDT'),
      emailNotifications: getStorageItem(EXTENDED_STORAGE_KEYS.EMAIL_NOTIFICATIONS, true),
      tradeNotifications: getStorageItem(EXTENDED_STORAGE_KEYS.TRADE_NOTIFICATIONS, true),
      priceAlerts: getStorageItem(EXTENDED_STORAGE_KEYS.PRICE_ALERTS, false),
      chatNotifications: getStorageItem(EXTENDED_STORAGE_KEYS.CHAT_NOTIFICATIONS, true),
      showExtension: getStorageItem(EXTENDED_STORAGE_KEYS.SHOW_EXTENSION, true),
      chartType: getStorageItem(EXTENDED_STORAGE_KEYS.CHART_TYPE, 'candle'),
      timeframe: getStorageItem(EXTENDED_STORAGE_KEYS.TIMEFRAME, '5m'),
      leverage: getStorageItem(EXTENDED_STORAGE_KEYS.LEVERAGE, 1),
      riskPerTrade: getStorageItem(EXTENDED_STORAGE_KEYS.RISK_PER_TRADE, 2),
      stopLossPercent: getStorageItem(EXTENDED_STORAGE_KEYS.STOP_LOSS_PERCENT, 2),
      takeProfitPercent: getStorageItem(EXTENDED_STORAGE_KEYS.TAKE_PROFIT_PERCENT, 4),
      trailingStopPercent: getStorageItem(EXTENDED_STORAGE_KEYS.TRAILING_STOP_PERCENT, 1),
      autoTradingEnabled: getStorageItem(EXTENDED_STORAGE_KEYS.AUTO_TRADING_ENABLED, false)
    };
  };

  const [settings, setSettings] = useState<SettingsState>(getInitialSettings);
  const [hasStoredCredentials, setHasStoredCredentials] = useState<boolean>(false);

  // Check if we have credentials stored
  useEffect(() => {
    setHasStoredCredentials(Boolean(settings.apiKey && settings.apiSecret));
  }, [settings.apiKey, settings.apiSecret]);

  // Apply dark mode setting to document
  useEffect(() => {
    if (settings.darkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, [settings.darkMode]);

  // Update settings in state and localStorage
  const updateSettings = (newSettings: Partial<SettingsState>) => {
    setSettings(prev => {
      const updated = { ...prev, ...newSettings };

      // Special handling for live trading mode
      if (newSettings.isLiveTrading !== undefined) {
        // Only allow live trading if we have API credentials
        if (newSettings.isLiveTrading && (!updated.apiKey || !updated.apiSecret)) {
          console.log('Cannot enable live trading without API credentials');
          updated.isLiveTrading = false;
        }
      }

      // Only persist to localStorage if it's available
      if (canUseStorage) {
        // Persist each updated setting to localStorage
        Object.entries(newSettings).forEach(([key, value]) => {
          const storageKey = EXTENDED_STORAGE_KEYS[key.toUpperCase() as keyof typeof EXTENDED_STORAGE_KEYS] || `poloniex_${key}`;
          setStorageItem(storageKey, value);
        });
      }

      return updated;
    });
  };

  // Reset all settings to default
  const resetSettings = () => {
    setSettings(defaultSettings);

    if (canUseStorage) {
      Object.values(EXTENDED_STORAGE_KEYS).forEach(key => {
        localStorage.removeItem(key);
      });
    }
  };

  // Export settings to JSON string
  const exportSettings = (): string => {
    try {
      // Create a copy of settings without sensitive data
      const exportableSettings = { ...settings };
      const sensitiveKeys: (keyof SettingsState)[] = ['apiKey', 'apiSecret'];

      sensitiveKeys.forEach(key => {
        if (key in exportableSettings) {
          delete exportableSettings[key];
        }
      });

      return JSON.stringify(exportableSettings);
    } catch (error) {
      console.error('Error exporting settings:', error);
      return '';
    }
  };

  // Import settings from JSON string
  const importSettings = (settingsJson: string): boolean => {
    try {
      const importedSettings = JSON.parse(settingsJson);

      // Validate imported settings
      if (typeof importedSettings !== 'object' || importedSettings === null) {
        throw new Error('Invalid settings format');
      }

      // Don't import sensitive data
      const sensitiveKeys: (keyof SettingsState)[] = ['apiKey', 'apiSecret'];
      sensitiveKeys.forEach(key => {
        if (key in importedSettings) {
          delete importedSettings[key];
        }
      });

      // Update settings
      updateSettings(importedSettings);
      return true;
    } catch (error) {
      console.error('Error importing settings:', error);
      return false;
    }
  };

  return (
    <SettingsContext.Provider
      value={{
        ...settings,
        updateSettings,
        resetSettings,
        hasStoredCredentials,
        exportSettings,
        importSettings,
        mockMode: !settings.isLiveTrading // Added for FuturesContext
      }}
    >
      {children}
    </SettingsContext.Provider>
  );
};

// End of component

