      name: 'Moving Average Crossover',
      type: StrategyType.MA_CROSSOVER,
      parameters: {
        fastPeriod: 10,
        slowPeriod: 50,
        pair: 'BTC-USDT',
        timeframe: '1d'
      } as MovingAverageCrossoverParameters,
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString(),
      performance: {
        totalPnL: 12.5,
        winRate: 0.65,
        tradesCount: 24
      }
    }
  ]);

  const [activeStrategies, setActiveStrategies] = useState<string[]>(['1']);
  const [errors, setErrors] = useState<string[]>([]);

  const addError = useCallback((error: string) => {
    // Avoid adding duplicate errors
    setErrors(prev => {
      if (prev.includes(error)) {
        return prev;
      }
      return [...prev, error];
    });
  }, []);

  const clearErrors = useCallback(() => {
    setErrors([]);
  }, []);

  // If there was an error loading data, add it to our errors
  useEffect(() => {
    if (error) {
      const errorMessage = error instanceof Error ? error.message : String(error);
      addError(errorMessage);
    }
  }, [error, addError]);

  const addStrategy = (strategy: Strategy) => {
    setStrategies(prev => [...prev, strategy]);
  };

  const removeStrategy = (id: string) => {
    setStrategies(prev => prev.filter(strategy => strategy.id !== id));
    setActiveStrategies(prev => prev.filter(strategyId => strategyId !== id));
  };

  const toggleStrategyActive = (id: string) => {
    setActiveStrategies(prev => 
      prev.includes(id) 
        ? prev.filter(strategyId => strategyId !== id) 
        : [...prev, id]
    );
  };

  // Place an order using the Poloniex API
  const placeOrder = async (
    pair: string, 
    side: 'buy' | 'sell', 
    type: 'limit' | 'market', 
    quantity: number, 
    price?: number
  ) => {
    try {
      if (isMockMode) {
        console.log('Using mock order placement', { pair, side, type, quantity, price });
        return { 
          success: true, 
          orderId: 'mock-order-' + Date.now(),
          pair,
          side,
          type,
          quantity,
          price: price || 'market'
        };
      }

      const result = await poloniexApi.placeOrder(pair, side, type, quantity, price);
      return result;
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Unknown error placing order';
      addError(errorMessage);
      throw new Error(errorMessage);
    }
  };

  return (
    <TradingContext.Provider value={{
      marketData,
      trades,
      strategies,
      activeStrategies,
      accountBalance,
      isLoading,
      isMockMode,
      addStrategy,
      removeStrategy,
      toggleStrategyActive,
      placeOrder,
      errors,
      addError,
      clearErrors,
      refreshApiConnection
    }}>
      {children}
    </TradingContext.Provider>
  );
};

// End of component

import React, { createContext, useEffect, ReactNode } from 'react';
import { webSocketService } from '../services/websocketService';
import { usePageVisibility } from '../hooks/usePageVisibility';

interface WebSocketContextType {
  isConnected: boolean;
  connectionState: string;
  lastError: string | null;
  connectionHealth: {
    isHealthy: boolean;
    uptime: number;
    latency: number | null;
    reconnectAttempts: number;
  };
}

const WebSocketContext = createContext<WebSocketContextType | null>(null);

export { WebSocketContext };
export type { WebSocketContextType };

interface WebSocketProviderProps {
  children: ReactNode;
}

export const WebSocketProvider: React.FC<WebSocketProviderProps> = ({ children }) => {
  const [contextState, setContextState] = React.useState<WebSocketContextType>({
    isConnected: false,
    connectionState: 'disconnected',
    lastError: null,
    connectionHealth: {
      isHealthy: false,
      uptime: 0,
      latency: null,
      reconnectAttempts: 0
    }
  });

  // Handle page visibility changes
  usePageVisibility(
    () => {
      // Page visible - ensure connection
      webSocketService.handlePageVisibilityChange(true);
    },
    () => {
      // Page hidden - maintain connection in background
      webSocketService.handlePageVisibilityChange(false);
    },
    30000 // 30 second grace period
  );

  useEffect(() => {
    // Connection state change handler
    const handleConnectionStateChange = (state: string) => {
      setContextState(prev => ({
        ...prev,
        connectionState: state,
        isConnected: state === 'connected'
      }));
    };

    // Error handler
    const handleError = (error: Error | unknown) => {
      setContextState(prev => ({
        ...prev,
        lastError: error instanceof Error ? error.message : 'Unknown error'
      }));
    };

    // Set up event listeners
    webSocketService.on('connectionStateChanged', handleConnectionStateChange);
    webSocketService.on('error', handleError);

    // Initialize connection
    webSocketService.connect().catch(console.error);

    // Update connection health periodically
    const healthInterval = setInterval(() => {
      const health = webSocketService.getConnectionHealth();
      setContextState(prev => ({
        ...prev,
        connectionHealth: {
          isHealthy: health.isHealthy,
          uptime: health.uptime,
          latency: health.latency,
          reconnectAttempts: health.reconnectAttempts
        }
      }));
    }, 5000);

    // Cleanup
    return () => {
      clearInterval(healthInterval);
      webSocketService.off('connectionStateChanged', handleConnectionStateChange);
      webSocketService.off('error', handleError);
      webSocketService.disconnect();
    };
  }, []);

  return (
    <WebSocketContext.Provider value={contextState}>
      {children}
    </WebSocketContext.Provider>
  );
};

// End of component

import { useEffect, useRef, useCallback } from 'react';

/**
 * Custom hook for managing accessibility features
 */
export const useAccessibility = () => {
  const focusRingRef = useRef<HTMLElement | null>(null);

  /**
   * Trap focus within a container element
   */
  const trapFocus = useCallback((containerElement: HTMLElement) => {
    const focusableElements = containerElement.querySelectorAll(
      'button, [href], input, select, textarea, [tabindex]:not([tabindex="-1"])'
    );

    const firstElement = focusableElements[0] as HTMLElement;
    const lastElement = focusableElements[focusableElements.length - 1] as HTMLElement;

    const handleTabKeyPress = (e: KeyboardEvent) => {
      if (e.key !== 'Tab') return;

      if (e.shiftKey) {
        if (document.activeElement === firstElement) {
          e.preventDefault();
          lastElement.focus();
        }
      } else {
        if (document.activeElement === lastElement) {
          e.preventDefault();
          firstElement.focus();
        }
      }
    };

    containerElement.addEventListener('keydown', handleTabKeyPress);

    return () => {
      containerElement.removeEventListener('keydown', handleTabKeyPress);
    };
  }, []);

  /**
   * Handle escape key to close modals/dropdowns
   */
  const handleEscapeKey = useCallback((callback: () => void) => {
    const handleKeyPress = (e: KeyboardEvent) => {
      if (e.key === 'Escape') {
        callback();
      }
    };

    document.addEventListener('keydown', handleKeyPress);

    return () => {
      document.removeEventListener('keydown', handleKeyPress);
    };
  }, []);

  /**
   * Announce changes to screen readers
   */
  const announceToScreenReader = useCallback((message: string, priority: 'polite' | 'assertive' = 'polite') => {
    const announcement = document.createElement('div');
    announcement.setAttribute('aria-live', priority);
    announcement.setAttribute('aria-atomic', 'true');
    announcement.className = 'sr-only';
    announcement.textContent = message;

    document.body.appendChild(announcement);

    setTimeout(() => {
      document.body.removeChild(announcement);
    }, 1000);
  }, []);

  /**
   * Set focus to element with proper error handling
   */
  const setFocus = useCallback((element: HTMLElement | null) => {
    if (element && typeof element.focus === 'function') {
      try {
        element.focus();
      } catch (error) {
        console.warn('Failed to set focus:', error);
      }
    }
  }, []);

  /**
   * Skip to main content link functionality
   */
  const skipToMainContent = useCallback(() => {
    const mainContent = document.querySelector('main[role="main"], main, #main-content');
    if (mainContent) {
      (mainContent as HTMLElement).focus();
      mainContent.scrollIntoView({ behavior: 'smooth' });
    }
  }, []);

  return {
    trapFocus,
    handleEscapeKey,
    announceToScreenReader,
    setFocus,
    skipToMainContent,
    focusRingRef
  };
};

/**
 * Hook for managing keyboard navigation in lists and grids
 */
export const useKeyboardNavigation = (
  itemCount: number,
  columns?: number,
  onSelect?: (index: number) => void
) => {
  const currentIndex = useRef(0);

  const handleKeyDown = useCallback((e: KeyboardEvent) => {
    const { key } = e;
    let newIndex = currentIndex.current;

    switch (key) {
      case 'ArrowDown':
        e.preventDefault();
        if (columns) {
          // Grid navigation
          newIndex = Math.min(currentIndex.current + columns, itemCount - 1);
        } else {
          // List navigation
          newIndex = Math.min(currentIndex.current + 1, itemCount - 1);
        }
        break;

      case 'ArrowUp':
        e.preventDefault();
        if (columns) {
          // Grid navigation
          newIndex = Math.max(currentIndex.current - columns, 0);
        } else {
          // List navigation
          newIndex = Math.max(currentIndex.current - 1, 0);
        }
        break;

      case 'ArrowRight':
        if (columns) {
          e.preventDefault();
          newIndex = Math.min(currentIndex.current + 1, itemCount - 1);
        }
        break;

      case 'ArrowLeft':
        if (columns) {
          e.preventDefault();
          newIndex = Math.max(currentIndex.current - 1, 0);
        }
        break;

      case 'Home':
        e.preventDefault();
        newIndex = 0;
        break;

      case 'End':
        e.preventDefault();
        newIndex = itemCount - 1;
        break;

      case 'Enter':
      case ' ':
        e.preventDefault();
        if (onSelect) {
          onSelect(currentIndex.current);
        }
        break;
    }

    if (newIndex !== currentIndex.current) {
      currentIndex.current = newIndex;
      // Focus the new item
      const items = document.querySelectorAll('[data-keyboard-nav-item]');
      const targetItem = items[newIndex] as HTMLElement;
      if (targetItem) {
        targetItem.focus();
      }
    }
  }, [itemCount, columns, onSelect]);

  const setCurrentIndex = useCallback((index: number) => {
    currentIndex.current = Math.max(0, Math.min(index, itemCount - 1));
  }, [itemCount]);

  return {
    handleKeyDown,
    currentIndex: currentIndex.current,
    setCurrentIndex
  };
};

/**
 * Hook for managing live regions for dynamic content updates
 */
export const useLiveRegion = () => {
  const liveRegionRef = useRef<HTMLDivElement | null>(null);

  useEffect(() => {
    // Create live region if it doesn't exist
    if (!liveRegionRef.current) {
      const liveRegion = document.createElement('div');
      liveRegion.setAttribute('aria-live', 'polite');
      liveRegion.setAttribute('aria-atomic', 'true');
      liveRegion.className = 'sr-only';
      liveRegion.id = 'live-region';
      document.body.appendChild(liveRegion);
      liveRegionRef.current = liveRegion;
    }

    return () => {
      if (liveRegionRef.current && liveRegionRef.current.parentNode) {
        liveRegionRef.current.parentNode.removeChild(liveRegionRef.current);
      }
    };
  }, []);

  const announce = useCallback((message: string, priority: 'polite' | 'assertive' = 'polite') => {
    if (liveRegionRef.current) {
      liveRegionRef.current.setAttribute('aria-live', priority);
      liveRegionRef.current.textContent = message;
    }
  }, []);

  return { announce };
};

import { useCallback } from 'react';
import { useErrorHandler } from './useErrorHandler';

/**
 * Custom hook for handling API requests with proper error handling
 */
export const useApiRequest = () => {
  const { error, hasError, handleError, resetError, withErrorHandling } = useErrorHandler();

  /**
   * Make a GET request with error handling
   * @param url URL to fetch
   * @param options Fetch options
   * @returns Response data or null on error
   */
  const get = useCallback(async <T,>(url: string, options?: RequestInit): Promise<T | null> => {
    const wrappedFn = withErrorHandling(async () => {
      const response = await fetch(url, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
        ...options,
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.message || `Request failed with status ${response.status}`);
      }

      return response.json();
    });

    return await wrappedFn();
  }, [withErrorHandling]);

  /**
   * Make a POST request with error handling
   * @param url URL to fetch
   * @param data Data to send
   * @param options Fetch options
   * @returns Response data or null on error
   */
  const post = useCallback(async <T,>(url: string, data: any, options?: RequestInit): Promise<T | null> => {
    const wrappedFn = withErrorHandling(async () => {
      const response = await fetch(url, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
        ...options,
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.message || `Request failed with status ${response.status}`);
      }

      return response.json();
    });

    return await wrappedFn();
  }, [withErrorHandling]);

  /**
   * Send a message to Chrome extension (placeholder)
   * @param message Message to send
   * @returns Response data or null on error
   */
  const sendExtensionMessage = useCallback(async <T,>(_message: any): Promise<T | null> => {
    console.log('Extension messaging not implemented');
    return null;
  }, []);

  /**
   * Get data from Chrome storage (placeholder)
   * @param key Storage key
   * @returns Storage data or null on error
   */
  const getExtensionStorage = useCallback(async <T,>(_key: string): Promise<T | null> => {
    console.log('Extension storage not implemented');
    return null;
  }, []);

  return {
    get,
    post,
    sendExtensionMessage,
    getExtensionStorage,
    error,
    hasError,
    handleError,
    resetError
  };
};

export default useApiRequest;

import { useState, useEffect, useCallback, useMemo } from 'react';

type ErrorType = 'api' | 'network' | 'validation' | 'authentication' | 'unknown';
type ErrorSeverity = 'low' | 'medium' | 'high' | 'critical';

interface ErrorHandlerOptions {
  retryCount?: number;
  retryDelay?: number;
  onRetry?: (error: Error, attempt: number) => void;
  onFallback?: (error: Error) => void;
  fallbackValue?: any;
  logToServer?: boolean;
  showToUser?: boolean;
}

interface ErrorState {
  hasError: boolean;
  error: Error | null;
  errorType: ErrorType;
  severity: ErrorSeverity;
  retryAttempt: number;
  lastErrorTime: number;
}

const DEFAULT_OPTIONS: ErrorHandlerOptions = {
  retryCount: 3,
  retryDelay: 1000,
  logToServer: true,
  showToUser: true
};

export const useErrorHandler = (options: ErrorHandlerOptions = {}) => {
  const config = useMemo(() => ({ ...DEFAULT_OPTIONS, ...options }), [options]);

  const [errorState, setErrorState] = useState<ErrorState>({
    hasError: false,
    error: null,
    errorType: 'unknown',
    severity: 'low',
    retryAttempt: 0,
    lastErrorTime: 0
  });

  // Reset error state
  const resetError = useCallback(() => {
    setErrorState({
      hasError: false,
      error: null,
      errorType: 'unknown',
      severity: 'low',
      retryAttempt: 0,
      lastErrorTime: 0
    });
  }, []);

  // Determine error type from error object
  const determineErrorType = useCallback((error: Error): ErrorType => {
    const message = error.message.toLowerCase();

    if (error.name === 'ApiError' || message.includes('api') || message.includes('endpoint')) {
      return 'api';
    }

    if (error.name === 'NetworkError' || 
        message.includes('network') || 
        message.includes('connection') ||
        message.includes('offline') ||
        message.includes('timeout')) {
      return 'network';
    }

    if (error.name === 'ValidationError' || message.includes('validation') || message.includes('invalid')) {
      return 'validation';
    }

    if (error.name === 'AuthenticationError' || 
        message.includes('auth') || 
        message.includes('permission') ||
        message.includes('token') ||
        message.includes('unauthorized')) {
      return 'authentication';
    }

    return 'unknown';
  }, []);

  // Determine error severity
  const determineErrorSeverity = useCallback((error: Error, type: ErrorType): ErrorSeverity => {
    const message = error.message.toLowerCase();

    // Critical errors
    if (message.includes('critical') || 
        message.includes('fatal') ||
        message.includes('crash')) {
      return 'critical';
    }

    // High severity errors
    if (type === 'authentication' || 
        message.includes('security') ||
        message.includes('data loss')) {
      return 'high';
    }

    // Medium severity errors
    if (type === 'api' || 
        type === 'network' ||
        message.includes('timeout')) {
      return 'medium';
    }

    // Default to low severity
    return 'low';
  }, []);

  // Log error to server
  const logErrorToServer = useCallback((error: Error, type: ErrorType, severity: ErrorSeverity) => {
    // In production, this would send to a logging service
    console.error('Error logged to server:', {
      message: error.message,
      stack: error.stack,
      type,
      severity,
      timestamp: new Date().toISOString(),
      userAgent: navigator.userAgent,
      url: window.location.href
    });

    // Store in localStorage for diagnostics
    try {
      const errorLog = JSON.parse(localStorage.getItem('errorLog') || '[]');
      errorLog.push({
        message: error.message,
        type,
        severity,
        timestamp: new Date().toISOString(),
        url: window.location.href
      });

      // Keep only the last 20 errors
      if (errorLog.length > 20) errorLog.shift();
      localStorage.setItem('errorLog', JSON.stringify(errorLog));
    } catch (e) {
      console.error('Failed to log error to localStorage', e);
    }
  }, []);

  // Handle error with retry logic
  const handleError = useCallback((error: Error) => {
    const errorType = determineErrorType(error);
    const severity = determineErrorSeverity(error, errorType);
    const now = Date.now();

    setErrorState(prev => {
      const newState = {
        hasError: true,
        error,
        errorType,
        severity,
        retryAttempt: prev.retryAttempt + 1,
        lastErrorTime: now
      };

      // Log error if configured
      if (config.logToServer) {
        logErrorToServer(error, errorType, severity);
      }

      // Check if we should retry
      if (newState.retryAttempt <= (config.retryCount || 0) && 
          (errorType === 'network' || errorType === 'api')) {

        // Schedule retry
        setTimeout(() => {
          if (config.onRetry) {
            config.onRetry(error, newState.retryAttempt);
          }

          // Only reset error state if it's the same error
          setErrorState(current => {
            if (current.error === error) {
              return {
                ...current,
                hasError: false
              };
            }
            return current;
          });
        }, (config.retryDelay || 1000) * Math.pow(2, newState.retryAttempt - 1)); // Exponential backoff
      } else if (config.onFallback) {
        // Use fallback if available
        config.onFallback(error);
      }

      return newState;
    });
  }, [
    config, 
    determineErrorType, 
    determineErrorSeverity, 
    logErrorToServer
  ]);

  // Wrap async function with error handling
  const withErrorHandling = useCallback(<T extends any[], R>(
    fn: (...args: T) => Promise<R>,
    options: ErrorHandlerOptions = {}
  ) => {
    const handlerOptions = { ...config, ...options };

    return async (...args: T): Promise<R> => {
      try {
        return await fn(...args);
      } catch (error) {
        handleError(error instanceof Error ? error : new Error(String(error)));

        if (handlerOptions.fallbackValue !== undefined) {
          return handlerOptions.fallbackValue as R;
        }

        throw error;
      }
    };
  }, [config, handleError]);

  // Effect to clear error after some time for non-critical errors
  useEffect(() => {
    if (errorState.hasError && 
        errorState.severity !== 'critical' && 
        errorState.severity !== 'high') {
      const timeout = setTimeout(() => {
        resetError();
      }, 10000); // Auto-clear after 10 seconds for low/medium severity

      return () => clearTimeout(timeout);
    }
  }, [errorState.hasError, errorState.severity, resetError]);

  return {
    error: errorState.error,
    errorType: errorState.errorType,
    severity: errorState.severity,
    hasError: errorState.hasError,
    retryAttempt: errorState.retryAttempt,
    handleError,
    resetError,
    withErrorHandling
  };
};

export default useErrorHandler;

import { useState, useEffect, useCallback, useRef } from 'react';
import { poloniexApi } from '@/services/poloniexAPI';
import { MarketData, Trade } from '@/types';
import { webSocketService } from '@/services/websocketService';
import { mockMarketData, mockTrades } from '@/data/mockData';
import { useSettings } from '@/hooks/useSettings';
import { shouldUseMockMode, IS_WEBCONTAINER } from '@/utils/environment';

interface PoloniexDataHook {
  marketData: MarketData[];
  trades: Trade[];
  accountBalance: any;
  isLoading: boolean;
  error: Error | null;
  isMockMode: boolean;
  fetchMarketData: (pair: string) => Promise<void>;
  fetchTrades: (pair: string) => Promise<void>;
  fetchAccountBalance: () => Promise<void>;
  refreshApiConnection: () => void;
}

export const usePoloniexData = (initialPair: string = 'BTC-USDT'): PoloniexDataHook => {
  const { apiKey, apiSecret, isLiveTrading } = useSettings();
  const [marketData, setMarketData] = useState<MarketData[]>(mockMarketData);
  const [trades, setTrades] = useState<Trade[]>(mockTrades);
  const [accountBalance, setAccountBalance] = useState<any>(null);
  const [isLoading, setIsLoading] = useState<boolean>(false);
  const [error, setError] = useState<Error | null>(null);

  // Determine mock mode based on environment and credentials
  const hasCredentials = Boolean(apiKey && apiSecret);
  const [isMockMode, setIsMockMode] = useState<boolean>(shouldUseMockMode(hasCredentials));

  // Create refs to hold latest function references to avoid dependency issues
  const fetchFunctionsRef = useRef<{
    fetchMarketData: (pair: string) => Promise<void>;
    fetchTrades: (pair: string) => Promise<void>;
    fetchAccountBalance: () => Promise<void>;
  }>();

  // Helper function to map Poloniex data to MarketData format

  const mapPoloniexDataToMarketData = useCallback((data: any[]): MarketData[] => {
    try {
      return data.map(item => ({
        pair: initialPair,
        timestamp: new Date(item[0]).getTime(),
        open: parseFloat(item[1]),
        high: parseFloat(item[2]),
        low: parseFloat(item[3]),
        close: parseFloat(item[4]),
        volume: parseFloat(item[5])
      }));
    } catch (err) {
      console.error('Error mapping Poloniex data:', err instanceof Error ? err.message : String(err));
      return [];
    }
  }, [initialPair]);

  const mapPoloniexTradeToTrade = useCallback((trade: any): Trade => {
    try {
      return {
        id: trade.id || `generated-${Date.now()}-${Math.random().toString(36).substring(2, 9)}`,
        pair: initialPair,
        timestamp: new Date(trade.createdAt).getTime(),
        type: trade.takerSide === 'buy' ? 'BUY' : 'SELL',
        price: parseFloat(trade.price),
        amount: parseFloat(trade.quantity),
        total: parseFloat(trade.price) * parseFloat(trade.quantity),
        strategyId: '', // Not available from API
        status: 'COMPLETED'
      };
    } catch (err) {
      console.error('Error mapping Poloniex trade:', err instanceof Error ? err.message : String(err));
      return {
        id: `error-${Date.now()}-${Math.random().toString(36).substring(2, 9)}`,
        pair: initialPair,
        timestamp: Date.now(),
        type: 'BUY',
        price: 0,
        amount: 0,
        total: 0,
        strategyId: '',
        status: 'FAILED'
      };
    }
  }, [initialPair]);

  // Fetch functions defined before refreshApiConnection
  const fetchMarketData = useCallback(async (pair: string) => {
    // If in mock mode, use mock data immediately
    if (isMockMode) {
      if (import.meta.env.DEV) {
        console.info('Mock mode active, using mock market data');
      }
      setMarketData(mockMarketData);
      return;
    }

    // In WebContainer, use mock data for development
    if (IS_WEBCONTAINER) {
      if (import.meta.env.DEV) {
        console.info('WebContainer environment detected, using mock market data');
      }
      setMarketData(mockMarketData);
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const data = await poloniexApi.getMarketData(pair);

      if (data && Array.isArray(data)) {
        const formattedData = mapPoloniexDataToMarketData(data);
        if (formattedData.length > 0) {
          setMarketData(formattedData);
        } else {
          throw new Error('No market data returned from API');
        }
      } else {
        throw new Error('Invalid market data format received from API');
      }
    } catch (err) {
      const error = err as Error;
      console.error('Error fetching market data:', error.message);
      setError(error);
      // Don't fall back to mock data - let the UI handle the error
    } finally {
      setIsLoading(false);
    }
  }, [isMockMode, mapPoloniexDataToMarketData]);

  const fetchTrades = useCallback(async (pair: string) => {
    // If in mock mode, use mock data immediately  
    if (isMockMode) {
      if (import.meta.env.DEV) {
        console.info('Mock mode active, using mock trades data');
      }
      setTrades(mockTrades);
      return;
    }

    // In WebContainer, use mock data for development
    if (IS_WEBCONTAINER) {
      if (import.meta.env.DEV) {
        console.info('WebContainer environment detected, using mock trades data');
      }
      setTrades(mockTrades);
      return;
    }

    setIsLoading(true);

    try {
      const data = await poloniexApi.getRecentTrades(pair);

      if (data && Array.isArray(data)) {
        const formattedTrades = data.map(mapPoloniexTradeToTrade).filter(trade => 
          trade.id.indexOf('error-') !== 0 && !isNaN(trade.price) && !isNaN(trade.amount)
        );

        if (formattedTrades.length > 0) {
          setTrades(formattedTrades);
        } else {
          throw new Error('No valid trades returned from API');
        }
      } else {
        throw new Error('Invalid trades data format received from API');
      }
    } catch (err) {
      const error = err as Error;
      console.error('Error fetching trades:', error.message);
      setError(error);
      // Don't fall back to mock data - let the UI handle the error
    } finally {
      setIsLoading(false);
    }
  }, [isMockMode, mapPoloniexTradeToTrade]);

  const fetchAccountBalance = useCallback(async () => {
    // If in mock mode, use mock data immediately
    if (isMockMode) {
      if (import.meta.env.DEV) {
        console.info('Mock mode active, using mock account data');
      }
      setAccountBalance({
        totalAmount: "15478.23",
        availableAmount: "12345.67",
        accountEquity: "15820.45",
        unrealizedPnL: "342.22",
        todayPnL: "156.78",
        todayPnLPercentage: "1.02"
      });
      return;
    }

    // In WebContainer, use mock data for development
    if (IS_WEBCONTAINER) {
      if (import.meta.env.DEV) {
        console.info('WebContainer environment detected, using mock account data');
      }
      setAccountBalance({
        totalAmount: "15478.23",
        availableAmount: "12345.67",
        accountEquity: "15820.45",
        unrealizedPnL: "342.22",
        todayPnL: "156.78",
        todayPnLPercentage: "1.02"
      });
      return;
    }

    setIsLoading(true);

    try {
      const data = await poloniexApi.getAccountBalance();
      setAccountBalance(data);
    } catch (err) {
      const error = err as Error;
      console.error('Error fetching account balance:', error.message);
      setError(error);
      // Don't fall back to mock data - let the UI handle the error
    } finally {
      setIsLoading(false);
    }
  }, [isMockMode]);

  // Update function refs to latest versions
  fetchFunctionsRef.current = {
    fetchMarketData,
    fetchTrades,
    fetchAccountBalance
  };

  // Function to refresh API connection when settings change - defined after all dependencies
  const refreshApiConnection = useCallback(() => {
    if (import.meta.env.DEV) {
      console.info('Refreshing API connection with new credentials');
    }
    setIsLoading(true);
    poloniexApi.loadCredentials();

    // Clear any existing errors
    setError(null);

    // Refresh data with new credentials
    Promise.all([
      fetchMarketData(initialPair),
      fetchTrades(initialPair),
      fetchAccountBalance()
    ]).finally(() => {
      setIsLoading(false);
    });
  }, [initialPair, fetchMarketData, fetchTrades, fetchAccountBalance]);

  // Update mock mode when credentials change
  useEffect(() => {
    const newHasCredentials = Boolean(apiKey && apiSecret);
    const newMockMode = shouldUseMockMode(newHasCredentials) || !isLiveTrading;
    setIsMockMode(newMockMode);

    if (isLiveTrading && newHasCredentials && !newMockMode) {
      if (import.meta.env.DEV) {
        console.info('Live trading enabled with credentials, refreshing API connection');
      }
      poloniexApi.loadCredentials();

      // Manually trigger data refresh without causing dependency loop
      setIsLoading(true);
      setError(null);

      // Use the ref functions to avoid dependency loop
      if (fetchFunctionsRef.current) {
        Promise.all([
          fetchFunctionsRef.current.fetchMarketData(initialPair),
          fetchFunctionsRef.current.fetchTrades(initialPair),
          fetchFunctionsRef.current.fetchAccountBalance()
        ]).finally(() => {
          setIsLoading(false);
        });
      }
    } else {
      if (import.meta.env.DEV) {
        console.info('Using mock mode - live trading disabled or missing credentials');
      }
    }
  }, [isLiveTrading, apiKey, apiSecret, initialPair]);

  // Handle real-time updates via WebSocket
  useEffect(() => {
    // In WebContainer, skip WebSocket connection and use mock data immediately
    if (IS_WEBCONTAINER) {
      if (import.meta.env.DEV) {
        console.info('WebContainer environment detected, using mock data instead of WebSocket');
      }
      setIsMockMode(true);
      setMarketData(mockMarketData);
      setTrades(mockTrades);

      // Skip the rest of the WebSocket setup
      return () => {}; // Empty cleanup function
    }

    // Connect to WebSocket for non-WebContainer environments
    webSocketService.connect()
      .then(() => {
        if (import.meta.env.DEV) {
          console.info('WebSocket setup complete');
        }

        // Check if we're in mock mode from the WebSocket service
        setIsMockMode(webSocketService.isMockMode());

        // Only subscribe if connection was successful
        if (webSocketService.isConnected()) {
          webSocketService.subscribeToMarket(initialPair);
        } else {
          if (import.meta.env.DEV) {
            console.info('Using mock data mode');
          }
          // Initialize with mock data if WebSocket connection failed
          setMarketData(mockMarketData);
          setTrades(mockTrades);
        }
      })
      .catch(err => {
        console.error('Error connecting to WebSocket:', err instanceof Error ? err.message : String(err));
        setIsMockMode(true);
        setMarketData(mockMarketData);
        setTrades(mockTrades);
      });

    // Listen for market data updates
    const handleMarketDataUpdate = (data: MarketData) => {
      if (data.pair === initialPair) {
        setMarketData(prevData => {
          // Find if this timestamp already exists
          const existingIndex = prevData.findIndex(item => item.timestamp === data.timestamp);

          if (existingIndex >= 0) {
            // Update existing data
            const updatedData = [...prevData];
            updatedData[existingIndex] = data;
            return updatedData;
          } else {
            // Add new data and maintain order
            const newData = [...prevData, data];
            return newData.sort((a, b) => a.timestamp - b.timestamp);
          }
        });
      }
    };

    // Listen for trade updates
    const handleTradeUpdate = (trade: Trade) => {
      if (trade.pair === initialPair) {
        setTrades(prevTrades => {
          // Check if trade already exists
          if (!prevTrades.some(t => t.id === trade.id)) {
            return [trade, ...prevTrades].slice(0, 50); // Keep last 50 trades
          }
          return prevTrades;
        });
      }
    };

    webSocketService.on('marketData', handleMarketDataUpdate);
    webSocketService.on('tradeExecuted', handleTradeUpdate);

    // Initial data fetch with small delay to avoid overwhelming the browser
    setTimeout(() => {
      if (fetchFunctionsRef.current) {
        fetchFunctionsRef.current.fetchMarketData(initialPair);
        setTimeout(() => {
          if (fetchFunctionsRef.current) {
            fetchFunctionsRef.current.fetchTrades(initialPair);
            setTimeout(() => {
              if (fetchFunctionsRef.current) {
                fetchFunctionsRef.current.fetchAccountBalance();
              }
            }, 500);
          }
        }, 500);
      }
    }, 500);

    // Cleanup
    return () => {
      webSocketService.off('marketData', handleMarketDataUpdate);
      webSocketService.off('tradeExecuted', handleTradeUpdate);
      if (webSocketService.isConnected()) {
        webSocketService.unsubscribeFromMarket(initialPair);
      }
    };
  }, [initialPair]);

  return {
    marketData,
    trades,
    accountBalance,
    isLoading,
    error,
    isMockMode,
    fetchMarketData,
    fetchTrades,
    fetchAccountBalance,
    refreshApiConnection
  };
};

import * as tf from '@tensorflow/tfjs';

/**
 * Deep Q-Network (DQN) Trading System
 * 
 * This module implements a reinforcement learning based trading system using Deep Q-Networks.
 * It includes environment simulation, model training, experience replay, and trading execution.
 */

// Types for DQN trading
export interface DQNConfig {
  stateDimension: number;
  actionDimension: number;
  learningRate: number;
  gamma: number;
  epsilonStart: number;
  epsilonEnd: number;
  epsilonDecay: number;
  memorySize: number;
  batchSize: number;
  updateTargetFreq: number;
  hiddenLayers: number[];
  activationFunction: string;
  optimizer: string;
}

export interface DQNModelInfo {
  id: string;
  name: string;
  description: string;
  config: DQNConfig;
  performance: DQNPerformance;
  createdAt: number;
  updatedAt: number;
  lastTrainedAt: number;
  status: 'training' | 'ready' | 'error';
  filePath?: string;
  episodesCompleted: number;
  totalTrainingSteps: number;
}

export interface DQNPerformance {
  averageReward: number;
  cumulativeReward: number;
  sharpeRatio: number;
  maxDrawdown: number;
  winRate: number;
  episodeRewards: number[];
  trainingEpisodes: number;
}

export interface DQNAction {
  timestamp: number;
  symbol: string;
  action: 'buy' | 'sell' | 'hold';
  confidence: number;
  position?: number;
  price?: number;
}

// Default DQN configuration
const defaultDQNConfig: DQNConfig = {
  stateDimension: 30,  // Number of features in state
  actionDimension: 3,  // Buy, Sell, Hold
  learningRate: 0.0001,
  gamma: 0.99,         // Discount factor
  epsilonStart: 1.0,   // Initial exploration rate
  epsilonEnd: 0.01,    // Final exploration rate
  epsilonDecay: 0.995, // Exploration decay rate
  memorySize: 10000,   // Experience replay buffer size
  batchSize: 64,       // Training batch size
  updateTargetFreq: 100, // Target network update frequency
  hiddenLayers: [128, 64], // Hidden layer sizes
  activationFunction: 'relu',
  optimizer: 'adam'
};

// DQN Agent class
class DQNAgent {
  private config: DQNConfig;
  private mainModel: tf.LayersModel;
  private targetModel: tf.LayersModel;
  private memory: Array<[number[], number, number, number[], boolean]>;
  private epsilon: number;
  private step: number;

  constructor(config: DQNConfig) {
    this.config = config;
    this.mainModel = this.createModel();
    this.targetModel = this.createModel();
    this.updateTargetModel();
    this.memory = [];
    this.epsilon = config.epsilonStart;
    this.step = 0;
  }

  // Create neural network model
  private createModel(): tf.LayersModel {
    const model = tf.sequential();

    // Input layer
    model.add(tf.layers.dense({
      units: this.config.hiddenLayers[0],
      activation: this.config.activationFunction as any,
      inputShape: [this.config.stateDimension]
    }));

    // Hidden layers
    for (let i = 1; i < this.config.hiddenLayers.length; i++) {
      model.add(tf.layers.dense({
        units: this.config.hiddenLayers[i],
        activation: this.config.activationFunction as any
      }));
    }

    // Output layer
    model.add(tf.layers.dense({
      units: this.config.actionDimension,
      activation: 'linear'
    }));

    // Compile model
    model.compile({
      optimizer: this.config.optimizer === 'adam' ? tf.train.adam(this.config.learningRate) : tf.train.sgd(this.config.learningRate),
      loss: 'meanSquaredError'
    });

    return model;
  }

  // Update target model with weights from main model
  private updateTargetModel(): void {
    const weights = this.mainModel.getWeights();
    this.targetModel.setWeights(weights);
  }

  // Remember experience for replay
  remember(state: number[], action: number, reward: number, nextState: number[], done: boolean): void {
    this.memory.push([state, action, reward, nextState, done]);
    if (this.memory.length > this.config.memorySize) {
      this.memory.shift();
    }
  }

  // Choose action using epsilon-greedy policy
  chooseAction(state: number[]): number {
    if (Math.random() < this.epsilon) {
      // Exploration: random action
      return Math.floor(Math.random() * this.config.actionDimension);
    } else {
      // Exploitation: best action according to model
      return tf.tidy(() => {
        const stateTensor = tf.tensor2d([state]);
        const prediction = this.mainModel.predict(stateTensor) as tf.Tensor;
        const action = tf.argMax(prediction, 1).dataSync()[0];
        return action;
      });
    }
  }

  // Get action confidence
  getActionConfidence(state: number[], action: number): number {
    return tf.tidy(() => {
      const stateTensor = tf.tensor2d([state]);
      const prediction = this.mainModel.predict(stateTensor) as tf.Tensor;
      const qValues = prediction.dataSync();
      const maxQ = Math.max(...qValues);
      const actionQ = qValues[action];

      // Normalize to [0, 1] range
      return actionQ / (maxQ + 1e-8);
    });
  }

  // Train model with experience replay
  async replay(): Promise<void> {
    if (this.memory.length < this.config.batchSize) {
      return;
    }

    // Sample batch from memory
    const batch = this.sampleBatch();

    // Extract batch components
    const states = batch.map(experience => experience[0]);
    const actions = batch.map(experience => experience[1]);
    const rewards = batch.map(experience => experience[2]);
    const nextStates = batch.map(experience => experience[3]);
    const dones = batch.map(experience => experience[4]);

    // Train the model
    await tf.tidy(() => {
      // Convert to tensors
      const statesTensor = tf.tensor2d(states);
      const nextStatesTensor = tf.tensor2d(nextStates);

      // Get current Q values
      const currentQs = this.mainModel.predict(statesTensor) as tf.Tensor;
      const currentQsArray = currentQs.arraySync() as number[][];

      // Get next Q values from target model
      const nextQs = this.targetModel.predict(nextStatesTensor) as tf.Tensor;
      const nextQsArray = nextQs.arraySync() as number[][];

      // Create target Q values by updating only the chosen actions
      const targetQsArray = [...currentQsArray];

      for (let i = 0; i < batch.length; i++) {
        const action = actions[i];
        const reward = rewards[i];
        const done = dones[i];

        let targetQ;
        if (done) {
          targetQ = reward;
        } else {
          const nextQ = nextQsArray[i];
          const maxNextQ = Math.max(...nextQ);
          targetQ = reward + this.config.gamma * maxNextQ;
        }

        targetQsArray[i][action] = targetQ;
      }

      const targetQsTensor = tf.tensor2d(targetQsArray);

      // Train the model
      return this.mainModel.fit(statesTensor, targetQsTensor, {
        epochs: 1,
        verbose: 0
      });
    });

    // Update epsilon (exploration rate)
    if (this.epsilon > this.config.epsilonEnd) {
      this.epsilon *= this.config.epsilonDecay;
    }

    // Update target model periodically
    this.step++;
    if (this.step % this.config.updateTargetFreq === 0) {
      this.updateTargetModel();
    }
  }

  // Sample batch from memory
  private sampleBatch(): Array<[number[], number, number, number[], boolean]> {
    const indices: number[] = [];
    const batchSize = Math.min(this.config.batchSize, this.memory.length);

    while (indices.length < batchSize) {
      const index = Math.floor(Math.random() * this.memory.length);
      if (!indices.includes(index)) {
        indices.push(index);
      }
    }

    return indices.map(index => this.memory[index]);
  }

  // Save model
  async saveModel(path: string): Promise<void> {
    await this.mainModel.save(`file://${path}`);
  }

  // Load model
  async loadModel(path: string): Promise<void> {
    this.mainModel = await tf.loadLayersModel(`file://${path}/model.json`);
    this.targetModel = await tf.loadLayersModel(`file://${path}/model.json`);
  }
}

// Trading environment class
class TradingEnvironment {
  private data: any[];
  private currentStep: number;
  private position: number;
  private initialBalance: number;
  private balance: number;
  private inventory: number[];
  private rewards: number[];
  private done: boolean;
  private stateDimension: number;
  private transactionCost: number;

  constructor(data: any[], initialBalance: number = 10000, transactionCost: number = 0.001) {
    this.data = data;
    this.initialBalance = initialBalance;
    this.transactionCost = transactionCost;
    this.stateDimension = 30; // Default state dimension
    this.reset();
  }

  reset(): number[] {
    this.currentStep = 0;
    this.position = 0; // 0: no position, 1: long position
    this.balance = this.initialBalance;
    this.inventory = [];
    this.rewards = [];
    this.done = false;
    return this.getState();
  }

  getState(): number[] {
    // If we've reached the end of data
    if (this.currentStep >= this.data.length - 1) {
      this.done = true;
      return new Array(this.stateDimension).fill(0);
    }

    // Get current candle and previous candles
    const currentCandle = this.data[this.currentStep];
    const lookback = 10; // Use 10 previous candles for state
    const historyStart = Math.max(0, this.currentStep - lookback);
    const priceHistory = this.data.slice(historyStart, this.currentStep + 1);

    // Extract features
    const state: number[] = [];

    // Position indicator
    state.push(this.position);

    // Price features
    const currentPrice = currentCandle.close;
    const normalizedPrices = priceHistory.map(candle => candle.close / currentPrice - 1);
    state.push(...normalizedPrices);

    // Volume features
    const avgVolume = priceHistory.reduce((sum, candle) => sum + candle.volume, 0) / priceHistory.length;
    const normalizedVolumes = priceHistory.map(candle => candle.volume / avgVolume);
    state.push(...normalizedVolumes);

    // Candlestick features
    const bodySizes = priceHistory.map(candle => Math.abs(candle.close - candle.open) / candle.open);
    state.push(...bodySizes);

    // Pad state to fixed dimension if needed
    while (state.length < this.stateDimension) {
      state.push(0);
    }

    // Truncate if too long
    return state.slice(0, this.stateDimension);
  }

  step(action: number): [number[], number, boolean] {
    // If already done, return terminal state
    if (this.done) {
      return [new Array(this.stateDimension).fill(0), 0, true];
    }

    // Get current price
    const currentPrice = this.data[this.currentStep].close;
    let reward = 0;

    // Execute action
    if (action === 0) { // Buy
      if (this.position === 0) { // Only buy if no position
        const sharesBought = Math.floor(this.balance / currentPrice);
        const cost = sharesBought * currentPrice * (1 + this.transactionCost);

        if (sharesBought > 0) {
          this.balance -= cost;
          this.inventory.push(currentPrice);
          this.position = 1;
          reward = -this.transactionCost * cost; // Small negative reward for transaction cost
        } else {
          reward = -0.1; // Penalty for invalid action
        }
      } else {
        reward = -0.1; // Penalty for invalid action
      }
    } else if (action === 1) { // Sell
      if (this.position === 1) { // Only sell if has position
        const sharesSold = this.inventory.length;
        const revenue = sharesSold * currentPrice * (1 - this.transactionCost);

        if (sharesSold > 0) {
          this.balance += revenue;
          const avgBuyPrice = this.inventory.reduce((sum, price) => sum + price, 0) / sharesSold;
          reward = (currentPrice - avgBuyPrice) / avgBuyPrice; // Reward based on profit percentage
          this.inventory = [];
          this.position = 0;
        } else {
          reward = -0.1; // Penalty for invalid action
        }
      } else {
        reward = -0.1; // Penalty for invalid action
      }
    } else { // Hold
      if (this.position === 1) {
        // Small reward/penalty based on price movement while holding
        const prevPrice = this.data[Math.max(0, this.currentStep - 1)].close;
        reward = (currentPrice - prevPrice) / prevPrice * 0.1;
      } else {
        reward = 0; // Neutral reward for holding cash
      }
    }

    // Move to next step
    this.currentStep++;
    this.rewards.push(reward);

    // Check if episode is done
    if (this.currentStep >= this.data.length - 1) {
      this.done = true;

      // Add final portfolio value to reward
      const finalPortfolioValue = this.balance + (this.inventory.length * this.data[this.currentStep].close);
      const totalReturn = (finalPortfolioValue - this.initialBalance) / this.initialBalance;
      reward += totalReturn;
    }

    return [this.getState(), reward, this.done];
  }

  getPerformance(): {
    totalReturn: number;
    finalValue: number;
    sharpeRatio: number;
    maxDrawdown: number;
    winRate: number;
    cumulativeReward: number;
  } {
    const finalPortfolioValue = this.balance + (this.inventory.length * this.data[this.currentStep].close);
    const totalReturn = (finalPortfolioValue - this.initialBalance) / this.initialBalance;

    // Calculate Sharpe ratio
    const returns = this.rewards.filter(r => r !== 0);
    const meanReturn = returns.reduce((sum, r) => sum + r, 0) / returns.length;
    const stdReturn = Math.sqrt(returns.reduce((sum, r) => sum + Math.pow(r - meanReturn, 2), 0) / returns.length);
    const sharpeRatio = meanReturn / (stdReturn + 1e-8);

    // Calculate max drawdown
    let peak = -Infinity;
    let maxDrawdown = 0;
    let cumulativeReward = 0;

    for (const reward of this.rewards) {
      cumulativeReward += reward;
      if (cumulativeReward > peak) {
        peak = cumulativeReward;
      }
      const drawdown = (peak - cumulativeReward) / (peak + 1e-8);
      maxDrawdown = Math.max(maxDrawdown, drawdown);
    }

    // Calculate win rate
    const trades = this.rewards.filter(r => r !== 0);
    const winningTrades = trades.filter(r => r > 0);
    const winRate = winningTrades.length / (trades.length + 1e-8);

    return {
      totalReturn,
      finalValue: finalPortfolioValue,
      sharpeRatio,
      maxDrawdown,
      winRate,
      cumulativeReward
    };
  }
}

// Train DQN model
export const trainDQNModel = async (
  data: any[],
  config: Partial<DQNConfig> = {},
  modelName: string = 'DQN Trading Model',
  episodes: number = 100
): Promise<DQNModelInfo> => {
  try {
    // Merge with default config
    const fullConfig: DQNConfig = { ...defaultDQNConfig, ...config };

    // Create environment and agent
    const env = new TradingEnvironment(data);
    const agent = new DQNAgent(fullConfig);

    // Training variables
    const episodeRewards: number[] = [];
    let totalSteps = 0;

    // Train for specified number of episodes
    for (let episode = 0; episode < episodes; episode++) {
      let state = env.reset();
      let done = false;
      let episodeReward = 0;

      // Episode loop
      while (!done) {
        // Choose action
        const action = agent.chooseAction(state);

        // Take action
        const [nextState, reward, isDone] = env.step(action);

        // Remember experience
        agent.remember(state, action, reward, nextState, isDone);

        // Train model
        await agent.replay();

        // Update state and counters
        state = nextState;
        done = isDone;
        episodeReward += reward;
        totalSteps++;
      }

      // Record episode results
      episodeRewards.push(episodeReward);

      // Log progress
      if ((episode + 1) % 10 === 0) {
        const avgReward = episodeRewards.slice(-10).reduce((sum, r) => sum + r, 0) / 10;
        console.log(`Episode ${episode + 1}/${episodes}, Avg Reward: ${avgReward.toFixed(2)}`);
      }
    }

    // Get performance metrics
    const performance = env.getPerformance();

    // Save model
    const modelId = `dqn_${Date.now()}`;
    const modelsDir = './models';

    try {
      // Dynamic import for Node.js environments only
      if (typeof process !== 'undefined' && process.versions && process.versions.node) {
        const fs = await import('fs');
        if (!fs.existsSync(modelsDir)) {
          fs.mkdirSync(modelsDir, { recursive: true });
        }
      }
    } catch (error) {
      console.error('Error creating models directory:', error);
    }

    const modelPath = `${modelsDir}/${modelId}`;
    await agent.saveModel(modelPath);

    // Create model info
    const modelInfo: DQNModelInfo = {
      id: modelId,
      name: modelName,
      description: `DQN trading model trained on ${data.length} candles`,
      config: fullConfig,
      performance: {
        averageReward: episodeRewards.reduce((sum, r) => sum + r, 0) / episodes,
        cumulativeReward: performance.cumulativeReward,
        sharpeRatio: performance.sharpeRatio,
        maxDrawdown: performance.maxDrawdown,
        winRate: performance.winRate,
        episodeRewards,
        trainingEpisodes: episodes
      },
      createdAt: Date.now(),
      updatedAt: Date.now(),
      lastTrainedAt: Date.now(),
      status: 'ready',
      filePath: modelPath,
      episodesCompleted: episodes,
      totalTrainingSteps: totalSteps
    };

    // Save model info
    try {
      // Dynamic import for Node.js environments only
      if (typeof process !== 'undefined' && process.versions && process.versions.node) {
        const fs = await import('fs');
        const modelInfoPath = `${modelsDir}/${modelId}_info.json`;
        fs.writeFileSync(modelInfoPath, JSON.stringify(modelInfo, null, 2));
      }
    } catch (error) {
      console.error('Error saving model info:', error);
    }

    return modelInfo;
  } catch (error) {
    console.error('Error training DQN model:', error);
    throw error;
  }
};

// Continue training an existing DQN model
export const continueDQNTraining = async (
  modelInfo: DQNModelInfo,
  data: any[],
  additionalEpisodes: number = 50
): Promise<DQNModelInfo> => {
  try {
    // Create environment and agent
    const env = new TradingEnvironment(data);
    const agent = new DQNAgent(modelInfo.config);

    // Load existing model
    await agent.loadModel(modelInfo.filePath as string);

    // Training variables
    const episodeRewards: number[] = [];
    let totalSteps = modelInfo.totalTrainingSteps;

    // Train for additional episodes
    for (let episode = 0; episode < additionalEpisodes; episode++) {
      let state = env.reset();
      let done = false;
      let episodeReward = 0;

      // Episode loop
      while (!done) {
        // Choose action
        const action = agent.chooseAction(state);

        // Take action
        const [nextState, reward, isDone] = env.step(action);

        // Remember experience
        agent.remember(state, action, reward, nextState, isDone);

        // Train model
        await agent.replay();

        // Update state and counters
        state = nextState;
        done = isDone;
        episodeReward += reward;
        totalSteps++;
      }

      // Record episode results
      episodeRewards.push(episodeReward);

      // Log progress
      if ((episode + 1) % 10 === 0) {
        const avgReward = episodeRewards.slice(-10).reduce((sum, r) => sum + r, 0) / 10;
        console.log(`Continued Training - Episode ${episode + 1}/${additionalEpisodes}, Avg Reward: ${avgReward.toFixed(2)}`);
      }
    }

    // Get performance metrics
    const performance = env.getPerformance();

    // Save model
    const newModelId = `${modelInfo.id}_continued_${Date.now()}`;
    const modelsDir = './models';
    const modelPath = `${modelsDir}/${newModelId}`;
    await agent.saveModel(modelPath);

    // Create model info
    const newModelInfo: DQNModelInfo = {
      ...modelInfo,
      id: newModelId,
      name: `${modelInfo.name} (Continued)`,
      description: `${modelInfo.description} - Continued training with ${data.length} additional candles`,
      performance: {
        averageReward: episodeRewards.reduce((sum, r) => sum + r, 0) / additionalEpisodes,
        cumulativeReward: performance.cumulativeReward,
        sharpeRatio: performance.sharpeRatio,
        maxDrawdown: performance.maxDrawdown,
        winRate: performance.winRate,
        episodeRewards: [...modelInfo.performance.episodeRewards, ...episodeRewards],
        trainingEpisodes: modelInfo.performance.trainingEpisodes + additionalEpisodes
      },
      updatedAt: Date.now(),
      lastTrainedAt: Date.now(),
      status: 'ready',
      filePath: modelPath,
      episodesCompleted: modelInfo.episodesCompleted + additionalEpisodes,
      totalTrainingSteps: totalSteps
    };

    // Save model info
    try {
      // Dynamic import for Node.js environments only
      if (typeof process !== 'undefined' && process.versions && process.versions.node) {
        const fs = await import('fs');
        const modelInfoPath = `${modelsDir}/${newModelId}_info.json`;
        fs.writeFileSync(modelInfoPath, JSON.stringify(newModelInfo, null, 2));
      }
    } catch (error) {
      console.error('Error saving model info:', error);
    }

    return newModelInfo;
  } catch (error) {
    console.error('Error continuing DQN model training:', error);
    throw error;
  }
};

// Get DQN actions for a sequence of market data
export const getDQNActions = async (
  modelInfo: DQNModelInfo,
  data: any[]
): Promise<DQNAction[]> => {
  try {
    // Create agent
    const agent = new DQNAgent(modelInfo.config);

    // Load model
    await agent.loadModel(modelInfo.filePath as string);

    // Create environment
    const env = new TradingEnvironment(data);

    // Get actions
    const actions: DQNAction[] = [];
    let state = env.reset();
    let position = 0;

    for (let i = 0; i < data.length - 1; i++) {
      // Get action
      const actionIndex = agent.chooseAction(state);
      const confidence = agent.getActionConfidence(state, actionIndex);

      // Map action index to action type
      let actionType: 'buy' | 'sell' | 'hold';
      switch (actionIndex) {
        case 0:
          actionType = 'buy';
          position = 1;
          break;
        case 1:
          actionType = 'sell';
          position = 0;
          break;
        default:
          actionType = 'hold';
          break;
      }

      // Add action to list
      actions.push({
        timestamp: data[i].timestamp,
        symbol: data[i].symbol,
        action: actionType,
        confidence,
        position,
        price: data[i].close
      });

      // Take step in environment
      const [nextState, , done] = env.step(actionIndex);

      // Update state
      state = nextState;

      // Break if done
      if (done) break;
    }

    return actions;
  } catch (error) {
    console.error('Error getting DQN actions:', error);
    throw error;
  }
};

