import axios, { AxiosResponse } from 'axios';
import { getAccessToken, getRefreshToken, storeAuthData, clearAuthData, shouldRefreshToken } from '@/utils/auth';

// Get the backend URL from environment
const API_BASE_URL = import.meta.env.VITE_BACKEND_URL || 
                     import.meta.env.VITE_API_URL || 
                     'http://localhost:3000';

interface LoginCredentials {
  username: string;
  password: string;
}

interface LoginResponse {
  success: boolean;
  accessToken: string;
  refreshToken: string;
  expiresIn: number;
  user: {
    id: string;
    username: string;
    email: string;
    role: string;
  };
}

interface RefreshResponse {
  success: boolean;
  accessToken: string;
  expiresIn: number;
}

interface ApiResponse<T = any> {
  success: boolean;
  data?: T;
  error?: string;
  code?: string;
}

/**
 * Auth Service for JWT authentication
 */
export class AuthService {
  private static instance: AuthService;
  private refreshPromise: Promise<string | null> | null = null;

  static getInstance(): AuthService {
    if (!AuthService.instance) {
      AuthService.instance = new AuthService();
    }
    return AuthService.instance;
  }

  /**
   * Login with username and password
   */
  async login(credentials: LoginCredentials): Promise<ApiResponse<LoginResponse>> {
    try {
      const response: AxiosResponse<LoginResponse> = await axios.post(
        `${API_BASE_URL}/api/auth/login`,
        credentials,
        {
          headers: {
            'Content-Type': 'application/json'
          }
        }
      );

      if (response.data.success) {
        // Store authentication data
        storeAuthData(response.data);
        return { success: true, data: response.data };
      }

      return { success: false, error: 'Login failed' };
    } catch (error: any) {
      console.error('Login error:', error);

      if (error.response?.data) {
        return { 
          success: false, 
          error: error.response.data.error || 'Login failed',
          code: error.response.data.code
        };
      }

      return { success: false, error: 'Network error occurred' };
    }
  }

  /**
   * Refresh access token
   */
  async refreshToken(): Promise<string | null> {
    // Prevent multiple simultaneous refresh requests
    if (this.refreshPromise) {
      return this.refreshPromise;
    }

    this.refreshPromise = this._refreshToken();
    const result = await this.refreshPromise;
    this.refreshPromise = null;
    return result;
  }

  private async _refreshToken(): Promise<string | null> {
    try {
      const refreshToken = getRefreshToken();

      if (!refreshToken) {
        console.warn('No refresh token available');
        return null;
      }

      const response: AxiosResponse<RefreshResponse> = await axios.post(
        `${API_BASE_URL}/api/auth/refresh`,
        { refreshToken },
        {
          headers: {
            'Content-Type': 'application/json'
          }
        }
      );

      if (response.data.success) {
        // Update access token
        const currentUser = this.getCurrentUser();
        if (currentUser) {
          storeAuthData({
            accessToken: response.data.accessToken,
            refreshToken: refreshToken,
            expiresIn: response.data.expiresIn,
            user: currentUser
          });
        }

        return response.data.accessToken;
      }

      return null;
    } catch (error: any) {
      console.error('Token refresh error:', error);

      // If refresh fails, clear auth data and redirect to login
      if (error.response?.status === 403 || error.response?.status === 401) {
        this.logout();
      }

      return null;
    }
  }

  /**
   * Logout user
   */
  async logout(): Promise<void> {
    try {
      const refreshToken = getRefreshToken();

      if (refreshToken) {
        // Notify server to invalidate refresh token
        await axios.post(
          `${API_BASE_URL}/api/auth/logout`,
          { refreshToken },
          {
            headers: {
              'Content-Type': 'application/json'
            }
          }
        );
      }
    } catch (error) {
      console.error('Logout error:', error);
    } finally {
      // Always clear local auth data
      clearAuthData();
    }
  }

  /**
   * Verify current token
   */
  async verifyToken(): Promise<boolean> {
    try {
      const token = getAccessToken();

      if (!token) {
        return false;
      }

      const response = await axios.get(
        `${API_BASE_URL}/api/auth/verify`,
        {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        }
      );

      return response.data.success;
    } catch (error) {
      console.error('Token verification error:', error);
      return false;
    }
  }

  /**
   * Get current user data
   */
  getCurrentUser(): any | null {
    const userData = localStorage.getItem('user_data');
    if (!userData) return null;

    try {
      return JSON.parse(userData);
    } catch (error) {
      console.error('Error parsing user data:', error);
      return null;
    }
  }

  /**
   * Make authenticated request with automatic token refresh
   */
  async authenticatedRequest<T = any>(
    method: 'GET' | 'POST' | 'PUT' | 'DELETE',
    endpoint: string,
    data?: any
  ): Promise<ApiResponse<T>> {
    try {
      let token = getAccessToken();

      // Check if we need to refresh the token
      if (shouldRefreshToken()) {
        console.log('Token needs refresh, refreshing...');
        const newToken = await this.refreshToken();
        if (newToken) {
          token = newToken;
        } else {
          return { success: false, error: 'Authentication failed' };
        }
      }

      if (!token) {
        return { success: false, error: 'No authentication token' };
      }

      const config = {
        method,
        url: `${API_BASE_URL}${endpoint}`,
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        ...(data && { data })
      };

      const response = await axios(config);
      return { success: true, data: response.data };
    } catch (error: any) {
      console.error('Authenticated request error:', error);

      // If token is invalid, try to refresh once
      if (error.response?.status === 401 || error.response?.status === 403) {
        const newToken = await this.refreshToken();
        if (newToken) {
          // Retry the request with new token
          try {
            const config = {
              method,
              url: `${API_BASE_URL}${endpoint}`,
              headers: {
                'Authorization': `Bearer ${newToken}`,
                'Content-Type': 'application/json'
              },
              ...(data && { data })
            };

            const response = await axios(config);
            return { success: true, data: response.data };
          } catch (retryError: any) {
            console.error('Retry request error:', retryError);
            return { 
              success: false, 
              error: retryError.response?.data?.error || 'Request failed'
            };
          }
        } else {
          return { success: false, error: 'Authentication failed' };
        }
      }

      return { 
        success: false, 
        error: error.response?.data?.error || 'Request failed'
      };
    }
  }
}

// Export singleton instance
export const authService = AuthService.getInstance();

import { Strategy } from '@/types';
import { executeStrategy } from '@/utils/strategyExecutors';
import { poloniexApi } from '@/services/poloniexAPI';
import { aiSignalGenerator, AISignal } from '@/ml/aiSignalGenerator';
import { logger } from '@/utils/logger';
import { useAppStore } from '@/store';

interface AutomatedTradingConfig {
  maxPositions: number;
  maxLeverage: number;
  riskPerTrade: number;
  stopLossPercent: number;
  takeProfitPercent: number;
  trailingStopPercent?: number;
}

class AutomatedTradingService {
  private static instance: AutomatedTradingService;
  private activeStrategies: Map<string, Strategy> = new Map();
  private positions: Map<string, any> = new Map();
  private config: AutomatedTradingConfig;
  private isRunning: boolean = false;
  private updateInterval: NodeJS.Timeout | null = null;

  private constructor() {
    // Default configuration
    this.config = {
      maxPositions: 3,
      maxLeverage: 5,
      riskPerTrade: 2, // Percentage of account balance
      stopLossPercent: 2,
      takeProfitPercent: 4
    };

    // Subscribe to position updates
    poloniexApi.onPositionUpdate(this.handlePositionUpdate.bind(this));
    poloniexApi.onLiquidationWarning(this.handleLiquidationWarning.bind(this));
    poloniexApi.onMarginUpdate(this.handleMarginUpdate.bind(this));
  }

  public static getInstance(): AutomatedTradingService {
    if (!AutomatedTradingService.instance) {
      AutomatedTradingService.instance = new AutomatedTradingService();
    }
    return AutomatedTradingService.instance;
  }

  /**
   * Start automated trading
   */
  public start(): void {
    if (this.isRunning) return;

    this.isRunning = true;
    this.updateInterval = setInterval(this.update.bind(this), 5000);
    logger.info('Automated trading started');
  }

  /**
   * Stop automated trading
   */
  public stop(): void {
    if (!this.isRunning) return;

    this.isRunning = false;
    if (this.updateInterval) {
      clearInterval(this.updateInterval);
    }
    logger.info('Automated trading stopped');
  }

  /**
   * Add strategy to automated trading
   */
  public addStrategy(strategy: Strategy): void {
    this.activeStrategies.set(strategy.id, strategy);
    logger.info(`Strategy ${strategy.id} added to automated trading`);
  }

  /**
   * Remove strategy from automated trading
   */
  public removeStrategy(strategyId: string): void {
    this.activeStrategies.delete(strategyId);
    logger.info(`Strategy ${strategyId} removed from automated trading`);
  }

  /**
   * Update configuration
   */
  public updateConfig(config: Partial<AutomatedTradingConfig>): void {
    this.config = { ...this.config, ...config };
    logger.info('Automated trading configuration updated', this.config);
  }

  /**
   * Main update loop with AI-enhanced decision making
   */
  private async update(): Promise<void> {
    try {
      // Get account balance
      const balance = await poloniexApi.getAccountBalance();

      // Check if we can open new positions
      if (this.positions.size >= this.config.maxPositions) {
        logger.info('Maximum positions reached, skipping new trades');
        return;
      }

      // Execute each active strategy with AI enhancement
      for (const strategy of this.activeStrategies.values()) {
        try {
          // Get market data
          const marketData = await poloniexApi.getMarketData(strategy.parameters.pair);

          // Execute traditional strategy
          const strategyResult = executeStrategy(strategy, marketData);

          // Generate AI signal for comparison and enhancement
          const aiSignal = await aiSignalGenerator.generateSignal(marketData);

          // Combine strategy and AI signals
          const finalSignal = this.combineSignals(strategyResult, aiSignal);

          if (finalSignal && finalSignal.action !== 'HOLD') {
            await this.executeTrade(strategy, finalSignal, balance.availableAmount);
          }
        } catch (error) {
          logger.error(`Error processing strategy ${strategy.id}:`, error);
        }
      }

      // Also run pure AI trading for configured pairs
      await this.executeAITradingSignals();

    } catch (error) {
      logger.error('Error in automated trading update:', error);
    }
  }

  /**
   * Execute AI-only trading signals for configured pairs
   */
  private async executeAITradingSignals(): Promise<void> {
    try {
      // Get trading settings from store
      const { defaultPair } = useAppStore.getState().trading;

      // Get market data for default pair
      const marketData = await poloniexApi.getMarketData(defaultPair);

      // Generate AI signal
      const aiSignal = await aiSignalGenerator.generateSignal(marketData);

      // Check if signal meets our criteria
      if (aiSignal.action !== 'HOLD' && aiSignal.confidence > 0.7 && aiSignal.riskLevel !== 'HIGH') {
        // Create a virtual strategy for AI signals
        const aiStrategy: Strategy = {
          id: 'ai-signal-' + Date.now(),
          name: 'AI Signal Strategy',
          description: 'Pure AI-generated trading signals',
          type: 'Custom',
          parameters: {
            pair: defaultPair,
            timeframe: '5m'
          },
          isActive: true,
          riskLevel: aiSignal.riskLevel,
          createdAt: Date.now(),
          lastModified: Date.now()
        };

        const balance = await poloniexApi.getAccountBalance();
        await this.executeTrade(aiStrategy, aiSignal, balance.availableAmount);
      }
    } catch (error) {
      logger.error('Error in AI trading signals:', error);
    }
  }

  /**
   * Combine traditional strategy signals with AI signals
   */
  private combineSignals(strategyResult: any, aiSignal: AISignal): AISignal | null {
    // If both signals agree, increase confidence
    if (strategyResult.signal === aiSignal.action) {
      return {
        ...aiSignal,
        confidence: Math.min(aiSignal.confidence * 1.2, 1),
        reason: `Strategy + AI: ${strategyResult.reason} | ${aiSignal.reason}`
      };
    }

    // If signals disagree, only proceed if AI signal has very high confidence
    if (strategyResult.signal !== aiSignal.action && aiSignal.confidence > 0.8) {
      return {
        ...aiSignal,
        confidence: aiSignal.confidence * 0.8,
        reason: `AI override: ${aiSignal.reason} (conflicted with strategy: ${strategyResult.reason})`
      };
    }

    // If traditional strategy says HOLD but AI has strong signal
    if (!strategyResult.signal && aiSignal.action !== 'HOLD' && aiSignal.confidence > 0.75) {
      return {
        ...aiSignal,
        confidence: aiSignal.confidence * 0.9,
        reason: `AI signal: ${aiSignal.reason} (no strategy signal)`
      };
    }

    // Default to hold if signals are weak or conflicting
    return null;
  }

  /**
   * Execute a trade based on strategy signal or AI signal
   */
  private async executeTrade(
    strategy: Strategy,
    signal: 'BUY' | 'SELL' | AISignal,
    availableBalance: number
  ): Promise<void> {
    try {
      const pair = strategy.parameters.pair;

      // Handle both traditional signals and AI signals
      let action: 'BUY' | 'SELL';
      let stopLoss: number | undefined;
      let takeProfit: number | undefined;
      let confidence = 1;
      let reason = 'Strategy signal';

      if (typeof signal === 'string') {
        // Traditional signal
        action = signal;
      } else {
        // AI signal
        action = signal.action as 'BUY' | 'SELL';
        stopLoss = signal.stopLoss;
        takeProfit = signal.takeProfit;
        confidence = signal.confidence;
        reason = signal.reason;
      }

      // Get current market data
      const marketData = await poloniexApi.getMarketData(pair);
      const lastPrice = marketData[marketData.length - 1].close;

      // Calculate position size based on risk and confidence
      const baseRiskAmount = (availableBalance * this.config.riskPerTrade) / 100;
      const adjustedRiskAmount = baseRiskAmount * confidence; // Reduce size for lower confidence
      const quantity = adjustedRiskAmount / lastPrice;

      // Validate minimum position size
      if (quantity < 0.001) { // Minimum position size
        logger.warn(`Position size too small for ${pair}: ${quantity}`);
        return;
      }

      // Place main order
      const order = await poloniexApi.placeOrder(
        pair,
        action.toLowerCase() as 'buy' | 'sell',
        'market',
        quantity
      );

      // Use AI-provided stop loss or calculate default
      let stopPrice: number;
      if (stopLoss) {
        stopPrice = stopLoss;
      } else {
        stopPrice = action === 'BUY'
          ? lastPrice * (1 - this.config.stopLossPercent / 100)
          : lastPrice * (1 + this.config.stopLossPercent / 100);
      }

      // Place stop loss order
      try {
        await poloniexApi.placeConditionalOrder(
          pair,
          action === 'BUY' ? 'sell' : 'buy',
          'stop',
          quantity,
          stopPrice
        );
      } catch (error) {
        logger.warn('Failed to place stop loss order:', error);
      }

      // Use AI-provided take profit or calculate default
      let takeProfitPrice: number;
      if (takeProfit) {
        takeProfitPrice = takeProfit;
      } else {
        takeProfitPrice = action === 'BUY'
          ? lastPrice * (1 + this.config.takeProfitPercent / 100)
          : lastPrice * (1 - this.config.takeProfitPercent / 100);
      }

      // Place take profit order
      try {
        await poloniexApi.placeConditionalOrder(
          pair,
          action === 'BUY' ? 'sell' : 'buy',
          'takeProfit',
          quantity,
          takeProfitPrice
        );
      } catch (error) {
        logger.warn('Failed to place take profit order:', error);
      }

      // Store position information
      const positionId = `${strategy.id}-${Date.now()}`;
      this.positions.set(positionId, {
        strategy: strategy.id,
        pair,
        action,
        quantity,
        entryPrice: lastPrice,
        stopPrice,
        takeProfitPrice,
        confidence,
        reason,
        timestamp: Date.now(),
        orderId: order?.id
      });

      // Add toast notification
      const store = useAppStore.getState();
      store.addToast({
        message: `Trade executed: ${action} ${quantity.toFixed(4)} ${pair} at ${lastPrice.toFixed(2)}`,
        type: 'success',
        dismissible: true
      });

      logger.info(`Trade executed for strategy ${strategy.id}`, {
        action,
        pair,
        quantity,
        entryPrice: lastPrice,
        stopPrice,
        takeProfitPrice,
        confidence,
        reason
      });
    } catch (error) {
      logger.error('Error executing trade:', error);

      // Add error toast notification
      const store = useAppStore.getState();
      store.addToast({
        message: `Trade execution failed: ${error instanceof Error ? error.message : 'Unknown error'}`,
        type: 'error',
        dismissible: true
      });
    }
  }

  /**
   * Handle position update from exchange
   */
  private handlePositionUpdate(position: any): void {
    this.positions.set(position.symbol, position);
    logger.info('Position updated:', position);
  }

  /**
   * Handle liquidation warning
   */
  private handleLiquidationWarning(warning: any): void {
    logger.warn('Liquidation warning received:', warning);
    // Implement emergency position closure or risk reduction
  }

  /**
   * Handle margin update
   */
  private handleMarginUpdate(margin: any): void {
    logger.info('Margin updated:', margin);
    // Implement margin management logic
  }
}

