  /**
   * Setup notification handlers
   */
  private setupNotificationHandlers(): void {
    // Toast notifications
    this.notificationHandlers.set('toast', async (alert: Alert) => {
      // This would integrate with your toast system
      console.log(`Toast: ${alert.title} - ${alert.message}`);
    });

    // Browser notifications
    this.notificationHandlers.set('browser', async (alert: Alert) => {
      if ('Notification' in window && Notification.permission === 'granted') {
        new Notification(alert.title, {
          body: alert.message,
          icon: '/favicon.ico',
          tag: alert.id
        });
      }
    });

    // Sound notifications
    this.notificationHandlers.set('sound', async (alert: Alert) => {
      if (alert.priority === 'critical' || alert.priority === 'high') {
        // Play alert sound
        try {
          const audio = new Audio('/alert-sound.mp3');
          audio.play().catch(e => console.warn('Could not play alert sound:', e));
        } catch (error) {
          console.warn('Alert sound not available');
        }
      }
    });

    // Webhook notifications (placeholder)
    this.notificationHandlers.set('webhook', async (alert: Alert) => {
      // This would send to configured webhook URL
      console.log(`Webhook: ${JSON.stringify(alert)}`);
    });
  }

  /**
   * Trigger an alert
   */
  private triggerAlert(type: AlertType, alertData: { title: string; message: string; data?: any }): void {
    // Check for existing similar alerts to avoid spam
    const recentSimilar = this.alerts.filter(
      a => a.type === type && 
      a.title === alertData.title && 
      a.timestamp > Date.now() - (5 * 60 * 1000) // 5 minutes
    );

    if (recentSimilar.length > 0) return;

    const alert: Alert = {
      id: `alert_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
      ruleId: type,
      timestamp: Date.now(),
      title: alertData.title,
      message: alertData.message,
      priority: this.getPriorityForType(type),
      type,
      data: alertData.data,
      acknowledged: false
    };

    this.alerts.push(alert);
    this.updateAlertsCount();

    // Send notifications
    this.sendNotifications(alert);

    logger.warn(`Alert triggered: ${alert.title}`, alert);
  }

  /**
   * Check metric-based alerts
   */
  private checkMetricAlerts(metrics: PerformanceMetrics): void {
    for (const rule of this.alertRules.values()) {
      if (!rule.enabled) continue;

      // Check cooldown
      if (rule.lastTriggered && 
          Date.now() - rule.lastTriggered < rule.cooldownMinutes * 60 * 1000) {
        continue;
      }

      const metricValue = this.getMetricValue(metrics, rule.condition.metric);
      if (metricValue === undefined) continue;

      const conditionMet = this.evaluateCondition(metricValue, rule.condition);

      if (conditionMet) {
        this.triggerAlert(rule.type, {
          title: rule.name,
          message: `${rule.condition.metric} is ${metricValue} (threshold: ${rule.condition.value})`,
          data: { metric: rule.condition.metric, value: metricValue, threshold: rule.condition.value }
        });

        rule.lastTriggered = Date.now();
      }
    }
  }

  /**
   * Send notifications for an alert
   */
  private async sendNotifications(alert: Alert): Promise<void> {
    const rule = this.alertRules.get(alert.ruleId);
    const methods = rule?.notificationMethods || ['toast'];

    for (const method of methods) {
      const handler = this.notificationHandlers.get(method);
      if (handler) {
        try {
          await handler(alert);
        } catch (error) {
          logger.error(`Failed to send ${method} notification:`, error);
        }
      }
    }
  }

  /**
   * Get priority for alert type
   */
  private getPriorityForType(type: AlertType): AlertRule['priority'] {
    const priorityMap: Record<AlertType, AlertRule['priority']> = {
      'price_movement': 'medium',
      'portfolio_risk': 'high',
      'position_pnl': 'medium',
      'system_error': 'high',
      'trading_signal': 'low',
      'market_volatility': 'medium',
      'api_issues': 'medium',
      'performance_degradation': 'medium'
    };

    return priorityMap[type] || 'medium';
  }

  /**
   * Get metric value from performance metrics
   */
  private getMetricValue(metrics: PerformanceMetrics, metricName: string): number | undefined {
    return (metrics as any)[metricName];
  }

  /**
   * Evaluate alert condition
   */
  private evaluateCondition(value: number, condition: AlertCondition): boolean {
    switch (condition.operator) {
      case 'gt': return value > condition.value;
      case 'lt': return value < condition.value;
      case 'eq': return value === condition.value;
      case 'gte': return value >= condition.value;
      case 'lte': return value <= condition.value;
      case 'change_gt':
      case 'change_lt':
        // Would need historical data for change calculations
        return false;
      default: return false;
    }
  }

  /**
   * Update component status
   */
  private updateComponentStatus(component: keyof SystemStatus['components'], status: string): void {
    (this.systemStatus.components as any)[component] = status;
    this.updateOverallStatus();
  }

  /**
   * Update overall system status
   */
  private updateOverallStatus(): void {
    const components = Object.values(this.systemStatus.components);

    if (components.some(status => status === 'error' || status === 'down' || status === 'disconnected')) {
      this.systemStatus.overall = 'critical';
    } else if (components.some(status => status === 'warning' || status === 'degraded' || status === 'delayed')) {
      this.systemStatus.overall = 'warning';
    } else {
      this.systemStatus.overall = 'healthy';
    }

    this.systemStatus.lastUpdate = Date.now();
  }

  /**
   * Update alerts count
   */
  private updateAlertsCount(): void {
    const activeAlerts = this.getActiveAlerts();
    this.systemStatus.alertsCount = {
      critical: activeAlerts.filter(a => a.priority === 'critical').length,
      high: activeAlerts.filter(a => a.priority === 'high').length,
      medium: activeAlerts.filter(a => a.priority === 'medium').length,
      low: activeAlerts.filter(a => a.priority === 'low').length
    };
  }

  /**
   * Acknowledge alerts by type
   */
  private acknowledgeAlertsByType(type: AlertType): void {
    this.alerts
      .filter(a => a.type === type && !a.acknowledged)
      .forEach(a => {
        a.acknowledged = true;
        a.acknowledgedAt = Date.now();
      });

    this.updateAlertsCount();
  }

  /**
   * Update system status periodically
   */
  private updateSystemStatus(): void {
    this.systemStatus.uptime = Date.now() - (this.systemStatus.lastUpdate || Date.now());
    this.systemStatus.lastUpdate = Date.now();
  }

  /**
   * Check all alert rules
   */
  private checkAlertRules(): void {
    if (this.performanceHistory.length > 0) {
      const latestMetrics = this.performanceHistory[this.performanceHistory.length - 1];
      this.checkMetricAlerts(latestMetrics);
    }
  }

  /**
   * Clean up old data
   */
  private cleanupOldData(): void {
    const cutoff = Date.now() - (7 * 24 * 60 * 60 * 1000); // 7 days

    // Clean old alerts
    this.alerts = this.alerts.filter(alert => alert.timestamp >= cutoff);

    // Clean old performance data
    this.performanceHistory = this.performanceHistory.filter(
      metrics => metrics.timestamp >= cutoff
    );
  }

  /**
   * Get recent prices for a pair (placeholder - would need actual price storage)
   */
  private getRecentPrices(pair: string): number[] {
    // This would retrieve recent price history from storage
    // For now, return empty array
    return [];
  }

  /**
   * Calculate volatility from price history
   */
  private calculateVolatility(prices: number[]): number {
    if (prices.length < 2) return 0;

    const returns = [];
    for (let i = 1; i < prices.length; i++) {
      returns.push((prices[i] - prices[i - 1]) / prices[i - 1]);
    }

    const mean = returns.reduce((sum, ret) => sum + ret, 0) / returns.length;
    const variance = returns.reduce((sum, ret) => sum + Math.pow(ret - mean, 2), 0) / returns.length;

    return Math.sqrt(variance);
  }
}

/**
 * Global monitoring instance
 */
export const monitoringSystem = new MonitoringSystem();

/**
 * Performance Optimization Utilities for High-Frequency Trading
 * Implements caching, batching, and optimization strategies
 */

export interface PerformanceConfig {
  // Caching settings
  enableCaching: boolean;
  cacheMaxSize: number;
  cacheExpiryMs: number;

  // Batching settings
  enableBatching: boolean;
  batchSize: number;
  batchDelayMs: number;

  // Rate limiting settings
  maxRequestsPerSecond: number;
  maxConcurrentRequests: number;

  // Memory management
  maxMemoryUsageMB: number;
  gcThresholdMB: number;

  // WebSocket optimization
  wsHeartbeatInterval: number;
  wsReconnectDelay: number;
  wsMaxReconnectAttempts: number;
}

export interface CacheEntry<T> {
  data: T;
  timestamp: number;
  expiresAt: number;
  accessCount: number;
  lastAccessed: number;
}

export interface BatchRequest {
  id: string;
  type: string;
  params: any;
  resolve: (result: any) => void;
  reject: (error: any) => void;
  timestamp: number;
}

export interface PerformanceMetrics {
  cacheHitRate: number;
  avgResponseTime: number;
  requestsPerSecond: number;
  memoryUsage: number;
  batchEfficiency: number;
  errorRate: number;
}

export class PerformanceOptimizer {
  private config: PerformanceConfig;
  private cache: Map<string, CacheEntry<any>> = new Map();
  private requestQueue: BatchRequest[] = [];
  private batchProcessor: NodeJS.Timeout | null = null;
  private rateLimiter: Map<string, number[]> = new Map();
  private activeRequests = 0;
  private metrics: PerformanceMetrics = {
    cacheHitRate: 0,
    avgResponseTime: 0,
    requestsPerSecond: 0,
    memoryUsage: 0,
    batchEfficiency: 0,
    errorRate: 0
  };
  private metricsHistory: PerformanceMetrics[] = [];
  private startTime = Date.now();

  constructor(config?: Partial<PerformanceConfig>) {
    this.config = {
      enableCaching: true,
      cacheMaxSize: 1000,
      cacheExpiryMs: 300000, // 5 minutes

      enableBatching: true,
      batchSize: 10,
      batchDelayMs: 100,

      maxRequestsPerSecond: 10,
      maxConcurrentRequests: 5,

      maxMemoryUsageMB: 512,
      gcThresholdMB: 256,

      wsHeartbeatInterval: 30000,
      wsReconnectDelay: 5000,
      wsMaxReconnectAttempts: 5,

      ...config
    };

    this.startCacheCleanup();
    this.startMetricsCollection();
  }

  /**
   * Cached API request wrapper
   */
  async cachedRequest<T>(
    key: string,
    requestFn: () => Promise<T>,
    forceRefresh: boolean = false
  ): Promise<T> {
    const startTime = performance.now();

    try {
      // Check cache first
      if (!forceRefresh && this.config.enableCaching) {
        const cached = this.getCached<T>(key);
        if (cached) {
          this.updateMetrics('cacheHit', performance.now() - startTime);
          return cached;
        }
      }

      // Apply rate limiting
      if (!this.checkRateLimit('api')) {
        throw new Error('Rate limit exceeded');
      }

      // Check concurrent request limit
      if (this.activeRequests >= this.config.maxConcurrentRequests) {
        await this.waitForSlot();
      }

      this.activeRequests++;

      try {
        const result = await requestFn();

        // Cache the result
        if (this.config.enableCaching) {
          this.setCached(key, result);
        }

        this.updateMetrics('cacheMiss', performance.now() - startTime, true);
        return result;
      } finally {
        this.activeRequests--;
      }
    } catch (error) {
      this.updateMetrics('error', performance.now() - startTime, false);
      throw error;
    }
  }

  /**
   * Batched request processing
   */
  async batchRequest<T>(
    type: string,
    params: any
  ): Promise<T> {
    if (!this.config.enableBatching) {
      throw new Error('Batching is disabled');
    }

    return new Promise<T>((resolve, reject) => {
      const request: BatchRequest = {
        id: `${type}_${Date.now()}_${Math.random()}`,
        type,
        params,
        resolve,
        reject,
        timestamp: Date.now()
      };

      this.requestQueue.push(request);

      // Start batch processor if not running
      if (!this.batchProcessor) {
        this.batchProcessor = setTimeout(() => {
          this.processBatch();
        }, this.config.batchDelayMs);
      }

      // Process immediately if batch is full
      if (this.requestQueue.length >= this.config.batchSize) {
        if (this.batchProcessor) {
          clearTimeout(this.batchProcessor);
          this.batchProcessor = null;
        }
        this.processBatch();
      }
    });
  }

  /**
   * Optimized WebSocket connection manager
   */
  createOptimizedWebSocket(url: string, protocols?: string[]): OptimizedWebSocket {
    return new OptimizedWebSocket(url, protocols, this.config);
  }

  /**
   * Memory usage monitoring and cleanup
   */
  monitorMemory(): void {
    if (typeof window !== 'undefined' && 'performance' in window && 'memory' in (window.performance as any)) {
      const memory = (window.performance as any).memory;
      const usedMB = memory.usedJSHeapSize / 1024 / 1024;

      this.metrics.memoryUsage = usedMB;

      if (usedMB > this.config.gcThresholdMB) {
        this.performGarbageCollection();
      }
    }
  }

  /**
   * Get performance metrics
   */
  getMetrics(): PerformanceMetrics {
    return { ...this.metrics };
  }

  /**
   * Get performance history
   */
  getMetricsHistory(): PerformanceMetrics[] {
    return [...this.metricsHistory];
  }

  /**
   * Clear all caches
   */
  clearCache(): void {
    this.cache.clear();
  }

  /**
   * Update configuration
   */
  updateConfig(newConfig: Partial<PerformanceConfig>): void {
    this.config = { ...this.config, ...newConfig };
  }

  /**
   * Debounced function executor
   */
  debounce<T extends (...args: any[]) => any>(
    func: T,
    wait: number
  ): (...args: Parameters<T>) => void {
    let timeout: NodeJS.Timeout | null = null;

    return (...args: Parameters<T>) => {
      if (timeout) {
        clearTimeout(timeout);
      }

      timeout = setTimeout(() => {
        func(...args);
      }, wait);
    };
  }

  /**
   * Throttled function executor
   */
  throttle<T extends (...args: any[]) => any>(
    func: T,
    limit: number
  ): (...args: Parameters<T>) => void {
    let inThrottle = false;

    return (...args: Parameters<T>) => {
      if (!inThrottle) {
        func(...args);
        inThrottle = true;
        setTimeout(() => inThrottle = false, limit);
      }
    };
  }

  /**
   * Memoization for expensive calculations
   */
  memoize<T extends (...args: any[]) => any>(
    func: T,
    maxCacheSize: number = 100
  ): T {
    const cache = new Map<string, { result: ReturnType<T>; timestamp: number }>();

    return ((...args: Parameters<T>): ReturnType<T> => {
      const key = JSON.stringify(args);
      const cached = cache.get(key);

      if (cached && Date.now() - cached.timestamp < this.config.cacheExpiryMs) {
        return cached.result;
      }

      const result = func(...args);

      // Manage cache size
      if (cache.size >= maxCacheSize) {
        const oldestKey = cache.keys().next().value;
        cache.delete(oldestKey);
      }

      cache.set(key, { result, timestamp: Date.now() });
      return result;
    }) as T;
  }

  /**
   * Batch array processing with yielding
   */
  async *batchProcess<T, R>(
    items: T[],
    processor: (item: T) => Promise<R>,
    batchSize: number = this.config.batchSize
  ): AsyncGenerator<R[], void, unknown> {
    for (let i = 0; i < items.length; i += batchSize) {
      const batch = items.slice(i, i + batchSize);
      const results = await Promise.all(batch.map(processor));
      yield results;

      // Yield control to prevent blocking
      await new Promise(resolve => setTimeout(resolve, 0));
    }
  }

  /**
   * Get cached value
   */
  private getCached<T>(key: string): T | null {
    const entry = this.cache.get(key);
    if (!entry) return null;

    if (Date.now() > entry.expiresAt) {
      this.cache.delete(key);
      return null;
    }

    entry.accessCount++;
    entry.lastAccessed = Date.now();
    return entry.data;
  }

  /**
   * Set cached value
   */
  private setCached<T>(key: string, data: T): void {
    // Remove oldest entries if cache is full
    if (this.cache.size >= this.config.cacheMaxSize) {
      this.evictOldestEntry();
    }

    const entry: CacheEntry<T> = {
      data,
      timestamp: Date.now(),
      expiresAt: Date.now() + this.config.cacheExpiryMs,
      accessCount: 0,
      lastAccessed: Date.now()
    };

    this.cache.set(key, entry);
  }

  /**
   * Check rate limit
   */
  private checkRateLimit(type: string): boolean {
    const now = Date.now();
    const windowStart = now - 1000; // 1 second window

    if (!this.rateLimiter.has(type)) {
      this.rateLimiter.set(type, []);
    }

    const timestamps = this.rateLimiter.get(type)!;

    // Remove old timestamps
    while (timestamps.length > 0 && timestamps[0] < windowStart) {
      timestamps.shift();
    }

    if (timestamps.length >= this.config.maxRequestsPerSecond) {
      return false;
    }

    timestamps.push(now);
    return true;
  }

  /**
   * Wait for available request slot
   */
  private async waitForSlot(): Promise<void> {
    return new Promise(resolve => {
      const checkSlot = () => {
        if (this.activeRequests < this.config.maxConcurrentRequests) {
          resolve();
        } else {
          setTimeout(checkSlot, 50);
        }
      };
      checkSlot();
    });
  }

  /**
   * Process batched requests
   */
  private async processBatch(): Promise<void> {
    if (this.requestQueue.length === 0) return;

    const batch = this.requestQueue.splice(0, this.config.batchSize);
    this.batchProcessor = null;

    // Group requests by type
    const grouped = batch.reduce((acc, req) => {
      if (!acc[req.type]) {
        acc[req.type] = [];
      }
      acc[req.type].push(req);
      return acc;
    }, {} as Record<string, BatchRequest[]>);

    // Process each group
    for (const [type, requests] of Object.entries(grouped)) {
      try {
        const results = await this.executeBatch(type, requests);
        requests.forEach((req, index) => {
          req.resolve(results[index]);
        });
      } catch (error) {
        requests.forEach(req => {
          req.reject(error);
        });
      }
    }

    // Update batch efficiency metric
    const efficiency = batch.length / this.config.batchSize;
    this.metrics.batchEfficiency = (this.metrics.batchEfficiency + efficiency) / 2;

    // Process remaining requests if any
    if (this.requestQueue.length > 0) {
      this.batchProcessor = setTimeout(() => {
        this.processBatch();
      }, this.config.batchDelayMs);
    }
  }

  /**
   * Execute batch of requests
   */
  private async executeBatch(type: string, requests: BatchRequest[]): Promise<any[]> {
    // This would be implemented based on specific API requirements
    // For now, execute requests individually
    const results = [];
    for (const request of requests) {
      // Placeholder - implement actual batch API calls
      results.push(await this.executeRequest(request));
    }
    return results;
  }

  /**
   * Execute individual request (placeholder)
   */
  private async executeRequest(request: BatchRequest): Promise<any> {
    // Placeholder implementation
    return { type: request.type, params: request.params, timestamp: Date.now() };
  }

  /**
   * Evict oldest cache entry
   */
  private evictOldestEntry(): void {
    let oldestKey = '';
    let oldestTime = Infinity;

    for (const [key, entry] of this.cache.entries()) {
      if (entry.lastAccessed < oldestTime) {
        oldestTime = entry.lastAccessed;
        oldestKey = key;
      }
    }

    if (oldestKey) {
      this.cache.delete(oldestKey);
    }
  }

  /**
   * Update performance metrics
   */
  private updateMetrics(type: 'cacheHit' | 'cacheMiss' | 'error', duration: number, success: boolean = true): void {
    const now = Date.now();
    const requestsInLastSecond = this.getRequestsInLastSecond();

    // Update cache hit rate
    if (type === 'cacheHit' || type === 'cacheMiss') {
      const totalRequests = this.metricsHistory.length + 1;
      const hitRate = type === 'cacheHit' ? 1 : 0;
      this.metrics.cacheHitRate = (this.metrics.cacheHitRate * (totalRequests - 1) + hitRate) / totalRequests;
    }

    // Update response time
    this.metrics.avgResponseTime = (this.metrics.avgResponseTime + duration) / 2;

    // Update RPS
    this.metrics.requestsPerSecond = requestsInLastSecond;

    // Update error rate
    const errorRate = success ? 0 : 1;
    this.metrics.errorRate = (this.metrics.errorRate + errorRate) / 2;
  }

  /**
   * Get requests in last second
   */
  private getRequestsInLastSecond(): number {
    const cutoff = Date.now() - 1000;
    return this.metricsHistory.filter(m => m.requestsPerSecond > cutoff).length;
  }

  /**
   * Start cache cleanup routine
   */
  private startCacheCleanup(): void {
    setInterval(() => {
      const now = Date.now();
      for (const [key, entry] of this.cache.entries()) {
        if (now > entry.expiresAt) {
          this.cache.delete(key);
        }
      }
    }, 60000); // Clean every minute
  }

  /**
   * Start metrics collection
   */
  private startMetricsCollection(): void {
    setInterval(() => {
      this.monitorMemory();
      this.metricsHistory.push({ ...this.metrics });

      // Keep only last 100 metrics
      if (this.metricsHistory.length > 100) {
        this.metricsHistory.shift();
      }
    }, 10000); // Collect every 10 seconds
  }

  /**
   * Perform garbage collection hints
   */
  private performGarbageCollection(): void {
    // Clear old cache entries
    const cutoff = Date.now() - this.config.cacheExpiryMs / 2;
    for (const [key, entry] of this.cache.entries()) {
      if (entry.lastAccessed < cutoff) {
        this.cache.delete(key);
      }
    }

    // Clear old metrics
    this.metricsHistory = this.metricsHistory.slice(-50);

    // Clear old rate limit data
    const windowStart = Date.now() - 60000; // 1 minute
    for (const [type, timestamps] of this.rateLimiter.entries()) {
      this.rateLimiter.set(type, timestamps.filter(ts => ts > windowStart));
    }
  }
}

/**
 * Optimized WebSocket implementation
 */
export class OptimizedWebSocket {
  private ws: WebSocket | null = null;
  private url: string;
  private protocols?: string[];
  private config: PerformanceConfig;
  private reconnectAttempts = 0;
  private heartbeatInterval: NodeJS.Timeout | null = null;
  private messageQueue: any[] = [];
  private connectionState: 'connecting' | 'connected' | 'disconnected' | 'reconnecting' = 'disconnected';

  constructor(url: string, protocols?: string[], config?: PerformanceConfig) {
    this.url = url;
    this.protocols = protocols;
    this.config = config || {
      wsHeartbeatInterval: 30000,
      wsReconnectDelay: 5000,
      wsMaxReconnectAttempts: 5
    } as PerformanceConfig;

    this.connect();
  }

  /**
   * Send message with queuing
   */
  send(data: any): void {
    if (this.ws && this.ws.readyState === WebSocket.OPEN) {
      this.ws.send(typeof data === 'string' ? data : JSON.stringify(data));
    } else {
      this.messageQueue.push(data);
    }
  }

  /**
   * Close connection
   */
  close(): void {
    if (this.heartbeatInterval) {
      clearInterval(this.heartbeatInterval);
    }

    if (this.ws) {
      this.ws.close();
    }

    this.connectionState = 'disconnected';
  }

  /**
   * Get connection state
   */
  getState(): string {
    return this.connectionState;
  }

  /**
   * Connect to WebSocket
   */
  private connect(): void {
    this.connectionState = this.reconnectAttempts > 0 ? 'reconnecting' : 'connecting';

    try {
      this.ws = new WebSocket(this.url, this.protocols);

      this.ws.onopen = () => {
        this.connectionState = 'connected';
        this.reconnectAttempts = 0;

        // Send queued messages
        while (this.messageQueue.length > 0) {
          const message = this.messageQueue.shift();
          this.send(message);
        }

        // Start heartbeat
        this.startHeartbeat();
      };

      this.ws.onclose = () => {
        this.connectionState = 'disconnected';

        if (this.heartbeatInterval) {
          clearInterval(this.heartbeatInterval);
        }

        this.attemptReconnect();
      };

      this.ws.onerror = (error) => {
        console.error('WebSocket error:', error);
        this.attemptReconnect();
      };

    } catch (error) {
      console.error('Failed to create WebSocket:', error);
      this.attemptReconnect();
    }
  }

  /**
   * Attempt reconnection
   */
  private attemptReconnect(): void {
    if (this.reconnectAttempts >= this.config.wsMaxReconnectAttempts) {
      console.error('Max reconnection attempts reached');
      return;
    }

    this.reconnectAttempts++;

    setTimeout(() => {
      this.connect();
    }, this.config.wsReconnectDelay * Math.pow(2, this.reconnectAttempts - 1)); // Exponential backoff
  }

  /**
   * Start heartbeat mechanism
   */
  private startHeartbeat(): void {
    this.heartbeatInterval = setInterval(() => {
      if (this.ws && this.ws.readyState === WebSocket.OPEN) {
        this.ws.send(JSON.stringify({ type: 'ping', timestamp: Date.now() }));
      }
    }, this.config.wsHeartbeatInterval);
  }
}

/**
 * Global performance optimizer instance
 */
export const performanceOptimizer = new PerformanceOptimizer();

/**
 * Utility functions for common optimizations
 */
export const optimizationUtils = {
  /**
   * Lazy load component
   */
  lazyLoad: <T>(factory: () => Promise<{ default: T }>): Promise<T> => {
    return factory().then(module => module.default);
  },

  /**
   * Virtual scrolling helper
   */
  createVirtualScroller: (
    itemHeight: number,
    containerHeight: number,
    totalItems: number
  ) => {
    return {
      getVisibleRange: (scrollTop: number) => {
        const start = Math.floor(scrollTop / itemHeight);
        const end = Math.min(
          start + Math.ceil(containerHeight / itemHeight) + 1,
          totalItems
        );
        return { start: Math.max(0, start), end };
      },

      getTotalHeight: () => totalItems * itemHeight,

      getItemStyle: (index: number) => ({
        position: 'absolute' as const,
        top: index * itemHeight,
        height: itemHeight,
        width: '100%'
      })
    };
  },

  /**
   * Image lazy loading
   */
  setupLazyImages: (selector: string = 'img[data-src]') => {
    if ('IntersectionObserver' in window) {
      const imageObserver = new IntersectionObserver((entries) => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            const img = entry.target as HTMLImageElement;
            img.src = img.dataset.src!;
            img.removeAttribute('data-src');
            imageObserver.unobserve(img);
          }
        });
      });

      document.querySelectorAll(selector).forEach(img => {
        imageObserver.observe(img);
      });
    }
  }
};

